css:
@-moz-document url-prefix("http://www.jeuxvideo.com/forums/") {
#content {
    background-color: #4d4d4d;
    background-image: none;
}

.container-content {
    background-color: #5f5f5f;
}

.titre-bloc-forum span {
    color: #ffffff !important;
}

#table-liste-topic-forum {
    background-color: #5f5f5f;
    border: 1px solid #444444;
}
  
#table-liste-topic-forum tr, #table-liste-topic-forum tr a, #table-liste-topic-forum tr a:visited {
    color: #dddddd !important;
}
  
#table-liste-topic-forum .sujet-topic a:visited {
    color: #999999 !important;
}

#table-liste-topic-forum tbody tr, .bloc-message-forum:nth-child(even) {
    background-color: #404040;
}

#table-liste-topic-forum tbody tr:nth-child(2n+1), .bloc-message-forum:nth-child(odd) {
    background-color: #494949;
}
    
#table-liste-topic-forum tbody tr:hover {
    background-color: #555555;
}

.bloc-message-forum {
    color: #eeeeee;
    border: 1px solid #444444;
}

.bloc-message-forum .bloc-header {
    border-bottom: 1px dotted #666666;
}

.bloc-message-forum .bloc-pseudo-msg {
    color: #eeeeee;
}

a, .lien-jv {
    color: #777777;
}

.btn-actu-new-list-forum, .btn-topic-resolu, .btn-lock-topic, .btn-repondre-msg {
    background-color: #494949;
    border: 1px solid #444444;
}

.btn-actu-new-list-forum:hover, .btn-topic-resolu:hover, .btn-lock-topic:hover {
  	background-color: #555555;
}

.panel-jv-forum {
    background-color: #494949;
}

#table-liste-topic-forum thead, .panel-jv-forum .panel-heading {
    background-color: #272727;
}

.panel-jv-forum .titre-info-fofo {
    color: #ff6000;
}

.jv-editor .conteneur-editor {
    background-color: #494949;
    border: 1px solid #444444;
}

.jv-editor .btn-group {
    background-color: #404040;
    border: 1px solid #444444;
}

.jv-editor .btn-jv-editor-toolbar {
    color: #eeeeee;
}

.jv-editor .btn-jv-editor-toolbar:hover {
	color: #ff4000;
}

.jv-editor .label-previsu {
    color: #bbbbbb;
}

.jv-editor .btn-jv-editor-previsu {
    background-color: #666666;
    border: 1px solid #444444;
}

.jv-editor .btn-jv-editor-previsu.active {
    background-color: #555555;
    border: 1px solid #444444;
}

.jv-editor .area-editor {
    background-color: #666666;
    color: #eeeeee;
}

.jv-editor .text-editor {
    background-color: #5f5f5f;
  	border-top: 1px solid #444444;
}

.jv-editor .previsu-editor {
  	color: #eeeeee;
    border: 1px solid #444444;
}

input[type=text], select, .btn-lancer-rech, .btn-ajouter-sondage {
  	background-color: #666666 !important;
  	color: #eeeeee !important;
  	border: 1px solid #444444 !important;
}

.btn-poster-msg {
  	width: 180px;
}

.btn-poster-msg:hover {
  	color: #ffffff;
  	background-color: #fd4400;
}
    
.btn-poster-msg:hover:before {
  	content: ">> ";
}
        
.btn-poster-msg:hover:after {
  	content: " <<";
}

.icon_search {
  	
}

.signature-msg {
    border-top: 1px dotted #666666;
    color: #999999;
}

.conteneur-messages-pagi {
    border-bottom: 1px solid #444444;
}
}
