@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.google.com") {
  body {
    background-color: #000;
  }
  .gb_5 {
    border-bottom: 1px solid #555;
  }
  /* Top right text menu */
  #gb#gb a.gb_d {
    color: #999;
  }
  #gb#gb a.gb_d:hover {
    color: #CCC;
  }
  /* Menu icon */
  .gb_z .gb_A {
    background-position: -56.5px -70px;
  }
  /* Notifications icon */
  .gb_Wa {
    background-position: 2.5px 0px;
  }
  /* Chat icon */
  .gb_Ob .gb_A {
    background-position: -1px -34px;
  }
  /* Footer */
  #fbar {
    background-color: #1B1B1B;
    border-top: 1px solid #424242;
  }
  /* Footer Text */
  #footer a {
    color: #666;
  }
  #footer a:hover {
    color: #999;
  }
  /* Dark Button CSS */
  #gbqfba, #gbqfbb {
    outline: 0;
    color: #9fa8b0;
    font-weight: bold;
    text-shadow: 1px 1px #1f272b;
    border: 1px solid #1c252b;
    border-radius: 3px;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    background: #232B30; /* old browsers */
    background: -moz-linear-gradient(top, #3D4850 3%, #313d45 4%, #232B30 100%); /* firefox */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,#3D4850), color-stop(4%,#313d45), color-stop(100%,#232B30)); /* webkit */
    box-shadow: 1px 1px 1px rgba(0,0,0,0.2); /* CSS3 */
    -moz-box-shadow: 1px 1px 1px rgba(0,0,0,0.2); /* Firefox */
    -webkit-box-shadow: 1px 1px 1px rgba(0,0,0,0.2); /* Safari, Chrome */
  }
  #gbqfba:hover, #gbqfbb:hover {
    color: #fff;
    background: #4C5A64; /* old browsers */
    background: -moz-linear-gradient(top, #4C5A64 3%, #404F5A 4%, #2E3940 100%); /* firefox */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,#4C5A64), color-stop(4%,#404F5A), color-stop(100%,#2E3940)); /* webkit */
  }
  #gbqfba:active, #gbqfbb:active {
    top: 1px;
    color: #fff;
    background: #20282D; /* old browsers */
    background: -moz-linear-gradient(top, #20282D 3%, #252E34 51%, #222A30 100%); /* firefox */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,#20282D), color-stop(51%,#252E34), color-stop(100%,#222A30)); /* webkit */
    -moz-box-shadow: 1px 1px 1px rgba(255,255,255,0.1); /* Firefox */
    -webkit-box-shadow: 1px 1px 1px rgba(255,255,255,0.1); /* Safari, Chrome */
    box-shadow: 1px 1px 1px rgba(255,255,255,0.1); /* CSS3 */
  }
  .gbqfba, .gbqfbb {
    color: #BBB !important;
  }
  .gbqfba:hover, .gbqfbb:hover {
    color: #fff !important;
  }
  /* Search bar */
  .sbib_b, .sbib_a, #gbqfqwb, #gbqfqw {
    background-color: #333;
    border-radius: 2px;
  }
  #gbqfq {
    color: #BBB;
  }
  .gbqfif {
    color: #666;
  }
  #gbqfqw {
    border: 1px solid #555;
  }
  /* Search results */
   /* Top bar */
   .gb_Pc {
     background-color: #000;
   }
   #hdtb .hdtb_mitem a:hover {
     color: #CCC;
   }
   li a.ab_button {
     background-image: -moz-linear-gradient(center top , rgba(245, 245, 245, 0.5), rgba(241, 241, 241, 0.5));
     background-color: #888;
   }
   /* Content listing */
   li.g {
     padding: 0.6em;
     background-color: #BFBFBF;
     border-radius: 2px;
     width: 106%;
   }
   .gb_Tb {
     background-color: #000;
   }
   #hdtbSum {
     background-color: #000;
     border-bottom: 1px solid #555;
   }
   #appbar {
     background-color: #000;
   }
   #mngb {
     border-bottom: 1px solid #555;
   }
  /* Footer */
  ._HR a, #swml a:hover, .fbar a:hover, #fsett a:hover {
    color: #CCC;
  }
}
