css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("orgmode.org") {

  body {
    background-color: #2f2f2f;
    color: #cfcfcf;
  }

  a {
    color: #709080;
  }
  a:hover,
  a:active {
    color: #9fafaf;
  }
  a:visited {
    color: #efdcbc;
  }

  code,
  pre,
  kbd {
    background-color: #101010;
    color: #cfcfcf;
    border: 1px solid #000000;
    box-shadow: none;
    border-radius: 0.4em;
  }
  
  div#content {
    background-color: transparent;
  }

  h1, h2, h3, h4 {
    color: grey
  }
  
}
