css:
@-moz-document regexp("https*://www.amazon.(ca|cn|co.jp|co.uk|com|com.au|com.br|com.mx|de|es|fr|in|it|nl)/mn/dcw/myx.*") {
div#desktopHeader_myx,
div#desktopFooter_myx,
div#ng-app div[retail-nav-touch-dmyx],
div#ng-app div[navigation-header-dmyx],
div#ng-app hr.navPadding_myx,
div#ng-app div[content-table-dmyx] div[ng-switch="isLargeView"],
div#ng-app div[content-table-dmyx] div[ng-show="isLinked"],
div#ng-app div[content-table-dmyx] div[view-orders-dmyx],
div#ng-app div[content-table-dmyx] div.videoAppCTA_myx,
div#contentAction_delete_myx,
div#contentAction_dummy_del_myx
{
display:none!important;
}
div#ng-app div[content-table-dmyx] div.floatingListHeader_myx
{
position:static!important;
}
div.contentContainer_myx label.listViewIconPosition_myx
{
height:20px!important;
vertical-align:50%!important;
}
div.contentContainer_myx div.contentTableListRow_myx *
{
margin-top:auto!important;
margin-bottom:auto!important;
padding-top:inherit!important;
padding-bottom:inherit!important;
white-space:nowrap!important;
}
div.contentContainer_myx div.contentTableListRow_myx:nth-child(2n+1)
{
background:#eeeeee!important;
}
div.contentContainer_myx hr.contentTableListRow_myx
{
border-color:#333333!important;
}
}
