css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.fanfiction.net") {

/* ad removal borrowed from Stendec, I've got Mozilla with AdBlock so don't get them in the first place */

/* start ad removal */
center + script + div,  table + script[type="text/javascript"] + div,  hr[noshade] + table[width="100%"] + div,
/* right side ads */
center div[align="center"], 
/* top ad div */
table[width="728"][height="90"][cellpadding="1"], 
br + center *,
/* bottom ad */
table.myborder_only_bottom[cellpadding="4"][cellspacing="0"][width="100%"] tbody tr td[align="center"],
/* front page ad. */
span[id^="AdBrite"],
/* Full Page Ads */
table.myborder tr td[valign="top"] div:not([id="bio"])
{display:none !important;} 
/* end ad removal */

/* main background color and text color */
body {
	color: #000000;
	background-color: #CCCCCC  !important;
	font-family:verdana,arial,helvetica,sans-serif;
	font-size: 0.8em !important;
        line-height: 1.1em !important;
}

/* link colors */

a:link {
	color: #6F0000;
	text-decoration: none;
}
a:hover {
        text-decoration:underline;
}
a:active {
}
a:visited {
	color: #00004F;
        text-decoration: none;
}

/* changed the font style of the stories, for easier reading. Made is em's  */
.storytext {
	font-family: verdana, arial, helvetica, sans-serif;
        font-size: 1.05em !important;
	line-height: 1.35em !important;

}


}
