css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.daydeal.ch") {
  body { background-color:#333333 !important; max-width: 960px !important; margin-left:auto !important; margin-right: auto !important; }
  section#navigation { max-width:940px !important; margin-left:auto !important; margin-right: auto !important; }
  section#navigation-static { display:block !important; max-width:940px !important; margin-left:auto !important; margin-right: auto !important; }
  #navigation.fixed  { left:50% !important; margin-left:-470px !important; width:940px !important; }
  
  div#contentWrapper { max-width:960px !important; margin: 0 auto 0 auto !important; border-left:5px solid #38B624 !important; border-right:5px solid #38B624 !important; }
  div#page-content { border-left: 5px solid #127C02 !important; border-right: 5px solid #127C02 !important; }
  div.description { margin-bottom: 2em !important; }
  div.description h1 {  font-size: 22px !important; padding:10px 0 10px 0 !important; margin:0 !important; }
  div.description p { line-height: 130% !important; }
  .meta-first-line { font-size:30px !important; font-family:Tahoma,Arial,sans-serif !important;  }
  .meta-second-line { font-size:25px !important; font-family:Tahoma,Arial,sans-serif !important; }
  span.price { font-size:41px !important; font-family:Tahoma,Arial,sans-serif !important; margin-left:10px !important; }
  span.currency { font-size:28px !important; font-family:Tahoma,Arial,sans-serif !important; }
  
  div.originalPrice { font-size:20px !important; font-family:Tahoma,Arial,sans-serif !important; }
  ul.parent li a { white-space: nowrap !important; font-size:15px !important; font-weight:bold !important; }
  
  #accessoriesCarousel .item__footer { width:80% !important; }
  #accessoriesCarousel .item__label { font-size: 12px !important; }
  
  a.socialIcon { max-width:36px !important; max-height: 36px !important; }
  #socialButtons { margin:30px 0 0 !important; max-height: 25px !important; }
  h3 { font-size: 24px !important; }
  div.carousel-wrapper .slick-prev { left: 15px !important; }
  div.carousel-wrapper .slick-next { right: 15px !important; }
  div.comment-info { text-align:left !important; font-size:22px !important; line-height:130% !important; border-bottom:1px dotted #E0E0E0 !important; padding-bottom:0 !important; margin-bottom:10px !important; }
  div.comment-info div { display:inline-block !important; margin-right:10px !important; font-size:15px !important; }
  div.comment-info span { font-size:15px !important; }
  div.comment-body { text-align:left !important; font-size:14px !important; line-height:130% !important; }
  a.showResponses { font-size:14px !important; }
  
  div.comments-container .intro-text { padding:5px !important; float:left !important; width:100% !important; font-size:12px !important; }
  #commentsTimeline .timeline-comment .comment-body:before { display:none !important; }
  #commentsTimeline .comments-container:before { width:0 !important; }
  #commentsTimeline .timeline-comment.left, #commentsTimeline .timeline-comment.right { padding:0 !important; margin:0 !important; border-top:2px solid #38B624 !important;  }
  #commentsTimeline .timeline-comment { width:100% !important; float:left !important; clear:left !important; }
  #commentsTimeline .timeline-comment.left, #commentsTimeline .timeline-comment.right { float:left !important; clear:left !important; }
  .time-marker  { display:none !important;  }
  
  .comment .comment-title { line-height:105% !important; padding:10px 0 0px 24px !important; }
  .comment .comment-title > h3 {  font-size:18px !important; font-weight:bold !important; font-family:Exo,Arial,Helvetica,sans-serif !important; text-align:left !important; }
  .authorName-wrapper { font-size:15px !important; margin-top:0px !important; }
  .authorName-wrapper div { display:inline-block !important; margin-right:10px !important; }
  
  div.overlay { width: 960px !important; height:100% !important; margin: auto !important; padding:0 10px 0 10px !important;  }
  button.closeOverlay { width:40px !important; height:40px !important; overflow:hidden !important; }
  span.closeOverlayImage { position: absolute !important; right: 1px !important; top: 9px !important; width: 30px !important; height: 19px !important; }
  span.closeOverlayLabel { display:none !important; }
  div.overlayContent { margin-top:5px !important; }
  form#addNewCommentForm { max-width:940px !important; }
  form#addNewCommentForm legend { font-size:26px !important; margin-bottom:20px !important; border-bottom:1px solid #333333 !important; }
  form#addNewCommentForm input, form#addNewCommentForm textarea  { background-color:#D0D0D0 !important; color:#000000 !important; font-size:22px !important; padding-left:5px !important; }
  form#addNewCommentForm textarea  { font-weight:normal !important; font-size:17px !important; }
  form#addNewCommentForm div.ym-fbox { margin-bottom: 6px !important; }
  input::-webkit-input-placeholder { color:#888888 !important; font-weight:normal !important; font-size:15px !important; }
  input:-moz-placeholder { color:#888888 !important; opacity:1 !important; font-weight:normal !important; font-size:15px !important; }
  input::-moz-placeholder { color:#888888 !important; opacity:1 !important; font-weight:normal !important; font-size:15px !important; }
  form#addNewCommentForm button.submit { min-width:150px !important; font-size:19px !important; padding:10px !important; margin:10px 47px 0 0 !important; float:right !important; }
}
