css:
@-moz-document regexp("http?://.*hattrick.org(?!(.*\/Club\/)).*") {
.ft-match-player-home 
{
   background-color: LightBlue !important;
   color: #4272a1 !important;
}

.ft-match-player-away 
{
   background-color: #4272a1 !important;
   color: LightBlue !important;
}

div#cfPollHeader
{
   background-color: CornflowerBlue  !important;
}

.ft-staff-la.ft-staff-foxtrick
{
   background-color: CornflowerBlue  !important;
   color: #4272a1 !important;
}


#stageLogo, #logo 
{
   background:url(http://img4.imageshack.us/img4/9661/logoblueg2.png) top left no-repeat !important;
}

.eventCalendarDate.selectedEventDate
{
   background-color: #cde5fd  !important;
   color: #4272a1 !important;
   border: 1px solid CornflowerBlue  !important;
   border-bottom-width: 0 !important;
}
 
.eventItem 
{
   background-color: #cde5fd  !important;
   border: 1px solid CornflowerBlue  !important;
}

#menu 
{
  background-color: #454545  !important;
}
 
#menu a:hover,.cfHeader
{
  background-color: #4272a1  !important;
  color: #FFFFFF !important
}

a[href]:hover,.subMenuBox h3,p .date, .feedItem .date,.ft-last-login-diff,h2,
a#ft-show-friendlies, a#ctl00_ctl00_CPContent_CPMain_hlAllGames, a.ft-link,
span.date,span#ft_clear_cache, span#ft_report_bug, a#ctl00_ctl00_CPContent_CPMain_repLinks_ctl00_1
{
  color: #4272a1 !important;
}
.boxHead h2 
{
  background-color: #4272a1  !important;
  border-radius: 8px 8px 0px 0px !important;
}

.ignore 
{
   background:transparent url('http://imageshack.us/a/img51/4835/crosssmallf.png') 50% no-repeat !important;
}

input.up
{
  background:transparent url('http://i.imgur.com/8syUXZl.png') 50% no-repeat !important;
}

input.down
{
  background:transparent url('http://i.imgur.com/zdI9iIF.pnghttp://i.imgur.com/zdI9iIF.png') 50% no-repeat !important;
}


a[href]
{
  color: #4272a1;
}
 
.cfHeader a,.boxHead h2
{
  color: white !important;
}
#ft_rapidid a {
  color: #4272a1 !important;
}

.ft-match-report-penalty-shoot-out,.ft-match-report-result,.ft-match-report-extra-time,.ft-match-report-full-time,.ft-match-report-half-time,.ft-match-report-kick-off 
{
   background-color: #4272a1 !important;
}
.ft-popup-list 
{
   border: 2px solid #4272a1  !important; 
}

.ft-popup-list-down a[more],.ft-popup-list-up a[more],.ft-popup-list-down a[href],.ft-popup-list-up a[href]
{
  color: #4272a1 !important;
}
div.htms-stats-loss
{
background-color: #8fafbf !important;
}

table.search, .ok
{

background-color: #cde5fd  !important;
  border: 1px solid CornflowerBlue  !important;
}

#ctl00_ctl00_CPContent_CPMain_lnkCreateTournament
{
color: CornflowerBlue  !important;
background-color: #cde5fd  !important;
  border: 1px solid CornflowerBlue  !important;
  text-shadow: none;
  box-shadow: none;
}
}
