css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("plug.dj") {

/* Background colour */
  /* More transparent */
    /* Moderation, Community type */
#dialog-container .toggle:hover,
#dialog-container .reason:hover,
#dialog-room-create .type:hover,
    /* Specific to unavailable content */
#dialog-restricted-media .divider{
  background-color: rgba(0, 0, 0, 0.25) !important;
} /* less transparent */
#dialog-container .dialog-frame,
#dialog-container .dialog-body,
#dialog-container .dialog-input-background,
div.sign-up.box input,
    /* Specific to create community */
#dialog-room-create .dialog-input-background,
#dialog-room-create .type.selected,
#dialog-room-create .type.selected:hover,
    /* Specific to unavailable content */
#dialog-restricted-media .row:hover,
    /* Specific to staff menu */
#dialog-user-role .role-menu,
#dialog-user-role .role-menu ul,
    /* Specific to edit dialogs */
#dialog-media-update .swap,
    /* Specific to moderation */
#dialog-container .toggle.selected,
#dialog-container .toggle.selected:hover,
#dialog-container .reason.selected,
#dialog-container .reason.selected:hover,
    /* Specific to name change */
#dialog-purchase input,
    /* Registration */
div.sign-up.box,
#login-facebook,
div.sign-up.box .error,
div.sign-up.box #submit,
div.sign-up-overlay.showing .box,
div.sign-up-overlay.showing button,
div.sign-up-overlay.showing .login-facebook,
div.sign-up-overlay.showing input,
    /* RCS */
.rsButton,
.rsBAButton,
.rsBAButton:hover,
#rs-dialog-ccc,
#rs-dialog-ccc .dialog-frame,
#rs-dialog-afk,
#rs-dialog-afk .dialog-frame,
#rs-dialog-afk p,
#rs-dialog-cbg,
#rs-dialog-cbg .dialog-frame,
#rs-dialog-cbg1,
#rs-dialog-cbg1 .dialog-frame,
#rs-dialog-cbg1 p,
#rs-dialog-cm,
#rs-dialog-cm .dialog-frame,
#rs-dialog-cm p,
#rs-dialog-isize,
#rs-dialog-isize .dialog-frame,
#rs-dialog-isize p:first-child{
  background-color: rgba(0, 0, 0, 0.50) !Important;
}     /* Blue */
div.sign-up-overlay.showing .or-circle{
  background-color: rgba(0, 156, 221, 0.25) !important;
}
    /* Background colour hack */
      /*Make frame elements darker */
#dialog-container .dialog-frame,
        /* RCS */
#rs-dialog-ccc .dialog-frame:first-child,
#rs-dialog-ccc .submit,
#rs-dialog-afk .submit,
#rs-dialog-cbg .submit,
#rs-dialog-cbg1 .submit,
#rs-dialog-cm .submit,
#rs-dialog-isize .submit,
div.sign-up-overlay.showing button.selected{
  box-shadow: inset 0px 0px 0px 64px rgba(0, 0, 0, 0.50) !Important;
}
      /* Facebook button */
#login-facebook,
div.sign-up-overlay.showing .login-facebook{
  box-shadow: inset 50px 0px 0px 0px rgb(49, 73, 124) !important;
}
/* Border colour hacks */
  /* Delete playlist field, textbox field empty / disabled */
#dialog-container .no-submit .dialog-input-background.focused,
#dialog-room-create:not(.available) .dialog-input-background.focused,
#dialog-playlist-delete .dialog-input-background.focused,
#dialog-purchase.username .container:not(.available) .focused{
  box-shadow: inset 0px 0px 0px 1px rgb(240, 79, 48) !important;
} /* Textbox; "Checking" */
#dialog-room-create.checking .dialog-input-background.focused,
#dialog-purchase.username .container.checking .focused{
  box-shadow: inset 0px 0px 0px 1px rgb(68, 74, 89) !important;
} /* Moderation toggle buttons */
#dialog-container .toggle.short{
  box-shadow: inset 1px 0px 0px 0px rgb(50, 55, 66) !important;
}
#dialog-container .toggle.medium{
  box-shadow: inset 1px 0px 0px 0px rgb(50, 55, 66),
              inset -1px 0px 0px 0px rgb(50, 55, 66) !important;
}
#dialog-container .toggle.long{
  box-shadow: inset -1px 0px 0px 0px rgb(50, 55, 66) !important;
}
/* Text colours */
  /* Orange */
#dialog-purchase .price,
#dialog-confirm .submit,
#dialog-restricted-media .submit,
#dialog-container .destructive:not(.no-submit) .button.submit,
    /* RCS */
.rsBAButton:hover{
  color: rgb(240, 79, 48) !important;
} /* Gold */
#dialog-purchase .submit.subscribe{
  color: rgb(197, 152, 64) !important;
} /* Purple */
div.sign-up.box .notice a,
div.sign-up.box .already-have button,
div.sign-up-overlay.showing a,
#dialog-gift-send .submit,
    /* RCS */
#rs-dialog,
.rsBAButton{
  color: rgb(172, 118, 255) !important;
} /* Blue */
#dialog-room-create .submit,
#dialog-purchase .submit,
#dialog-gift-receive .submit,
#dialog-user-role .submit,
#dialog-playlist-create .submit,
#dialog-media-update .submit,
#dialog-playlist-rename .submit,
#dialog-alert .submit,
#dialog-level-up .submit,
div.sign-up.box #submit,
div.sign-up-overlay.showing button:not(.sup):not(.log),
    /* RCS */
#rs-dialog-ccc .submit,
#rs-dialog-afk .submit,
#rs-dialog-cbg .submit,
#rs-dialog-cbg1 .submit,
#rs-dialog-cm .submit,
#rs-dialog-isize .submit{
  color: rgb(0, 156, 221) !important;
} /* "Facebook" blue */
#login-facebook,
div.sign-up-overlay.showing .login-facebook
{
  color: rgb(59, 89, 152) !important;
} /* "Error" red */
div.sign-up.box .error{
  color: rgb(219, 24, 46) !important;
} /* White */
div.sign-up.box .notice,
div.sign-up.box .already-have,
#dialog-purchase .label{
  color: rgb(238, 238, 238) !important;
} /* Readibility fixes */
#dialog-container,
#dialog-container input,
    /* RCS */
#rs-dialog-ccc,
#rs-dialog-afk,
#rs-dialog-cbg,
#rs-dialog-cbg1,
#rs-dialog-cm,
#rs-dialog-isize,
div.sign-up-overlay.showing button,
div.sign-up-overlay.showing input,
div.sign-up-overlay.showing .login-facebook{
  text-shadow: 0px 0px 6px black,
               0px 0px 6px black,
               0px 0px 6px black !important;
}
div.sign-up-overlay.showing .or-circle,
div.sign-up-overlay.showing .or,
div.sign-up-overlay.showing .notice,
div.sign-up-overlay.showing .forgot{
  text-shadow: 0px 0px 3px black,
               0px 0px 3px black,
               0px 0px 3px black !important;
}
/* Close button */
.icon.icon-dialog-close{
  background-color: rgba(0, 0, 0, 0.50) !Important;
  box-shadow: 0px -1px 0px 12px rgba(0, 0, 0, 0.50) !Important;
  height: 31px !important;
}
/* Disappear */
  /* Background colour */
#dialog-container .dialog,
    /* Specific to dialog */
div.sign-up,
#dialog-container .button.submit,
#dialog-container .toggle:not(:hover):not(.selected),
#dialog-container .reason:not(:hover):not(.selected),
#diaslog-user-role .role-menu li,
  /* RCS */
.rsBAInput{
  background-color: transparent !Important;
} /* Shadows */
#dialog-container .dialog,
div.sign-up,
#rs-view{
  box-shadow: none !important;
}   /* Hide inactive text */
#dialog-container .no-submit .button.submit,
#dialog-container .destructive.no-submit .button.submit{
  color: transparent !important;
} /* Remove gradiants */
#dialog-container .submit,
#dialog-container .destructive:not(.no-submit) .button.submit,
    /* RCS */
#rs-dialog-ccc .submit,
#rs-dialog-afk .submit,
#rs-dialog-cbg .submit,
#rs-dialog-cbg1 .submit,
#rs-dialog-cm .submit,
#rs-dialog-isize .submit{
  background-image: none !Important;
}

}
