css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("explosm.net") {
div#superheader-bar 
{
  background: linear-gradient(to top , #000 0%, #000 70%, #555 100% ) !important;
  background: #000;
}
div.leaderboard-container,
div#more-from-explosm,
div.row div.columns h2.gamma,
div.clearfix+div.row hr
{
  display: none !important;
}


a
{
  color: #FFA70D;
}

.custom-secondary-button:hover , .custom-primary-button:hover , .custom-facebook-button:hover , .custom-google-plus-button:hover , .custom-twitter-button:hover
{
  color: #FFA70D;
  background: #000;
}
.custom-secondary-button:focus , .custom-primary-button:focus , .custom-facebook-button:focus , .custom-google-plus-button:focus , .custom-twitter-button:focus
{
  color: #FF7707;
  background: #000;
}

.custom-secondary-button , .custom-primary-button, .custom-facebook-button , .custom-google-plus-button , .custom-twitter-button
{
  color: #fff;
  background: #000;
}


div#explosmore-callout-banner
{
  color: #000 !important;
  background: #fff;
}

div#explosmore-callout-banner h4.body-font
{
  color: #000;
}

div#footer
{
  background: #000;
}
div#footer div.row a
{
  color: #fff;
}
div#footer div.row a:hover
{
  color: #FFA70D;
}
div#footer div.row a:focus
{
  color: #FF7707;
}

div#legal-footer
{
  /*background: #000; */
  background: linear-gradient(to bottom , #000, #333 ) !important;
}



img[src$="nav-button_first@2x.png"]
{
  background: url(http://www.imageupload.co.uk/images/2014/12/10/media-control-vector.png) -48px -7px no-repeat !important;
  width: 0px;
  padding: 0px 25px 43px 19px;
  height: 0px;
}
img[src$="nav-button_first@2x.png"]:hover { background: #fd9 url(http://www.imageupload.co.uk/images/2014/12/10/media-control-vector.png) -48px -7px no-repeat !important; }

