img[src$="nav-button_newest@2x.png"]
{
  background:  url(http://www.imageupload.co.uk/images/2014/12/10/media-control-vector.png) -96px -6px no-repeat  !important;
  width: 0px;
  padding: 0px 25px 43px 19px;
  height: 0px;
}
