css:
@-moz-document url-prefix("https://www.tumblr.com") {

/* Home */

#home_button {

    padding-right: -17px !important;
}


#home_button a {

    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 36px !important;
    padding-top: 40px !important;
    background: url('https://31.media.tumblr.com/5a6a0203f63acf0803f5d91a74bc755a/tumblr_inline_ngdymnn3Xe1qc0er4.png') !important;}

/* discover */

