css:
@-moz-document
  url-prefix('https://www.tumblr.com/') 
{
#account_button > .tab_anchor > I::before,
DIV.tab_bar > .tab.iconic > .tab_anchor::before {
    color: /*[[lime]]*/;
}
#xkit-control.xkit-help-forwarder > .tab_anchor {
    background-image: none !important;
}
#xkit-control.xkit-help-forwarder > .tab_anchor::before {
  content: "\f12a";
  font-family: "tumblr-icons","Blank";
  font-size: 24px;
  -webkit-font-smoothing: antialiased;
}
DIV.tab.iconic .tab_notice {
    background-color: /*[[lime]]*/;
}
DIV.tab.iconic .tab_notice .tab_notice_value {
    color: inherit;
}

}
