css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.fantabobshow.com") {
  
/*
  ############################################################################################################
  #Si certaines parties du CSS ne modifient ni les couleurs ni les images, laissez-les et passez votre chemin#
  ############################################################################################################
*/
  
/*Bannière*/
#header
  {background: rgb(46, 98, 176) url(https://dl.dropboxusercontent.com/u/47007147/FBS/SweetDreams.png) repeat-x bottom;}
  
#logo img /*Masquer le logo*/
  {display:none}
  
#userBar .itemLabel /*Masquer le message de mise à jour, je verrai comment le changer plus tard*/
  {display: none}
  
/*Barre nav*/
.secondaryBar /*Marquer tous les forums comme lus etc*/
  {height: 43px;background: transparent url(https://dl.dropboxusercontent.com/u/47007147/FBS/bar.png) top repeat-x;}
  
.navTabs .navTab.selected .navLink /*Accueil/Forum/Shoutbox etc*/
  {background: url(https://dl.dropboxusercontent.com/u/47007147/FBS/bar1.png) repeat-x bottom;}
  
.navTabs .navTab.PopupClosed .navLink /*Les parties où vous n'êtes pas, pour qu'elles soient bien visibles peu importe la bannière*/
  {color: rgb(255, 255, 255);
    text-shadow: 0px 0px 5px rgba(40,40,40,.9);}
  
/*Fond et pied de page*/
#content /*Fond en général*/
  {background: url(https://dl.dropboxusercontent.com/u/47007147/FBS/bg.png) repeat;}
  
.footer /*Partie du bas*/
  {background: url(https://dl.dropboxusercontent.com/u/47007147/FBS/foot-bg.png) repeat-x scroll center top transparent;}
  
.breadcrumb .jumpMenuTrigger /*Flèche vers le bas sur le bout des barres*/
  {background: transparent url(https://dl.dropboxusercontent.com/u/47007147/FBS/circle-arrow-down.png) no-repeat;
  width: 23px;
  height: 15px;}
  
.breadcrumb .crust .arrow span /*Flèche vers le côté dans les barres*/
  {background-image: url(https://dl.dropboxusercontent.com/u/47007147/FBS/breadcrumb-arrow.png);
  width: 7px;}
  
.breadcrumb
  {margin-top: 0px;}
  
.footer .choosers /*Masquer le sélecteur de style intégré (inutilisable)*/
  {display: none;}
  
#copyright a:link, #copyright /*Virer le copyright*/
  {display: none;}
  
.legal span /*Virer les infos légales*/
  {display: none;}
  
/*Forum en lui-même*/
.breadBoxTop /*Barre du haut Accueil>Forum, ne pas changer la hauteur inconsciemment*/
  {background: url(https://dl.dropboxusercontent.com/u/47007147/FBS/bar2.png);
  height: 37px;}
  
.breadBoxBottom /*Barre du bas Accueil>Forum*/
  {background: url(https://dl.dropboxusercontent.com/u/47007147/FBS/bar2.png);}
  
.nodeList .categoryStrip /*Fond des titres des sections*/
  {background: url(https://dl.dropboxusercontent.com/u/47007147/FBS/hightlight.png) repeat-x top;}
 
.mainContent /*Fond du forum*/
  {background: rgb(255, 255, 255) url(https://dl.dropboxusercontent.com/u/47007147/FBS/content-bg.png) repeat-x top;}
  
/*Liste des sujets*/
.discussionList .sectionHeaders /*Barre titre/date de début etc*/
  {background: #4d4d4d url(https://dl.dropboxusercontent.com/u/47007147/FBS/hightlight.png) repeat-x top;}
  
a.callToAction span /*Bouton Poster une nouvelle discussion - Ne pas changer l'url bosser avec le reste*/
  {background: #22826a url(http://forum.fantabobshow.com/styles/minecraft/xenforo/gradients/form-button-white-25px.png) repeat-x center -8px;}
  
a.callToAction /*pareil*/
  {background: #124c3e url(http://forum.fantabobshow.com/styles/minecraft/xenforo/gradients/form-button-white-25px.png) repeat-x center top;
  border: 1px solid #22826a;
  margin-right: 10px;}
  
#content .pageContent /*Fond du forum*/
  {background: rgb(255, 255, 255) url(https://dl.dropboxusercontent.com/u/47007147/FBS/content-bg.png) repeat-x top}
  
/*Overlay*/
.xenPreviewTooltip /*Fond du bloc de prévisualisation du sujet, de base transparent (rendant illisible), préférez un fond clair, j'ai gardé le même que d'habitude*/
  {background: rgb(255,255,255) url(https://dl.dropboxusercontent.com/u/47007147/FBS/content-bg.png) repeat-x top;}
  
.subHeading /*J'ai oublié. Le Highlight habituel*/
  {background: rgb(255,255,255) url(https://dl.dropboxusercontent.com/u/47007147/FBS/hightlight.png) repeat-x top;}
  
.memberListItem /*Parei avec le content-bg*/
  {background: rgb(255,255,255) url(https://dl.dropboxusercontent.com/u/47007147/FBS/content-bg.png) repeat-x top;}

/*Sidebar*/  
.sidebar .section .primaryContent   h3, .sidebar .section .secondaryContent h3 /*Barre des blocs du côté*/
  {background-image: url(https://dl.dropboxusercontent.com/u/47007147/FBS/title2.png);
  background-position: bottom;
  margin: 16px -15px 0;
  top: -17px;}
  
.sidebar .primaryContent, .sidebar .secondaryContent /*Fond des blocs du côté de l'index*/
  {background: rgb(255, 255, 255) url(https://dl.dropboxusercontent.com/u/47007147/FBS/content-bg.png) repeat-x top;}
  
.sidebar .primaryContent, .sidebar .secondaryContent /*La même*/
  {background: rgb(255, 255, 255) url(https://dl.dropboxusercontent.com/u/47007147/FBS/content-bg.png) repeat-x top;}
  
/*Search*/
.formPopup .controlsWrapper /*Ne pas toucher*/
  {background: white url(http://forum.fantabobshow.com/styles/minecraft/xenforo/gradients/category-23px-light.png) repeat-x top}
  
.button.primary /*Bouton Rechercher, je sais pas pq j'ai mis la couleur comme ça*/
  {background-color: rgb(2, 40, 38);}
  
/*Profil*/
.heading, .xenForm .formHeader /*Menu Mon compte*/
  {background-color: #195f4e;}
  
.subHeading
  {}
  
/*Couleurs*/
/*J'ai oublié l'utilité de pas mal, fiez vous aux teintes pour mettre les vôtres*/
/*#124c3e foncé  #22826a clair  #195f4e moyen*/
.primaryContent a /*Lien tel qu'on le trouve dans l'index en titre de section*/
  {color: #124c3e;}
  
.secondaryContent a /*Lien tel qu'on le trouve dans l'index en dernier message (à droite des titres)*/
  {color: #22826a;}
  
a:link, a:visited /*Liens visités, pas d'exemple*/
  {color: #124c3e;}
  
.sidebar .section .secondaryContent .footnote, .sidebar .section .secondaryContent .minorHeading
  {color: #195f4e}
  
#userBar .Popup .PopupControl.PopupOpen a, #userBar .Popup.PopupContainerControl.PopupOpen a
  {color: #124c3e;}

#userBar a, #userBar .itemCount /*Ne pas toucher*/
  {color: rgb(255,255,255);}
  
.Menu
  {border-color: #22826a}
    
.Popup .PopupControl.PopupOpen, .Popup.PopupContainerControl.PopupOpen
  {background: #22826a url(http://forum.fantabobshow.com/styles/minecraft/xenforo/gradients/user-bar-hover.png) repeat-x top;}
  
.Popup .PopupControl.PopupOpen, .Popup.PopupContainerControl.PopupOpen
  {background:  #22826a url(http://forum.fantabobshow.com/styles/minecraft/xenforo/gradients/user-bar-hover.png) repeat-x scroll center top;}

.blockLinksList a, .blockLinksList label
  {color: #124c3e;}
  
.textWithCount.subHeading .text /*Profil de membre, "Je suis fan";"Mes fans"*/
  {color: rgb(255,255,255)}
}
