css:
@-moz-document url-prefix("http://bkrs.info/") {
/*Данный стиль посвящается всем изучающим китайский язык, а также им сочувствующим.
Главная цель — увеличить размер иероглифов.
Побочная цель — максимально упростить интерфейс прекрасного внутри, но страшного снаружи сайта bkrs.info.
<3 SD*/


/*Навигационная панель*/
#nav a {
	padding: 5px 5px;
  	font-family: calibri light, Arial, sans-serif;
  	font-size: 16pt;
}
.pt10 {
	font-size: 17pt;
    font-family: calibri light, Arial, sans-serif;
}


/*Сайт любителей КЯ*/
.anti_eyes {
  	padding: 5pt;
  	font-family: calibri light, Arial, sans-serif;
    font-size: 16pt;
}


/*daБКРС*/
#bkrs {
	font-family: KaiTi, simsun;
	font-size: 90pt;
	font-weight: bold;
	color: #c70000;
}
#bkrs2 {
	font-family: georgia, arial, sans-serif;
	font-size: 50pt;
}


/*Увеличить иероглифы в поисковой выдаче*/
.pt14 {
	font-family: Arial, sans-serif;
  	/*font-size: 26pt;*/
}
.black {
	font-family: Arial, sans-serif;
  	font-size: 26pt;
}
.ex_tbl {
	font-size: 26pt;
  	font-family: Arial, sans-serif;
}
.py {
  	font-family: Arial, sans-serif;
    font-size: 26pt;
}
.ru {
  	font-family: Arial, sans-serif;
    font-size: 26pt;
}
.ch {
  	font-family: Arial, sans-serif;
    font-size: 26pt;
}
.ch_ru {
	font-family: Arial, sans-serif;
	font-size: 26pt;
	/*font-weight: bold;*/
}
.ex_tbl {
  	font-size: 18pt;
	font-family: Arial, sans-serif;
}
#ch {
  	font-family: Arial, sans-serif;
    font-size: 26pt;
  	/*font-weight: bold;*/
}

    
/*Карандаш*/
.img {
	padding: 10pt;
	vertical-align: middle;
}
#sx {
	padding: 10pt;
}


/*Кнопка поиска*/
#go_button {
  	background-color: #c70000;
	-moz-border-radius:10px;
	-webkit-border-radius:10px;
	border-radius:10px;
	border:1px solid #942911;
	display:inline-block;
	cursor: pointer;
	color:#ffffff;
	font-family: calibri light, Arial, sans-serif;
	font-size:25px;
	padding: 4pt 20pt 4pt 20pt;
	text-decoration:none;
	text-shadow:1px 1px 0px #854629;
}
#go_button:hover {
	background-color:#eb2f21;
}
#go_button:active {
	position:relative;
	top:1px;
}


/*Убрать недавний поиск (на Главной)*/
#last_search_table {
	display:none;
}


/*Убрать форум (на Главной)*/
#treads_container {
	display:none;
}


/*Поисковая строка*/
#lips {
	display:none;
}
#kw {
	font-size: 25pt;
	text-align: left;
	font-family: calibri light, Arial, sans-serif;
}
#treads_container {
	display:none;
}


/*Убрать чат (на Главной)*/
.chat_tr_text {
	display:none;
}
.chat_tr_user {
	display:none;
}
.chat_tr_date {
	display:none;
}
textarea:disabled {
	display:none;
}
#chat_show_more {
	display:none;
}
#chat_div {
	display:none;
}
#chat_info_img {
	display:none;
}
.pt8 {
	display:none;
}
.gray {
	display:none;
}
#chat_container {
	display:none;
}
#threads_hidden {
	display:none;
}
}
