css:
@-moz-document url-prefix("http://www.sbmania.net/forums/") {
body {
  background: #101010;
  font-family: Century Gothic;
  min-height: 100%
}

#leftTop, #rightTop, #leftBottom, #rightBottom {
  display: none;
}

.rules li a{
  color: grey;
  font-size: 30px;
  font-weight: normal;
  position: absolute;
  top: 5px;
}

.header_bar_left {
  border-radius: 0px 0px 0px 0px;
  background: #202b2b;
}

.header_bar {
  height: 30px;
  background: #202b2b;
}

.header_bar_right {
  padding: 0px;
  background: none;
}
  
li #user_link {
  font-size: 30px;
  color: grey;
}

#inbox_link {
  position: absolute;
  top: -2px;
  left: 200px;
}

#notify_link {
  position: absolute;
  top: -2px;
  left: 260px;
}

.left a, .ipbmenu {
  font-family: Century Gothic;
  font-weight: bold;
}
  
#community_app_menu {
  background-color: black;
  width: 1031px;
  border-radius: 15px 15px 0px 0px;
}
  
#content {
  background-color: black;
  width: 1030px;
  visibility: visible;
  border-radius: 15px 15px 15px 15px
}

#corners {
  visibility: hidden;
}

.left, .ipbmenu, .right {
  visibility: visible;
}
  
#search {
  visibility: visible;
}
  
#nav_explore a {
  font-family: Century Gothic;
  font-weight: bold;
}
  
.row2 {
  background: black;
}
    
.row2 td {
  color: white;
}
      
.row td a {
  color: white;
}

.altrow td a {
  color: grey;
}

#chat-tab-count {
  background: black;
  height: 10px;
  border: 1px solid grey;
  font-weight: lighter;
  font-family: Century Gothic;
}

.maintitle a {
  font-family: Century Gothic;
  font-weight: bold;
  color: #CCCCCC;
}
  
.maintitle a img {
  display: none;
}
    
.submenu_links {
  border-radius: 10px;
}
      
#more_apps_menucontent {
 background: grey;
 border-radius: 10px;
}
      
#shoutbox-shouts {
 border-radius: 10px;
}
      
.block_wrap {
 border-radius: 10px;
}
        
.bottom {
 display: none;
}
}
