@-moz-document url-prefix("https://www.tumblr.com") {

/* Home */

#home_button {

    padding-right: -17px !important;
}


#home_button a {

    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 36px !important;
    padding-top: 40px !important;
    background: url('http://i.imgur.com/0XxucP3.png') !important;}

/* discover */

#discover_button {

    padding-right: -17px !important;
}


#discover_button a {

    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 36px !important;
    padding-top: 40px !important;
    background: url('http://i.imgur.com/A1iE2vJ.png') !important;}


/* Help */

#help_button {

    padding-right: -17px !important;
}


#help_button a {

    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 36px !important;
    padding-top: 40px !important;
    background: url('https://31.media.tumblr.com/77732d97b5127905f3ae5abd5f709672/tumblr_inline_ngfrm5uiwd1qc0er4.png') !important;}

/* Inbox */

#inbox_button {

    padding-right: -17px !important;
}


#inbox_button a {

    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 36px !important;
    padding-top: 40px !important;
    background: url('http://i.imgur.com/kgq54eO.png') !important;}

/* Settings */

#preferences_button {

    padding-right: -17px !important;
}


#preferences_button a {

    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 36px !important;
    padding-top: 40px !important;
    background: url('https://31.media.tumblr.com/f6cb55628031c5559f6ba6796dd6dd75/tumblr_inline_ngfrlyI3vH1qc0er4.png') !important;}

/* Xkit */

#xkit-control {

    padding-right: -17px !important;
}


#xkit-control a.tab_anchor {

    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 36px !important;
    padding-top: 40px !important;
    background: url('https://31.media.tumblr.com/a6b278af1c0c48c392edce6aea4b736e/tumblr_inline_ngfrleAN4O1qc0er4.png') !important;}

/* Log Out */

.tab.iconic.tab_log_out a{

    padding-right: -17px !important;
}


.tab.iconic.tab_log_out a {

    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 36px !important;
    padding-top: 40px !important;
    background: url('https://31.media.tumblr.com/b36b93c8d8510de65091f350ae748c04/tumblr_inline_ngfrmeH1qr1qc0er4.png') !important;}


/*Positioning*/

.tab_bar .iconic a.tab_anchor {background-size: 175px 36px;}

.tab_bar .tab_discover .tab_anchor {background-position: -175px 0}

.tab_bar .tab_log_out .tab_anchor {background-position: -140px 0}

.tab_bar .tab_settings .tab_anchor {background-position: -105px 0}

.tab_bar .tab_help .tab_anchor {background-position: -70px 0}

.tab_bar .tab_inbox .tab_anchor {background-position: -35px 0}

.tab_bar .tab_home .tab_anchor {background-position: 0 0}

.tab_bar .tab.iconic:hover .tab_anchor,.tab_bar .tab.iconic:active .tab_anchor {opacity: .7;}

.tab.iconic > .tab_anchor::before {content: none !important;}

}
