css:
@-moz-document domain("rrys.tv") {
body {
font-size: 14px;
font-family: 'Microsoft Yahei',Arial;
line-height: 150%;
color: #575757;
margin: 0;
padding: 0;
background: #e5e5e5;
}

.menuBox ul li a {
float: left;
display: block;
padding: 0 11px;
font-size: 17px;
height: 41px;
line-height: 41px;
color: #FFFFFF;
}

.menuBox {
clear: both;
background: #0983B8;
height: 41px;
margin-bottom: 18px;
}

.itemTit {
background: #0983B8;
height: 29px;
line-height: 29px;
}

.box_1 {
background: #FFFFFF;
 border: none;
}

.menuBox ul a:hover, .menuBox ul a.cur {
background: #046189;
color: #FFFFFF;
}

.topNav {
height: 32px;
line-height: 32px;
background: #2A2A2A;
color: #0983B84db;
position: relative;
z-index: 10000;
overflow: visible;
}

.footer {
background: #edebde;
padding: 0px;
line-height: 180%;
margin-top: 38px;
color: #543c01;
text-align: center;
overflow: hidden;
/* display: none; */
margin-top: 28px;
height: 1px;
overflow: hidden;
}

ul#tabs li a.current {
color: #0983B8;
height: 61px;
background: #0983B8;
}

ul#tabs li a.current {
color: #0983B8;
height: 61px;
background: #0983B8;
}

.searchBox #keyword_bnt {
float: right;
background: #353535;
width: 69px;
height: 30px;
border: none;
cursor: pointer;
vertical-align: middle;
font-weight: bold;
color: #FFFFFF;
font-size: 14px;
}

ul#tabs li a {
color: #000;
display: block;
height: 62px;
overflow: hidden;
background: #0983B8;
width: 261px;
}

.aft_log, .aft_log a {
color: #FAFAFA;
}

.user_l .u_i1 {
display: inline-block;
height: 32px;
line-height: 32px;
padding-left: 18px;
background: none;
}

.d_list dt {
background: none;
height: 32px;
line-height: 32px;
padding-left: 35px;
cursor: pointer;
}

.topSrearch .yyets_logo a {
background: none;
}

.searchBox #keyword {
float: left;
height: 29px;
line-height: 29px;
width: 380px;
padding: 0 0 0 3px;
background: #FFF;
font-size: 14px;
vertical-align: middle;
border: 1px solid #BBBBBB;
}

.searchBox .keyworde span {
color: #CD0000;
}

.bnts_6 {
background: #736519;
height: 22px;
line-height: 22px;
padding: 0 5px;
display: inline-block;
color: #fff;
font-size: 12px;
display: none;
}

#tab2 li.cur {
background: #0983B8;
color: #fff;
}

.gBoxMid strong.score {
color: #0983B8;
font-size: 60px;
position: absolute;
top: 10px;
right: 20px;
display: block;
padding: 20px;
font-family: '微软雅黑';
font-weight: normal;
}

/*首页结束*/

.border2blue {
border: none;
background: white;
}

.userMenu li a {
display: block;
height: 30px;
line-height: 30px;
margin-bottom: 5px;
font-weight: bold;
text-indent: 10px;
color: #5C5C5C;
}

.userMenu li a.cur, .userMenu li a:hover {
color: #FFF;
background: #0983B8;
z-index: 99;
}

ul.u_d_list li .f_r_info .title span.f_r {
display: inline-block;
padding: 5px 8px;
height: 18px;
line-height: 18px;
background: #0983B8;
color: #FFF;
}

.itemTit3 {
background: #0983B8;
height: 100%;
padding: 1px 10px;
color: #fff;
}

.bnt_o_i5 strong {
display: inline-block;
height: 22px;
line-height: 22px;
padding: 0 23px 0 6px;
position: relative;
color: #FFF;
background: #0983B8;
}

.bnt_o_i5 strong.cur {
background: #005D86;
color: rgb(176, 176, 176);
height: 26px;
line-height: 26px;
}
.bnt_o_i5s strong {
padding: 2px 10px;
}

ul.u_d_list li {
padding: 16px 0px;
border-bottom: 1px solid #C3C3C3;
}

.rss_tags .f_r a.cur {
background: #0983B8;
color: #fff;
}

.top_list2 .f_r_info strong.top1 {
background: #0983B8;
}

.top_list2 .f_r_info strong.top {
font-weight: normal;
display: inline-block;
background: #0983B8;
padding: 0 5px;
height: 16px;
line-height: 16px;
color: #fff;
margin-bottom: 5px;
}


.descBox li.cur a {
background: #0983B8;
color: #fff;
box-shadow: inset 0 0 8px #333;

}

.descBox {
background: #009FE4;
}

.descBox li a {
display: block;
height: 36px;
line-height: 36px;
padding: 0 10px;
float: left;
color: white;
}

.descBox li a:hover {
background: #0983B8;
color: #fff;
}

.room {
height: 155px;
display: none;
}

.box_4 {
background: #E5E5E5;
border: none;
}

.res_listview ul li {
padding: 10px;
border: none;
background: white;
margin-bottom: 5px;
}

.dashed li, .dot {
background: none;
border-bottom: 1px solid rgb(202, 202, 202);
}

.res_listview ul li .f_r_info .list_b {
background: #EFEFEF;
border: 1px solid #E2E2E2;
padding: 3px 10px;
height: 40px;
overflow: hidden;
}

.dashed li.last, .dashed li:last-child {
background: #FFFFFF;
}

/*今日更新*/
.t_s a:hover, .t_s a.cur {
background: #0983B8;
font-weight: bold;
color: #FFF;
}

.t_s a {
display: inline-block;
width: auto;
height: 27px;
line-height: 27px;
text-align: center;
font-size: 14px;
margin-right: 2px;
padding: 1px 10px;
}

.day_data {
border-top: 8px solid #0983B8;
border-top-left-radius: 3px;
border-top-right-radius: 3px;
border-bottom: none;
}

.d_r_t th {
font-weight: normal;
background: #0983B8;
padding: 3px 6px;
vertical-align: top;
color: white;
text-align: center;
}

.boxPadd {
padding: 8px;
background: white;
}

.descBox {
background: #049FE2;
border-top: none;
}

.sidebarlist a {
display: block;
border: none;
height: 30px;
line-height: 30px;
margin-bottom: 2px;
padding-left: 7px;
color: #2a2a2a;
overflow: hidden;
}

.sidebarlist a:hover {
font-weight: bold;
background: #0091D0;
border-color: #0091D0;
color: #fff;
}

.pages a.cur, .pages a:hover {
background: #0983B8;
color: #fff;
}
.pages a {
display: inline-block;
color: #1c1c1c;
border: none;
height: 19px;
line-height: 19px;
padding: 0 7px;
margin-left: 6px;
border-radius: 3px;
background: white;
}

ul#tabs li a {
color: #FFF;
display: block;
height: 62px;
overflow: hidden;
background: #0983B8;
width: 261px;
}

.bnt_o_i5 .a1 {
width: auto;
padding: 5px;
}

ul#tabs li a.current {
color: #FFFFFF;
height: 61px;
background: #08A0E2;
}

}
