   color: #dadada;
 }
 h1, h2, h3, h4, h5, h6, 
 h1 a:link, h1 a:visited, h2 a:link, h2 a:visited, h3 a:link, h3 a:visited, h4 a:link, h4 a:visited, h5 a:link, h5 a:visited, h6 a:link, h6 a:visited,
 .articleLinks li a, body.ss-base-body,
 .partnerZon .sms-ad .description a, a.teaserIntroLink, a.teaserIntroLink:hover, .partnerZon .sms-ad .heading a,
 #divCommentsPage .divNewslistWideContainer *{
   color: #dadada;
 }
 .toolKit .toolItem a {
   color: #bbb;
 }
 .csjobbWidget, .csjobbWidget .vinjett1 span, .featuredJobs .csjobbFrameWrapper, .featuredJobs .csjobbFrameWrapper a {
   background-color: #000;
   color: #dadada;   
 }
  
