css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://calendar.sunrise.am/") {

html,body,div,span,applet,object,iframe,h1,h2,h3,h4,h5,h6,p,blockquote,pre,a,abbr,acronym,address,big,cite,code,del,dfn,em,img,ins,kbd,q,s,samp,small,strike,strong,sub,sup,tt,var,b,u,i,center,dl,dt,dd,ol,ul,li,fieldset,form,label,legend,table,caption,tbody,tfoot,thead,tr,th,td,article,aside,canvas,details,embed,figure,figcaption,footer,header,hgroup,menu,nav,output,ruby,section,summary,time,mark,audio,video {
font-family:'Open Sans Condensed';
}

.navbar__item>a .icon,.navbar__item>a .ui-datepicker .ui-icon,.ui-datepicker .navbar__item>a .ui-icon,.navbar__item>span .icon,.navbar__item>span .ui-datepicker .ui-icon,.ui-datepicker .navbar__item>span .ui-icon,.tooltip-inner,.autocomplete-menu-item strong,.daynames__item--is-weekend.daynames__item--is-today,.daynames__item--is-today,.badge,.btn,.meet-reply .calendar-picker__action,.occurrence__overlay,.confirm__header h1,.show-more__header,.form-input--large,.form-select,.dialog .timezone-picker__btn,.form-reminder,.form-title p,.invitations-popover__title,.invitations__range,.dialog__bar__title,.people-picker__search-choice,.birthdays__title,.calendar-picker__list-calendar__title,.active .timezone-picker__label,.active .timezone-picker__local-time,.sidebar__bottom a,.connect__errors,.calendars-list__title,.navbar__item>a strong,.navbar__item>span strong,.month__day__date,.connection-form__error,.meet-edit__timeslots__item__overlay,.meet-edit__popover-list__title,.meet-button__list-item__title {
font-weight:700;
color:#1ac08e;
}

.sidebar__settings .icon_gear {
color:#ff604b;
}

.navbar__item,.daynames__item {
font-size:20px;
border:1px solid #1AC07E;
text-transform:uppercase;
font-weight:700;
color:#FA7564;
}

.app-sidebar .ui-datepicker-calendar td.ui-state-active {
background-color:#1abc9c;
}

.app-sidebar .ui-datepicker {
font-size:18px;
background-color:#16a085;
}

.app-sidebar .ui-icon-circle-triangle-w,.app-sidebar .ui-icon-circle-triangle-e {
color:#FA7564;
}

.app-sidebar .ui-datepicker-calendar thead th {
font-size:22px;
color:#FFF;
}

.app-sidebar .ui-datepicker-title {
font-size:22px;
text-transform:uppercase;
color:#FFF;
}

.calendars-list__list>li {
font-size:16px!important;
text-transform:uppercase;
color:#fff;
}

.sidebar__search-input .icon_search {
top:9px;
left:11px;
color:#FFF;
font-size:19px;
}

.sidebar__search-input input {
font-size:17px;
display:block;
text-transform:uppercase;
border:2px solid #1ABC9C;
background-color:#FF604B;
border-radius:50px;
padding:0 33px 0 35px;
}

.sidebar__search-clear .icon_plus {
color:#FF0202;
top:-1px;
right:-4px;
}

.search-results__title {
color:#FFF;
font-weight:200;
}

.search-results__datetime {
text-transform:capitalize;
color:#FF604B;
}

.search-results__datetime .muted {
text-transform:capitalize;
color:#1AC08E;
padding-left:4px;
}

.sidebar__list__title {
font-size:18px;
color:#FDFDFD;
text-transform:uppercase;
margin:-7px 7px 5px;
padding:0;
}

.sidebar__top:after,.sidebar__bottom:after,.sidebar__bottom:after {
visibility:hidden;
}

.sidebar__top,.sidebar__bottom {
border:1px solid #1ABC9C;
}

.month__day__date {
font-size:16px;
text-transform:capitalize;
padding:1px 5px 0;
}

.sidebar__list>li {
margin-bottom:15px;
}

.calendars-list__title {
font-size:16px;
margin-bottom:12px;
}

.timezone-picker__btn>.icon_earth {
font-size:28px;
top:-1px;
margin-left:7px;
margin-top:-4px;
color:#2c3e50;
}

.timezone-picker__btn .icon_chevron-bottom {
font-size:18px;
}

.btn,.meet-reply .calendar-picker__action,.btn-link,.btn-facebook,.btn-mail,.btn-google {
border-radius:25px;
}

.sidebar__list {
position:relative;
z-index:1;
padding:11px 4px;
}

.navbar__wrapper,.sidebar__top,.sidebar__search-input,.sidebar__content,.sidebar__search-clear {
background-color:#2c3e50;
}
}
