css:
@-moz-document domain("www.bt-chat.com") {
br,
.tbox,
.newstop,
.switchcontent,
.menumid,
.menubottom,
#news,
.newsbottom,
form a,
td.newsmid table  td table td,
.center.thinbarmid,
.aRight:only-child,
.all tr:first-of-type,
body > div:first-of-type,
body > table:nth-of-type(2) > tbody > tr > td:nth-of-type(2),
body > table:last-of-type,
body > table:last-of-type ~ *
{
	display:none !important;
}

body > table:nth-of-type(1) > tbody > tr {
  border-bottom: 1px solid black;
}

td.newsmid table.t540  td table td { display : table-cell;}


body {
	font-size: 16px !important;
	background: #fff !important;
	color:#47525c !important;
	text-align: center !important;
    margin:0 !important;
}

img { border:none !important;}


td,th {
	font-size: 16px !important;
	margin: 0 !important;
	background: #fff !important;
	color : #555 !important;
}

th { color:#000 !important;}

.bb { color:#fff !important;}

table {
	text-align: center !important;
	border-collapse:collapse !important;
	border:none !important;
}

.ul {text-decoration: none !important;}

a:link {
	color: #5874A3 !important;
	text-decoration: none !important;
}

a:hover {
	text-decoration: underline !important;
	color: #000000 !important;
}

a:visited {
	color: hsl(0, 0%, 90%) !important;
}

tr.off {
	font-size: 16px !important;
	background: #353535 !important;
}

tr.on {
	font-size: 16px !important;
	background: #eee !important;
}

.bottom { border-bottom: 1px dotted #999 !important; }
.top { border-top: 1px dotted #999 !important; }
.right { border-right: none !important;}
.left {	border-left: none !important; }

.all td {
	font-size : 0.8em !important;
	height : 40px !important;
}


input {
	background: #f5f5f5 !important;
	border: 5px solid #ccc !important;
	color: #999 !important;
	font-weight: 700 !important;
	font-size : 1.5em !important;
	padding : 8px 0.3em !important;
	letter-spacing : -1px !important;
}

input:hover, input:focus ,select:hover, select:focus {
	background: #eee !important;
	border: 5px solid #71cdff !important;
	color: #5874A3  !important;
}

input[type=text] {
	letter-spacing : -2px !important;
	padding : 9px 0.3em !important;
}


.buttonBarright {
  text-align: right !important;
  vertical-align: top !important;
  margin: 0 !important;
  padding: 4px 0 !important;
}

table, .center {margin : 0 auto !important;}

.aRight {
	text-align: right !important;
	margin-left: auto !important;
	margin-right: 0 !important;
}

.aLeft {
	text-align: left !important;
    margin-left: 0 !important;
    margin-right: auto !important;
}

.leftI {
    margin-left: 30px !important;
	margin-right: auto !important;
}

.pagenumlink {
        border: none !important;
        color: #71cdff !important;
        font-size: 12px !important;
}

.menumid {
    background:#fff !important;
	vertical-align:top !important;
}

.menubottom {
    background:#fff !important;
	width:147px !important; 
	height:20px !important;
}

.newsmid {
	background:#fff !important;
	color : #444 !important;
	vertical-align:top !important;
}

td[style='width:60px'] {
  width: 100px !important;
}
}
