css:
@-moz-document domain("jeuxvideo.com"), domain("forumjv.com") {
/*=================================================================================
 Un morceau de code pris de Guklam, créateur de Old JVC, MERCI ! http://www.jeuxvideo.com/forums/42-50-37763903-1-0-1-0-nofake-old-jvc-l-ancien-jvc.htm */


/*============ Retire les pubs ============*/
.part-boutique, 
.bonus-track, 
.bonus-sponso,
.mdd-card-suggest,
#prospect.show-pub, 
#prospect #table-prospect, 
#prospect-onglet a, 
#jv-feedback .btn-feedback,
#prospect-square.show-pub, 
#prospect-square2.show-pub, 
#prospect-square3.show-pub,
#wbdds_insertion_34271_interstitial,
#prospect {
  display:none !important;
}

/* Retire le logo bêta */
#header-logo:after {
	content: none !important;
}
/* Agrandissement de la barre de recherche */
#header-search-jv input[type="text"] {
	width: 210px !important;
}

/* Décale la barre de recherche */
.affix #header-search-jv {
	left: 23px !important;
}
/*=================================================================================*/


/* Couleur de fond */ 
#content {
margin-top: 0;
margin-bottom: 0;
padding-bottom: 20px;
background-color: #2c2c2c;
/*background-image: url('./img/generique/page-bg-3.jpg');*/
background-repeat: no-repeat;
background-position: center top;
}

/* Couleur du cadre central (où il y a les topics, messages) */
.container-content {
background: #EEF5FC;
margin: 0 auto;
padding: 20px 20px 0 20px;
min-height: 600px;
}

/* Couleur message topic (1) */
.bloc-message-forum:nth-child(odd) {
background: #e8f2fc;
}

/* Couleur message topic (2) */
.bloc-message-forum:nth-child(even) {
background: #f7fafc;
}

}
