css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.somethingawful.com") {

#thread table.post td.postbody,
form[method="POST"] textarea,
.inner.postbody
{
  font-family: Verdana!important;
  font-size: 13px!important;
}
#thread .post .userinfo dt.author
{
  font-family: Verdana!important;
  font-size:14px!important;
  font-weight:bold!important;
  margin-bottom:0px!important;
}
.breadcrumbs a,
.breadcrumbs,
.private_messages,
#cpmain #forum th,
#subforums .subforum dd,
#forum.threadlist th,
#forum.threadlist th a,
.thread td,
.thread td a,
#thread .userinfo .registered
{
  font-family: Verdana!important;
}
#navigation a,
#nav_purchase a,
.navigation a
{
  font-family: Arial!important;
  font-weight:normal!important;
}
dl.userinfo dd.title,
dl.userinfo dd.title a
{
  font-family: Verdana!important;
}
.postdate,
.postlinks
{
  font-family: Arial!important;
}
#forums .forum td a,
#forums .forum td
{
  font-family: Verdana!important;
  font-weight:600!important;
  font-size:12px!important;
}
#index #info td,
#index #info td a,
#index #pm td,
#index #pm td a
{
  font-family: Arial!important;
}
.forumhome #forums td.title
{
  height:40px!important;
}
#forums .forum .subforums,
#forums .forum .subforums a
{
  font-family: Arial!important;
  font-size:10px!important;
  font-weight:normal!important;
}
#forums .section .category a
{
  font-family: Arial!important;
  font-size:10px!important;
  font-weight:bold!important;
}
#forums td.moderators a
{
  font-size:10px!important;
  font-weight:normal!important;
}
a.thread_title
{
  font-family: Verdana!important;
  font-size:13px!important;
  padding-top:0px!important;
}
.title a.pagenumber,
#forum .thread .title_pages
{
  font-family: Verdana!important;
  font-size:10px!important;
}
#subforums tr.subforum td.title a
{
  font-family: Arial!important;
}
.thread .title .title_pages,
.thread .title .title_pages a,
.title .title_inner
{
  padding:0px!important;
  margin:0px!important;
  min-height:20px!important;
}
.thread .title .title_pages a
{
  letter-spacing:2px!important;
}
.thread .title .title_pages a[href*="goto=lastpost"]
{
  letter-spacing:0px!important;
}
.thread
{
  height:42px!important;
}
.lastseen *
{
  font-size:10px!important;
  font-family: Arial!important;
}
#thread .userinfo .title
{
  margin-top:0px!important;
}
#thread table.post td
{
  letter-spacing:0px!important;
}


}
