css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("wikipedia.org") {
#mw-head > *:not(#right-navigation), #right-navigation > *:not(#p-search)
, #p-logo, #p-lang *, #mw-fr-reviewnotice, .mw-editsection, #footer
,#mw-panel:not(:hover) * ,#siteNotice {display:none}

#p-search:not(:hover) > * {visibility: hidden}

#mw-panel:not(:hover):before {content:"Меню"; font-size:1em; color:#fff}

#p-navigation-label {display: block !important}

div#mw-panel {top:0px; width: auto; padding-top: .7em; position: fixed}
div#mw-panel > * {float:left; margin: 0 2.5px !important; padding: 0 .5em 0 .25em !important; border-radius: 2px; background: #fff !important; box-shadow: 0 0 3px #888}
div#mw-panel div.portal div.body {margin: .25em .25em .5em !important}
#mw-panel > *:hover {box-shadow: 0 0 5px #000}

html * {font-family: Georgia,"Times New Roman",Times,serif !important; font-size: 1em}
body, #mw-page-base {background: none !important}
#p-search {position: fixed; right: 0; top: 0}
.mw-body {position: absolute; margin: 0; top:0; left:100px; background:rgb(251,251,251) !important; width: calc(100% - 250px); border:none; box-shadow: 0 0 13px #888; text-align:justify}
.reference {font-size: .7em}
}
