css:
/************************
*	Nom : Old JVC orange;		*
*	Auteur : my_name_is_not *
*	Date: 13/12/14;		*
*************************/

/*====================== CORPS PRINCIPAL ======================*/

@-moz-document domain(jeuxvideo.com), 
domain(forumjv.com),
domain(web.archive.org){

/* Change le fond du site */
#content {
  	background:url(http://image.noelshack.com/fichiers/2014/50/1418128222-body-bg-jvshack.png) repeat-x 0 0 rgb(215, 215, 215);
}
	
/* Change le fond du header */
#header-top {
	background: url(http://image.jeuxvideo.com/css_img/defaut/header/logo_bg.png) !important;
}
	
/*============ Retire les pubs ============*/
.part-boutique, 
.bonus-track, 
.bonus-sponso,
.mdd-card-suggest,
#prospect.show-pub, 
#prospect #table-prospect, 
#prospect-onglet a, 
#jv-feedback .btn-feedback,
#prospect-square.show-pub, 
#prospect-square2.show-pub, 
#prospect-square3.show-pub,
#wbdds_insertion_34271_interstitial,
#prospect,
#wootbox_campaign {
  display:none !important;
}
	
/*============ Change la couleur des différents liens ============*/
.panel-jv-forum .lien-pratique-gestion, 
.panel-jv-forum .bloc-modo-fofo, 
.panel-jv-forum .titre-info-fofo, 
.panel-jv-forum .liste-modo-fofo,
.panel-jv-forum .bloc-info-forum .lien-jv, 
.panel-jv-forum .bloc-forums-preferes .lien-jv, 
.panel-jv-forum .bloc-sous-forums .lien-jv {
  	color:black !important;
}
	
.panel-jv-forum .bloc-info-forum .lien-jv:hover, 
.panel-jv-forum .bloc-info-forum .lien-jv:focus, 
.panel-jv-forum .bloc-info-forum .lien-jv:active, 
.panel-jv-forum .bloc-forums-preferes .lien-jv:hover, 
.panel-jv-forum .bloc-forums-preferes .lien-jv:focus, 
.panel-jv-forum .bloc-forums-preferes .lien-jv:active, 
.panel-jv-forum .bloc-sous-forums .lien-jv:hover, 
.panel-jv-forum .bloc-sous-forums .lien-jv:focus, 
.panel-jv-forum .bloc-sous-forums .lien-jv:active {
  color: rgb(255, 64, 0) !important;
}
	
.bloc-message-forum .text-modo, 
.bloc-avis .text-modo, 
.form-control-tag-inner .text-modo, 
.list-msg .text-modo, 
.text-modo
#table-liste-topic-forum .auteur-topic .text-modo 
{
	color: rgb(204, 0, 0) !important;
}
	
.bloc-liste-num-page a:visited {
	color:silver !important;
}
	
.panel-jv-forum .bloc-forums-preferes .lien-jv {
	background: url(http://image.jeuxvideo.com/css_img/defaut/sommaire/sommaire_puces.png) no-repeat -3px 3px !important;
	padding-left: 7px !important;
}
/*============ Change la couleur des différents liens ============*/

/* Changement de couleur du fond principal du site. */
.container-content { /* Coquinou :hap: */
	background: white !important;
}
	
/* Changement de couleur des menus genre ( :hap: ) forums préférés etc... */
.panel-jv-forum {
  	background: rgb(237,237,237) !important;
}
	

	
/* Agrandissement de la barre de recherche */
#header-search-jv input[type="text"] {
	width: 210px !important;
}

/*============ Menu du site ===============*/

/* Change le fond, et agrandissement du menu */
#navbar-jv {
	background: url(http://image.noelshack.com/fichiers/2014/50/1418477754-fonc-jaune.png) left top no-repeat !important;
	background-position: 0px 1px !important;
	width: 100% !important;
	text-align: center !important;
}

/* Désactive la position fixe du menu supérieur */
#header-bottom.affix .bloc-sticker {
	position: relative;
}
	
/* Change le fond du menu */
#navbar-jv .liste-rubrique {
	background: url(http://image.noelshack.com/fichiers/2014/50/1418478656-fonc-jaune2.png) right top no-repeat !important;
}
	
/* Retire la couleur bleu à l'ouverture du lien */
#navbar-jv .liste-rubrique>li.open {
	background: none !important;
}
	
/* Plus de bordure haute-basse */
#navbar-jv .content-menu-jv {
	border-top: none !important;
	border-bottom: none !important;
}

	
/* Changement de la position des rubriques, par cohérence avec le fond du menu */
#navbar-jv .rubrique-menu {
	margin-top: 10px;
}

	
	
/*============ Fin du menu ===============*/

/*============ Icônes des boutons citer, blacklisté, et "ddb" ============*/

/* Bouton Blacklist "Tronche" :hap: */
.picto-msg-tronche {
	background: url("http://image.jeuxvideo.com/pics/messages_prives/ico_black_on.png") 1px 0 no-repeat !important;
}
	
/* Bouton citer */
.picto-msg-quote {
	background: url("http://image.noelshack.com/fichiers/2014/50/1418298773-quote-jvshack.png") 4px 1px no-repeat;
}
	
/* Bouton éditer */
.picto-msg-crayon {
	background: url("http://image.noelshack.com/fichiers/2014/50/1418296609-edit2-jvshack.gif") 3px 0 no-repeat !important;
	padding-left: 16px !important;
}
	
/* Bouton ddb */
.picto-msg-croix, .picto-msg-exclam {
	background: url("http://image.jeuxvideo.com/pics/forums/bt_forum_avertirmod.gif") no-repeat !important;
}
	
.conteneur-messages-pagi {
	border-bottom: none !important;
	margin-bottom: -19px !important;
}

/*============ Footer ===============*/

/* Retire la partie bleu du footer */
#jv-footer .liens-pied {
	border-top: none !important; 
}

/*============ Fin du footer ===============*/

/*============ Fiche de jeu ============*/ 

/* Décale la fiche du jeu */
#forum-right-col > div.panel.panel-jv-fiche {
	left: 977px !important;
	position: absolute !important;
	top: -42px !important;
	width: 211px !important;
	border-radius: 5px !important;
}
	
.panel-jv-fiche .rappel-fiche {
	height: 69px !important; /* :hap: */
	border-radius: 5px !important;
}
	
.rappel-fiche .up-and-down {
	height: 70px !important;
}
	
.fichejeu-menu {
	margin-left: 14px !important;
	float: left !important;
}
	
#forum-right-col>div {
	width: 166px !important;
}

/*============ Fin fiche de jeu ============*/

/*============ Divers ============*/ 

/* Décale la barre de recherche */
.affix #header-search-jv {
	left: 23px !important;
}
	
/* Change la première lettre d'un test */
p.intro-article:first-letter {
	background: rgb(204, 0, 0);
	color: rgb(255, 255, 255);
	font-size: 26px;
	line-height: 28px;
	margin-right: 2px;
	padding: 0 5px;
}
	
.bloc-message-forum .bloc-date-msg {
	line-height: 34px !important;
}
	
.bloc-message-forum .bloc-options-msg span, .bloc-message-forum .bloc-options-msg a {
	margin-left: 5px !important;
}
	
.panel-jv-forum .liste-forums-preferes span.picto-suppression, .panel-jv-forum .liste-sujets-nomiss span.picto-suppression {
	margin-right: 16px !important;
}
	
#header-bottom .bloc-sticker.affix {
	position: relative !important;
}
	
.affix #header-user {
	right: -54px !important;
}

/*============ fin du divers ============*/ 

/*======================== IMAGES ========================*/ 

/* Change les dossiers jaunes des topics */
*[src="/img/forums/topic-dossier1.png"] {
	height: 10px !important;
	width: 16px !important;
	padding-bottom: 13px !important;
	background:url(http://image.noelshack.com/fichiers/2014/50/1418128632-dossierjaune-jvshack.gif) !important;
}
	
/* Change les dossiers rouges des topics */
*[src="/img/forums/topic-dossier2.png"] {
	height: 10px !important;
	width: 16px !important;
	padding-bottom: 13px !important;
	background:url(http://image.noelshack.com/fichiers/2014/50/1418129151-dossierrouge-jvshack.gif) !important;
}
	
/* Change l'épingle verte des topics */
*[src="/img/forums/topic-marque-on.png"] {
	height: 10px !important;
	width: 16px !important;
	padding-bottom: 14px !important;
	background:url(http://image.noelshack.com/fichiers/2014/50/1418129272-marque-on-jvshack.gif) !important;
}
	
/* Change l'épingle rouge des topics */
*[src="/img/forums/topic-marque-off.png"] {
  	height: 10px !important;
	width: 16px !important;
	padding-bottom: 14px !important;
	background:url(http://image.noelshack.com/fichiers/2014/50/1418129274-marque-off-jvshack.gif) !important;
}
	
/* Change le cadenas des topics */
*[src="/img/forums/topic-lock.png"] {
  	height: 10px !important;
	width: 16px !important;
	padding-bottom: 14px !important;
	background:url(http://image.noelshack.com/fichiers/2014/50/1418303551-cadenas-jvshack.gif) !important;
}
	
/*======================== fin des IMAGES ========================*/ 

	
/* Changement de la disposition du message en haut d'une page pour remplacer l'espace pris par la pub qui s'y trouvait avant, en plus petit */
.bloc-accueil {
	margin: 15px 20px 10px 20px;
}
.bloc-fil-ariane-crumb,
.bloc-fil-ariane-crumb-forum {
	background-color: #EBEBEB;
	padding: 10px 20px;
	margin: -5px 0px 20px 0px;
}

}
/*======================== fin du corps principal du site ========================*/

/*======================== PARTIE FORUM ========================*/

@-moz-document url-prefix(http://www.jeuxvideo.com/forums/), 
url-prefix(http://www.jeuxvideo.com/recherche/), 
url-prefix(http://www.forumjv.com/)   {


.titre-bloc {
	background: url(http://image.noelshack.com/fichiers/2014/50/1418510024-titre-forum-orange.png) !important;
  width: 554px !important;
	height: 23px !important;
  color: #252525 !important;
  padding: 2px !important;
}
	
.titre-head-bloc-forum .titre-bloc-forum {
  background: url(http://image.noelshack.com/fichiers/2014/50/1418510024-titre-forum-orange.png) !important;
  width: 554px !important;
	height: 23px !important;
  color: #252525 !important;
  padding: 2px !important;
}
	

/* Les deux fonctions suivantes sont à laisser ensemble, car la 2° n'est là que pour ajuster la 1°. On pourrait écourter en supprimaint la transparence, mais un peu de modulabilité ! */
.panel-jv-forum > .panel-heading  {
	background: url(http://image.noelshack.com/fichiers/2014/50/1418491868-forum-barre-orange.png);
	background-position: right;
}
.panel-jv-forum
{
	margin-right: 16px !important;
	border-radius: 0 35px 0 0;
}
	
#forum-right-col {
	width: 179px !important;
}
	
	
	
.titre-head-bloc-forum .titre-bloc-forum span
	{
		color: white !important;
	}
	
#forum-right-col > div:nth-child(4),
#forum-right-col > div:nth-child(5) {
	position: absolute;
	left: 758px !important;
	top: 62px !important;
	width: 200px !important;
}
	
#forum-right-col > div:nth-child(4),
#forum-right-col > div:nth-child(5) {
	top: 0px !important;
}
	
.panel-jv-forum .panel-heading {
	font-size: 11px !important;
}
	
#forum-main-col {
	float: right !important;
	width: 574px !important;
	margin-right: 207px !important;
	margin-top: -11px;
}
	
.panel-jv-forum .panel-body {
	overflow: inherit !important;
}
	
.fichejeu-jaquette {
	width: 177px !important;
}
	
.panel-jv-forum .liste-forums-preferes, 
.panel-jv-forum .liste-sujets-nomiss, 
.panel-jv-forum .liste-sous-forums {
	width: 200px !important;
}
	
.panel-jv-forum {
	width: 166px !important;
	font-size: 36px !important;
}
	
.panel-jv-forum:nth-child(4) {
	position: absolute !important;
	left: 757px !important;
	top: 0 !important;
	width: 200px !important;
}
	
.btn {
  border: none !important;
}
	
.btn-repondre-msg, .btn-repondre-msg:hover  {
    background: url("http://image.jeuxvideo.com/pics/forums/bt_forum_repondre.gif") !important;
	height: 17px !important;
	width: 80px !important;
}
	
div.bloc-pre-left > a > span, 
div.group-two > a:nth-child(1) > span, 
div.group-two > a:nth-child(1) > span:hover,
div.bloc-pre-left > a > span:hover {
    background: url("http://image.jeuxvideo.com/pics/forums/bt_forum_nouveau_sujet.gif") !important;
	height: 17px !important;
	width: 140px !important;
}
	
div.group-two > a:nth-child(2) > span, div.group-two > a:nth-child(2) > span:hover {
  	background: url("http://image.jeuxvideo.com/pics/forums/bt_forum_liste_sujets.gif") !important;
	height: 17px !important;
	width: 140px !important;
}
	
div.bloc-pre-right > button, div.bloc-pre-right > button {
    background: url("http://image.jeuxvideo.com/pics/forums/bt_forum_rafraichir.gif") !important;
	height: 17px !important;
	width: 80px !important;
}
	
.btn-poster-msg {
    background: url("http://image.jeuxvideo.com/pics/forums/bt_forum_poster.gif") !important;
	height: 17px !important;
	width: 80px !important;
}
	
.btn-topic-resolu {
  	background: url("http://image.noelshack.com/fichiers/2014/50/1418154045-rafraichir-jvshack.gif") !important;
	height: 17px !important;
	width: 80px !important;
}
	
.btn-actu-new-list-forum, 
.btn-repondre-msg, 
.btn-forum-modo, 
.btn-select-msg-modo, 
.btn-sondage-forum, 
.btn-poster-msg,
.btn-topic-resolu,
div.group-two > a:nth-child(2) > span, div.group-two > a:nth-child(2) > span:hover,
div.bloc-pre-left > a > span, 
div.group-two > a:nth-child(1) > span, 
div.group-two > a:nth-child(1) > span:hover,
div.bloc-pre-left > a > span:hover,
div.bloc-pre-right > button, div.bloc-pre-right > button {
	text-transform: none;
	font-weight: normal;
	padding: 0;
	font-size: 0;
	height: 0;
	min-width: 0;
	line-height: 0;
	border-radius: 0;
	text-shadow: none;
}
	
.bloc-message-forum .bloc-pseudo-msg {
	background: url("http://image.jeuxvideo.com/pics/forums/bt_forum_profil.gif") no-repeat right !important;
	padding-right: 15px !important;
}

	
}
/*======================== FIN DES FORUMS ========================*/





/*======================== PAGE D'ACCUEIL ========================*/

/* Pas intégré actuellement */

/*======================== FIN DE PAGE D'ACCUEIL ========================*/
