css:
@-moz-document domain("yammer.com") {

#masthead-container, .yj-selector-right-sidebar, .yj-chat-contact-list, .page-header-container, .invite, .yj-nav-list.yj-account-list, .groups-dir-link, .group-create-link, .yj-message-list-item--body-byline, .yj-nav-user, .badge[style] {
    display: none !important;
}

#column-two, #header-navigation, .one-column-layout {
    width: auto !important;
}

.two-column-layout {
    width: 820px !important;
}

.two-column-layout .column-two-left, #column-two {
    width: 600px !important;
}

.yj-thread-starter .yj-message {
    line-height: 1.5 !important;
    font-size: 16px !important;
}

.yj-message-action-list-item {
	font-weight: 100 !important;
}

#column-one {
    padding-left: 10px !important;
}
    
.yj-message-container { 
    font-family: HelveticaNeue-Light !important;
}

@media (max-width: 550px) {

    * {
        box-sizing: border-box !important;
    }


    .two-column-layout {
        width: 100% !important;
    }

    .two-column-layout .column-two-left, #column-two {
        width: 100% !important;
    }

    .two-column-layout #column-two {
        padding-right: 24px;
    }

    #column-one {
        display: none !important;
    }

    #header-container input.uni-search-input {
        width: 150px;
    }
}

}
