css:
/*分隔线*/
menuseparator {
	-moz-appearance: none!important;
	border-bottom: 1px solid rgba(85,187,236,0.6)!important;
	border-width: 0 0 6px 0;
	padding: 0 !important;
	margin: 0px 0px 2px 27px!important;
	height: 2px !important;
}
/*弹出菜单微透明*/
popup,menupopup,#PanelUI-popup {
	opacity: .9;
}
/*菜单选中区域样式*/
menupopup > menu[_moz-menuactive="true"],
menupopup > menuitem[_moz-menuactive="true"],
popup > menu[_moz-menuactive="true"],
popup > menuitem[_moz-menuactive="true"] {
	-moz-appearance: none;
	background: rgba(255,153,0,0.2)!important;
	border: 1px solid #55BBEC!important;
}
menu,menuitem:not([id="context-back"]),splitmenu,.splitmenu-menuitem {
	-moz-appearance: none!important;
	height: 24px!important;
	border: 1px solid transparent!important;
}
/*选中菜单的字体颜色*/
/*:-moz-any(menuitem,menu):hover > :-moz-any(.menu-text,.menu-iconic-text){color:green!important;}*/
/*颜色微调*/
popup,menupopup:not([id^="BMB"]) {
	-moz-appearance: none!important;
	background: -moz-linear-gradient(left,rgba(242,241,241,1) 26px,rgba(85,187,236,1) 27px,rgba(251,250,250,1) 27px,rgba(251,250,250,1) 28px) transparent!important;
	background-clip: content-box!important;
	-moz-border-top-colors: #55BBEC!important;
	-moz-border-left-colors: #55BBEC!important;
	-moz-border-bottom-colors: #55BBEC!important;
	-moz-border-right-colors: #55BBEC !important;
	border-width: 1px 1px 1px 1px!important;
	padding: 0px!important;
}
/*LASTPASS下拉菜单美化*/
#lpt_menu_search_box {
	border-color: rgb(85,187,236)!important;
}
#lpt_comp-bar-recentlyused,#lpt_comp-bar-recentlyused+menuseparator,#lpt_comp-bar-securenotes,#lpt_lppremium,#lpt_lphelp,#lpt_lpfillformsmenu1 {
	display: none !important;
}
#lpt_lastpass-compact-menu,#lpt_lastpass-compact-menu menupopup {
	max-width: 260px !important;
}
/*tooltip*/
tooltip {
    -moz-appearance: none !important;
    background: white !important;
    opacity: 0.9 !important;
    border: 1px solid #55BBEC !important;
    border-radius: 0px !important;
}
tooltip label {
    color: #FF9900 !important;
    border-radius: 3px !important;
}
