css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {

	body, #page, #watch-header, #watch-header, .yt-card, #action-panel-details, input, .yt-subscription-button-subscriber-count-branded-horizontal, .yt-uix-button, .yt-uix-button:hover, .yt-uix-button-default.yt-uix-button-active, .yt-uix-button-default.yt-uix-button-active:focus, .yt-uix-button-menu, .yt-ui-ellipsis {background-color: #1B1B1B; }
	
	#eow-title, #watch-description, #watch7-user-header .yt-user-info a, input, .yt-uix-button-content, .yt-uix-button-menu .yt-uix-button-menu-item, .channel-header .branded-page-header-title .branded-page-header-title-link, .yt-lockup-title a, .yt-lockup:hover a, .yt-lockup:hover .yt-lockup-meta a, .yt-lockup:hover .yt-lockup-description a { color: #DDDDDD; }
	
#watch7-headline { font-size: 32px; width: 900px;}
	
.branded-page-v2-secondary-col, #guide, #masthead-appbar, #appbar-guide-button, #footer-container, #watch-discussion, #watch7-sidebar-contents, #share-services-container, .cookie-alert, #alerts, #masthead-positioner-height-offset, #masthead-search button.yt-uix-button.yt-uix-button-size-default.yt-uix-button-default.search-btn-component.search-button, .yt-masthead-user-icon, #yt-masthead-signin, #yt-masthead #logo, #masthead-positioner, .yt-uix-button-subscribe-branded, #watch-like:before, #watch-dislike:before, .yt-uix-clickcard-card { display: none !important; }
	
#body-container, #watch7-main { padding-bottom: 0px; margin-bottom: 0px;}
#watch7-content, #watch-description-clip{width: 100%;}
#watch8-action-buttons, .yt-card .yt-uix-tabs, .yt-card .yt-uix-button-expander { border: 0px; }

	video {width: 100% !important; height: 100% !important;}
	.html5-progress-bar { width: 100% !important;}
* {font-family: Segoe UI !important;}
    
}
