@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("neogaf.com") {


/* Eye icon active state */
    
@keyframes eye-animate {
    from   {background-position: 0 center;}
    to     {background-position: -300px center;} /* The width of the sprite. */
}
    

/* Eye icon */
.threadbit.alt1 td:last-child {
    cursor: pointer;
    }
.threadbit.alt1 td:last-child:hover {
    color: transparent !important;
    background: url("data:image/png;base64,") no-repeat 0 center;
    transition: background-position 300ms;
    }
.threadbit.alt1 td:last-child:active {
    animation: eye-animate .2s steps(5) forwards; /* steps(n) = number of frames in the sprite */
    }

/* Row collapse */
.threadbit.alt1 {
    transition: all 1s;
    }
.threadbit.alt1:active {
    visibility: collapse;
    opacity: 0;
    transition: all 300ms 100ms;
    }

}
