css:
@namespace url(http://www.w3.org/1999/xhtml);

/* Using the Radiant Community Script? Alternate
   colour options for force-skip can be applied
   by performing the following;
     Line 22: perform as instructed
     Line 24: perform as instructed
     Line 39: perform as instructed
     Line 41: perform as instructed
     Line 45: perform as instructed
     Line 52: perform as instructed

   Afterward, the skip button will appear orange.
*/

@-moz-document domain("plug.dj") {

/* Background colour */
  /* History; Normal */
#history-button,
  /* Radiant Community force-skip; Normal */
/* Delete LAST two characters to comment */
#rs-skip-button
/* Delete FIRST two characters to comment */
{
  background-color: rgba(0, 0, 0, 0.25) !important;
}
  /* XP bar background */
#footer-user .info .bar,
#the-user-profile .bar,
  /* History; Selected */
#history-button.selected,
  /* Plugcubed */
#plugcubed,
  /* Plugcubed and Radiant: Chat colours */
#p3-settings-custom-colors input,
#rs-dialog-ccc .rs-custom-inputs,
  /* Radiant Community force-skip; Hover */
/* Delete LAST two characters to comment */
#rs-skip-button:hover
/* Delete FIRST two characters to comment */
{
  background-color: rgba(0, 0, 0, 0.50) !important;
} /* Alternate skip button colour */
/* Delete LAST two characters to decomment *//*
#rs-skip-button{
  background-color: rgba(240, 79, 48, 0.25) !Important;
}
#rs-skip-button:hover{
  background-color: rgba(240, 79, 48, 0.50) !Important;
}
*//* Delete FIRST two characters to decomment */
#footer-user .signup{
  background-color: rgba(0, 156, 221, 0.50) !important;
  text-shadow: 0px 0px 6px black,
               0px 0px 6px black,
               0px 0px 6px black !important;
}
/* Footer buttons */
  /* Playlist */
#playlist-button{
  background-image: linear-gradient(rgba(172, 118, 255, 0.25) 0px, rgba(146, 90, 255, 0.25) 100%) !important;
} /* Shop */
.shop-button{
  background-image: linear-gradient(rgba(0, 181, 230, 0.25) 0px, rgba(0, 156, 221, 0.25) 100%) !important;
}
/* Chat colour font */
#p3-settings-custom-colors input,
#rs-dialog-ccc .rs-custom-inputs{
  font-family: monospace !Important;
  color: rgb(238, 238, 238) !important;
}
/* Social media */
  /* Facebook */
#facebook-menu ul{
  background-color: rgba(59, 89, 152, 0.50) !Important;
}   /* Box shadow */
#facebook-menu li:first-child{
  box-shadow: inset 1px 0px 0px 0px rgb(59, 89, 152),
              inset 0px 1px 0px 0px rgb(59, 89, 152),
              inset -1px 0px 0px 0px rgb(59, 89, 152) !important;
}
#facebook-menu li:nth-child(2),
#facebook-menu li:last-child{
  box-shadow: inset 1px 0px 0px 0px rgb(59, 89, 152),
              inset -1px 0px 0px 0px rgb(59, 89, 152) !important;
} /* Twitter */
#twitter-menu ul{
  background-color: rgba(29, 173, 234, 0.50) !Important;
}   /* Box shadow */
#twitter-menu li:first-child{
  box-shadow: inset 1px 0px 0px 0px rgb(29, 173, 234),
              inset 0px 1px 0px 0px rgb(29, 173, 234),
              inset -1px 0px 0px 0px rgb(29, 173, 234) !important;
}
#twitter-menu li:nth-child(2),
#twitter-menu li:last-child{
  box-shadow: inset 1px 0px 0px 0px rgb(29, 173, 234),
              inset -1px 0px 0px 0px rgb(29, 173, 234) !important;
}
/* Disappear */
#facebook-menu li,
#twitter-menu li{
  background-color: transparent !Important;
} /* Plugcubed; Chat colour Field border */
#p3-settings-custom-colors input{
  border-color: transparent !Important;
}

}
