css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slashdot.org")
{
    body
    {
        background-color: #222 !important;
        background-image: url(data:image/png;base64,) !important;
        background-clip: border-box !important;
        background-origin: padding-box !important;
        background-repeat: repeat !important;
        background-size: auto !important;
        background-position: left top !important;
        background-attachment: scroll !important;
        color: #ddd !important;
    }
    
    input
    {color: #000 !important;}
    
    a:hover,
    a:visited
    {color: #fff !important;}
    
    aside h3
    {color: #666 !important;}
    
    #slashboxes article header
    {
        background-color: rgba(17, 17, 17, 0.6) !important;
        border-left: 1px solid #004545 !important;
        border-right: 1px solid #004545 !important;
    }
    
    #slashboxes article
    {
        color: #ddd !important;
        border: 1px solid #004545 !important;
        background-color: rgba(17, 17, 17, 0.6) !important;
    }
    
    a.prevnextbutdis
    {
        border:1px solid #2a2c2f;
        color: #ddd !important;
        background-color: #45484d; background-image: -webkit-gradient(linear, left top, left bottom, from(#45484d), to(#000000));
        background-image: linear-gradient(to bottom, #45484d, #000000);
    }
    
    #firehose article header .details
    {
        background-color: rgba(17, 17, 17, 0.6) !important;
        color: #066 !important;
    }
    
    #firehose article header .details a
    {
        color: #066 !important;
        text-decoration: underline !important;
    }
    
    .thisday-tb tr td
    {background-color: rgba(34, 34, 34, 0.6) !important;}
    
    .thisday-tb tr td.thisday-yr
    {
        background-color: rgba(51, 51, 51, 0.6) !important;
        color: #066 !important;
    }
    
    header.h,
    body > section,
    #jobBoardWidgetSearchForm,
    .thisday-tb,
    #d2header
    {background-color: transparent !important;}

    #slashboxes article a,
    #slashboxes article#thisday a
    {color: #ddd !important;}
    
    #slashboxes article header,
    #slashboxes article header a
    {color: #066 !important;}
    
    #jobBoardWidgetSearchForm input[type="submit"]
    {color: #555 !important;}
    
    #job_title,
    #firehose article,
    #firehose article header .details a:hover
    {color: #fff !important;}
    
    a.prevnextbutact
    {border: none !important;}
    
    nav.left_menu li.active a,
    .daybreak a
    {color: #333 !important;}
    
    .article .body .p > i
    {border-left-color: rgba(100, 100, 100, 0.6) !important;}
    
    section#besttabs
    {background-color: rgba(17, 17, 17, 0.6) !important;}
    
    ul.besttabslist li.active
    {
        background-color: rgba(51, 51, 51, 0.6) !important;
        box-shadow: 0px -1px 2px #666
    }
    
    #d2header .ccw-score-label .sc,
    #d2header .ccw-score-label > div
    {
        background-color: transparent !important;
        color: #fff !important;
    }
    
    #newa2footerv2 .ns h3 a:hover,
    #newa2footerv2 .pr h3 a:hover,
    #newa2footerv2 .ns h3 a:visited,
    #newa2footerv2 .pr h3 a:visited
    {
        color: #066 !important;
        text-decoration: none !important;
    }
}
