css:
@-moz-document domain(se7ensins.com) {
img[src*="logo_bg"]{
    width:0;
    height:100px;
    padding-right:385px;
    background:transparent url(http://i.imgur.com/icYssaR.png) top left no-repeat;
}    
html{
    color: #202020;
    background-color: #BBB;
}
#logoBlock {
    background: none #BBB;
}    
#content .pageContent {
    background-color: rgba(245,245,245, 0.5);    
}
#userBox {
    padding: 6px 9px 3px 6px;
    margin: 6px;
    background-color: rgba(26, 26, 26, 0);
    border: none;
    float: right;
}
#userBox .avatar img {
    width: 0px;
    height: 0px;

}
#userBox .navLink .itemCount {
    font-weight: bold;
    font-size: 9px;
    color: #FFF;
    background-color: #202020;
}    
.nodeList .categoryStrip {
background: #bbb;
}
.el_portal_index .itembit .subHeading {
background: #bbb;
}
.navTabs .navTab.selected {
background: #bbb;
}
.navTabs .navTab.PopupOpen {
background: #bbb;
}
.xenOverlay .formOverlay .heading {
background: #bbb;
}
.discussionList .sectionHeaders {
background: #bbb;
}
.messageHeader {
background: #bbb;
}
.subHeading {
background: #bbb;
}    
.heading, .xenForm .formHeader {
background: #bbb;
}
.navTabs .navTab.PopupOpen .navLink:hover, .navTabs .navTab.PopupClosed .navLink:hover {
background: #bbb;
}
.bbCodeBlock .type {
background: #bbb;
}
.navTabs {
    background-color: #bbb;
    padding-top: 5px;
    border-bottom: 5px solid #202020;
}
.Menu {
    font-size: 11px;
    background-color: #1A1A1A;
    margin-top: 1px;
    border-right: 1px none #F3F3F3;
    border-width: 5px 1px 1px;
    border-style: solid none none;
    border-color: #202020 #F3F3F3 #F3F3F3;
  
}
.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3 {
    font-weight: bold;
    font-size: 12px;
    color: #fff;
    background-color: #bbb;
    padding: 10px;
    margin: -5px -5px 5px;
    border-bottom: 3px solid #202020;
    border-radius: 3px;
}  
.sidebar .section .primaryContent h3 a, .sidebar .section .secondaryContent h3 a {
    font-weight: bold;
    font-size: 12px;
    color: #fff;
}
.primaryContent a {
    color: #202020;
}
.secondaryContent a {
    color: #202020;
}
.button.primary {
    background-color: #bbb;
}
a.callToAction {
    background-color: #bbb;
}    
a.callToAction:hover {
    color: #fff;
    background-color: #202020;
}        
.PageNav a:hover, .PageNav a:focus {
    color: #FFF;
    text-decoration: none;
    background-color: #bbb;
    border-color: #bbb;
}   
.PageNav a.currentPage {
    color: #FFF;
    background-color: #202020;
    border-color: #202020;
    position: relative;
} 
.xenOverlay .formOverlay a.button {
    color: #202020;
    background-color: #bbb;
    border-color: #bbb;
}    
.xenOverlay .formOverlay a.button:hover {
    color: #fff;
    background-color: #202020;
}    
    
.message .messageMeta .control:focus {
    color: #FFF;
    text-decoration: none;
    background-color: #202020;
}    
.message .messageMeta .control:hover {
    color: #FFF;
    text-decoration: none;
    background-color: #202020;  
}    
.itemPageNav a {
    color: #FFF;
    background-color: #bbb;
}    
.itemPageNav a:hover {
    color: #FFF;
    background-color: #202020;
}        
.subHeading {
    font-size: 11px;
    color: #202020;
}    
a:link, a:visited {
    color: #202020;
}
.footer a, .footer a:visited {
    color: #202020;
    padding: 5px;
    display: block;
}    
.footer a:hover, .footer a:visited {
    color: #bbb;    
}
.discussionListItem .iconKey .sticky, .iconKey .sticky {
    color: #202020;
}
.node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon {
    visibility: hidden;
}
.node .forumNodeInfo .nodeIcon, .node .categoryForumNodeInfo .nodeIcon {
    visibility: hidden;
}
.node .tinyIcon {
    font-size: 17px;
    color: #202020;
}
.nodeList .node .nodeLastPost .xeniquePosterAvatar .avatar img {
    background-color: transparent;
    padding: 0px;
    border-style: none;
    width: 0px;
    height: 0px;
}
.avatar .img.s {
    width: 7px;
    height: 7px;
}
.discussionListItem .posterAvatar .avatar img {
    width: 0px;
    height: 0px;
    display: block;
}
.sidebar .avatarList .avatar img {
   visibility: hidden;
}
#moderatorBar {
    font-size: 11px;
    background-color: #bbb;
    padding-top: 5px;
    padding-bottom: 5px;
    border-bottom: 1px solid #bbb;
}
.Menu .blockLinksList li a, .Menu .blockLinksList li label {
    color: #202020;
    border-radius: 0px;
}
.blockLinksList a:hover, .blockLinksList a:focus, .blockLinksList li.kbSelect a, .blockLinksList label:hover, .blockLinksList label:focus, .blockLinksList li.kbSelect label {
    color: #FFF;
    text-decoration: none;
    background-color: #bbb;
}
.navTabs .navTab.PopupClosed .navLink {
    color: #202020;
}    
.signature img {
    max-height: 0px !important;
    max-width: 0% !important;
}
.messageText img, .messageText iframe, .messageText object, .messageText embed {
    opacity: 0.1;
    max-width: 10%;      
}       
.avatar img, .avatar .img, .avatarCropper {
    max-height: 0px !important;
    max-width: 0% !important;
    visibility: hidden;
}    
.messageUserBlock div.avatarHolder .onlineMarker {
    position: absolute;
    top: 0px;
    left: 0px;
    border-width: 7px;
    border-style: solid;
    border-right: 7px solid transparent;
    border-color: #202020 transparent transparent #202020;
}   
.messageUserBlock .extraUserInfo img {
    visibility: hidden;
    max-width: 34px;
}    
.messageUserInfo .som {
    visibility: hidden;
    text-align: center;
    padding-top: 0px;
    padding-bottom: 0px;
}
.bbCodeSpoilerOuterButton {
    color: #202020;
    background: none repeat scroll 0% 0% #bbb;
    padding: 5px 10px;
    border-radius: 3px;
} 
.button {
    color: #000;
    background-color: #EAEAEA;
    border: 1px solid #EAEAEA;
   
}    
.button:hover {
    color: #fff;
    background-color: #202020;
    border: 1px solid #202020;
} 
.wrapped {
    visibility: hidden;
}
.iconFacebook {
    color: #989898 !important;
}    
.iconTwitter {
    color: #b3b3b3 !important;
}    
.iconGoogle {
    color: #acacac !important;
}    
.iconYouTube {
    color: #8c8c8c !important;
}
.iconRSS {
    color: #a7a7a7 !important;
}   
#moderatorBar .socialIcons a, #loginBar .socialIcons a {    
    visibility: hidden;
} 
.profilePage .followBlocks .section .subHeading.textWithCount {
    font-weight: bold;
    font-size: 12px;
    color: #fff !important;;
    background: none repeat scroll 0% 0% #bbb;
    padding: 10px;
    border-style: none none solid;
    border-bottom: 3px solid #202020;
    border-radius: 3px;

} 
.textWithCount.subHeading .text {
    color: #fff;
}
.profilePage .followBlocks .section .subHeading.textWithCount .count {
    color: #fff;
    background-color: #bbb;
    border-color: #fff;
}
}
