css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("8chan.co"), domain("8ch.net") {

body {
    background: none repeat scroll 0 0 #000000;
    color: #00BB00;
    font-family: arial,helvetica,sans-serif;
    font-size: 10pt;
    margin: 0 8px;
    padding-left: 5px;
    padding-right: 5px;
}
a, a:visited {
    color: #81a2be;
    text-decoration: underline;
}
a:hover, p.intro a.post_no:hover {
    color: #ff0000;
}
a.post_no {
    margin: 0;
    padding: 0;
    text-decoration: none;
}
p.intro a.post_no {
    color: inherit;
}
p.intro a.post_no, p.intro a.email, p.intro a.post_anchor {
    margin: 0;
}
p.intro a.email span.name {
    color: #c5c8c6;
}
p.intro a.email:hover span.name {
    color: #ff0000;
}
p.intro label {
    display: inline;
}
p.intro time, p.intro a.ip-link, p.intro a.capcode {
    direction: ltr;
    unicode-bidi: embed;
}
h2 {
    color: #af0a0f;
    font-size: 11pt;
    margin: 0;
    padding: 0;
}
header {
    background-color: #1d1f21;
    margin: 1em 0;
}
h1 {
    font-family: tahoma;
    font-size: 20pt;
    letter-spacing: -2px;
    margin: 0;
}
header div.subtitle, h1 {
    color: #af0a0f;
    text-align: center;
}
header div.subtitle {
    font-size: 8pt;
}
form {
    margin-bottom: 4em;
}
form table {
    margin: auto;
}
form table input {
    height: auto;
}
input[type="text"], input[type="password"], textarea {
    border: 1px solid grey;
    text-indent: 0;
    text-shadow: none;
    text-transform: none;
    word-spacing: normal;
}
form table tr td {
    background-color: 0 0 #000000;
    margin: 0;
    padding: 0;
    text-align: left;
}
form table.mod tr td {
    padding: 2px;
}
form table tr th {
    padding: 4px;
    text-align: left;
}
form table tr th {
    background: none repeat scroll 0 0 #000000;
}
form table tr td div.center {
    float: left;
    padding-left: 3px;
    text-align: center;
}
form table tr td div input {
    display: block;
    margin: 2px auto 0;
}
form table tr td div label {
    font-size: 10px;
}
.unimportant, .unimportant * {
    font-size: 10px;
}
.file {
    float: left;
    margin-right: 2px;
}
.file:not(.multifile) .post-image {
    float: left;
}
.file:not(.multifile) {
    float: none;
}
p.fileinfo {
    display: block;
    margin: 0 0 0 20px;
}
div.post p.fileinfo {
    padding-left: 5px;
}
div.banner {
    background-color: #e04000;
    font-size: 12pt;
    font-weight: bold;
    margin: 1em 0;
    text-align: center;
}
div.banner, div.banner a {
    color: white;
}
div.banner a:hover {
    color: #eef2ff;
    text-decoration: none;
}
img.banner, img.board_image {
    background-color: #1d1f21;
    border: 1px solid #333333;
    display: block;
    margin: 12px auto 0;
}
.post-image {
    border: medium none;
    display: block;
    float: left;
    margin: 5px 20px 10px;
}
div.post .post-image {
    margin: 0 20px 0 0;
    padding: 5px;
}
div.post img.icon {
    display: inline;
    margin: 0 5px;
    padding: 0;
}
div.post i.fa {
    font-size: 16px;
    margin: 0 4px;
}
div.post.op {
    margin-bottom: 5px;
    margin-right: 20px;
}
div.post.op hr {
    border-color: #d9bfb7;
}
p.intro {
    margin: 0.5em 0;
    padding: 0 0 0.2em;
}
input.delete {
    float: left;
    margin: 1px 6px 0 0;
}
p.intro span.subject {
    color: #3b36b6;
    font-weight: bold;
}
p.intro span.name {
    color: #00bb00;
    font-weight: bold;
}
p.intro span.capcode, p.intro a.capcode, p.intro a.nametag {
    color: #f00000;
    margin-left: 0;
}
p.intro a {
    margin-left: 8px;
}
div.delete {
    float: right;
}
div.post.reply p {
    margin: 0.3em 0 0;
}
div.post.reply div.body {
    margin-left: 1.8em;
    margin-top: 0.8em;
    padding-bottom: 0.3em;
    padding-right: 3em;
}
div.post.reply.highlighted {
    background-color: #00ff00;
    border: 1px solid #00ff00;
    margin-left: 1.1em;
    margin-top: 0.1em;
    padding-bottom: 0.6em;

    
}
div.post.reply div.body a {
    color: #00ff00;
}
div.post {
    max-width: 97%;
}
div.post div.body {
  	color: #00ff00;
    white-space: pre-wrap;
    word-wrap: break-word;
}
div.post.reply {
    background: none repeat scroll 0 0 #131313;
    border-color: #1e1e1e;
    border-style: none solid solid none;
    border-width: 2px;
    display: inline-block;
    margin: 0.2em 16px;
    padding: 0.2em 0.3em 0.5em 0.6em;
}
span.trip {
    color: #228854;
}
span.quote {
    color: #789922;
}
span.omitted {
    display: block;
    margin-top: 1em;
}
br.clear {
    clear: left;
    display: block;
}
span.controls {
    float: right;
    font-size: 80%;
    margin: 0;
    padding: 0;
}
span.controls.op {
    float: none;
    margin-left: 10px;
}
span.controls a {
    margin: 0;
}
div#wrap {
    margin: 0 auto;
    width: 900px;
}
div.ban {
    background: none repeat scroll 0 0 white;
    border: 1px solid #9988ee;
    margin: 30px auto;
    max-width: 700px;
}
div.ban p, div.ban h2 {
    padding: 3px 7px;
}
div.ban h2 {
    background: none repeat scroll 0 0 #9988ee;
    color: black;
    font-size: 12pt;
}
div.ban p {
    font-size: 12px;
    margin-bottom: 12px;
}
div.ban p.reason {
    font-weight: bold;
}
span.heading {
    color: #af0a0f;
    font-size: 11pt;
    font-weight: bold;
}
span.spoiler {
    background: none repeat scroll 0 0 black;
    color: black;
    padding: 0 1px;
}
div.post.reply div.body span.spoiler a {
    color: black;
}
span.spoiler:hover, div.post.reply div.body span.spoiler:hover a {
    color: white;
}
div.styles {
    float: right;
    padding-bottom: 20px;
}
div.styles a {
    margin: 0 10px;
}
div.styles a.selected {
    text-decoration: none;
}
table.test {
    width: 100%;
}
table.test td, table.test th {
    padding: 5px;
    text-align: left;
}
table.test tr.h th {
    background: none repeat scroll 0 0 #9988ee;
}
table.test td img {
    margin: 0;
}
fieldset label {
    display: block;
}
div.pages {
    background: none repeat scroll 0 0 #282a2e;
    border-bottom: 2px solid #1a1a1a;
    border-right: 2px solid #1a1a1a;
    color: #8899aa;
    display: inline;
    padding: 8px;
}
div.pages.top {
    display: block;
    margin-bottom: 5px;
    opacity: 0.9;
    padding: 5px 8px;
    position: fixed;
    right: 0;
    top: 0;
}
@media screen and (max-width: 800px) {
div.pages.top {
    display: none !important;
}
}
div.pages a.selected {
    color: #81a2be;
    font-weight: bolder;
}
div.pages a {
    text-decoration: none;
}
div.pages form {
    display: inline;
    margin: 0;
    padding: 0;
}
div.pages form input {
    display: inline;
    margin: 0 5px;
}
hr {
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    -moz-border-right-colors: none;
    -moz-border-top-colors: none;
    border-color: #333333 -moz-use-text-color -moz-use-text-color;
    border-image: none;
    border-style: solid none none;
    border-width: 1px medium medium;
    clear: left;
    height: 0;
}
div.boardlist {
    background-color: #1d1f21;
    color: white;
    font-size: 9pt;
    margin-top: 3px;
}
div.boardlist.bottom {
    margin-top: 20px;
}
div.boardlist a {
    text-decoration: none;
}
div.report {
    color: #333333;
}
table.modlog {
    margin: auto;
    width: 100%;
}
table.modlog tr td {
    margin: 0;
    padding: 4px 15px 0 0;
    text-align: left;
}
table.modlog tr th {
    padding: 4px 15px 5px 5px;
    text-align: left;
    white-space: nowrap;
}
table.modlog tr th {
    background-color: #9988ee;
}
td.minimal, th.minimal {
    white-space: nowrap;
    width: 1%;
}
div.top_notice {
    margin: 5px auto;
    text-align: center;
}
span.public_ban {
    color: red;
    display: block;
    font-weight: bold;
    margin-top: 15px;
}
span.toolong {
    display: block;
    margin-top: 15px;
}
div.blotter {
    color: red;
    font-weight: bold;
    text-align: center;
}
table.mod.config-editor {
    font-size: 9pt;
    width: 100%;
}
table.mod.config-editor td {
    border-bottom: 1px solid #9988ee;
    padding: 5px;
    text-align: left;
}
table.mod.config-editor input[type="text"] {
    width: 98%;
}
.desktop-style div.boardlist:nth-child(1) {
    left: 0;
    margin-top: 0;
    position: fixed;
    right: 0;
    top: 0;
    z-index: 30;
}
.desktop-style div.boardlist:nth-child(1):hover, .desktop-style div.boardlist.cb-menu:nth-child(1) {
    background-color: rgba(230, 230, 230, 0.6);
}
.desktop-style body {
    padding-top: 20px;
}
.desktop-style .sub {
    background: inherit;
}
.desktop-style .sub .sub {
    background: url("http://8chan.co/img/arrow.png") no-repeat scroll right center transparent;
    display: inline-block;
    text-indent: -9000px;
    width: 7px;
}
.desktop-style .sub .sub:hover, .desktop-style .sub .sub.hover {
    background: inherit;
    display: inline;
    text-indent: 0;
}
#attention_bar {
    height: 1.5em;
    max-height: 1.5em;
    max-width: 100%;
    overflow: hidden;
    text-align: center;
    width: 100%;
}
#attention_bar_form {
    display: none;
    margin: 0;
    padding: 0;
}
#attention_bar_input {
    margin: 0;
    padding: 0;
    text-align: center;
    width: 100%;
}
#attention_bar:hover {
    background-color: rgba(255, 255, 255, 0.2);
}
p.intro.thread-hidden {
    margin: 0;
    padding: 0;
}
form.ban-appeal {
    margin: 9px 20px;
}
form.ban-appeal textarea {
    display: block;
}
.MathJax_Display {
    display: inline !important;
}
pre {
    margin: 0;
}
.theme-catalog div.thread img {
    border: 2px solid rgba(153, 153, 153, 0);
    box-shadow: none;
    float: none !important;
    margin: auto auto 12px;
    max-height: 150px;
    max-width: 200px;
}
.theme-catalog div.thread {
    background: none repeat scroll 0 0 rgba(182, 182, 182, 0.12);
    border: 2px solid rgba(111, 111, 111, 0.34);
    display: inline-block;
    font-size: 11px;
    font-weight: normal;
    margin-bottom: 25px;
    margin-left: 20px;
    margin-right: 15px;
    max-height: 300px;
    overflow: hidden;
    padding: 15px;
    position: relative;
    text-align: center;
    vertical-align: top;
    width: 205px;
}
.theme-catalog div.thread strong {
    display: block;
}
.theme-catalog div.threads {
    margin-left: -20px;
    text-align: center;
}
.theme-catalog div.grid-size-small {
    max-height: 350px;
    max-width: 200px;
    width: 200px;
}
.theme-catalog div.grid-size-large {
    max-height: 450px;
    max-width: 300px;
    width: 300px;
}
.theme-catalog img.thread-image {
    height: auto;
    max-width: 100%;
}
@media (max-width: 420px) {
.theme-catalog ul#Grid {
    padding-left: 18px;
}
.theme-catalog div.thread {
    margin-left: 0;
    margin-right: 0;
    width: auto;
}
.theme-catalog div.threads {
    overflow: hidden;
}
}
.compact-boardlist {
    padding: 3px 3px 0;
}
.compact-boardlist .cb-item {
    display: inline-block;
    vertical-align: middle;
}
.compact-boardlist .cb-icon {
    padding-bottom: 1px;
}
.compact-boardlist .cb-fa {
    font-size: 21px;
    padding: 0 2px 2px;
}
.compact-boardlist .cb-cat {
    padding: 5px 6px 8px;
}
.cb-menuitem {
    display: table-row;
}
.cb-menuitem span {
    border-top: 1px solid rgba(0, 0, 0, 0.5);
    display: table-cell;
    padding: 5px;
    text-align: left;
}
.cb-menuitem span.cb-uri {
    text-align: right;
}
.boardlist:not(.compact-boardlist) #watch-pinned:before {
    content: " [ ";
}
.boardlist:not(.compact-boardlist) #watch-pinned:after {
    content: " ] ";
}
.boardlist:not(.compact-boardlist) #watch-pinned {
    display: inline;
}
.boardlist:not(.compact-boardlist) #watch-pinned a {
    margin-left: 3pt;
}
.boardlist:not(.compact-boardlist) #watch-pinned a:first-child {
    margin-left: 0;
}
.compact-boardlist #watch-pinned {
    display: inline-block;
    vertical-align: middle;
}
div.post video.post-image {
    margin: 5px 25px 5px 5px;
    padding: 0;
}
.new-posts {
    margin-top: 1em;
    opacity: 0.6;
}
.new-threads {
    text-align: center;
}
.why {
    background-color: #282a2e;
    border: 1px solid black;
    border-radius: 20px;
    clear: both;
    float: right;
    list-style-position: inside;
    padding: 10px;
    text-align: left;
    width: 25%;
}
a.button {
    background: none repeat scroll 0 0 #282a2e;
    border: 1px solid black;
    border-radius: 20px;
    display: inline-block;
    font-size: 2.75em;
    margin-right: 5px;
    margin-top: 5px;
    padding: 20px;
    text-decoration: none;
}
div.ban h2 {
    background: none repeat scroll 0 0 #383838;
    color: white;
}
div.ban {
    background: none repeat scroll 0 0 #5c5c5c;
    border: 0 solid grey;
}
.pln {
    color: #ffffff;
}
    
/* Dice roll background */    
.diceroll tr td {
    background-color: transparent;
    
}
    
/* Thread Subject */
p.intro span.subject {
    color: #b40000;
    font-weight: 700;

}
    
/*Top Navigation   */
    
.desktop-style div.boardlist:nth-child(1) {
    background-color: #1d1f21;
    border-bottom: 1px solid transparent;
}
    
/*Top Navigation Hover   */
.desktop-style div.boardlist:nth-child(1):hover, .desktop-style div.boardlist.cb-menu:nth-child(1) {
    background-color: #383c3e;
}

/*Content Menu */
.content_menu {
    background-color: #1d1f21;
    border: 1px solid #000;
    list-style-position: inside;
    padding: 15px 30px;
    text-align: left;
}

/* Table row */
table.modlog tr th {
    background-color: #282a2e;
}
}
