css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("love.mail.ru") {
  #logo, a.vip_icon, .bill,
  .b-invite, .reinformer__header, .reinformer__footer,
  #Gifts, .ContactBanner{display: none !important;}
  
  body,.boxWrap, #Anketa, .UserList {
    color: hsl(205, 10%, 70%) !important;
    background: hsl(205, 20%, 30%) !important;
    border: hsl(205, 50%, 30%) !important;
    font-family: Verdana !important;
  }
  ul.SearchResult li span{color: hsl(205, 10%, 70%) !important;}
  a{color: hsl(205, 70%, 50%) !important;}
  a:hover{color: hsl(205, 70%, 100%) !important;}
  /*   кнопки */
  .btn-group-item .inset, .button, .nav *,
  .Switch *, .m-contact *{
    background: hsl(205, 70%, 40%) !important;
    border-color: hsl(205, 50%, 30%) !important;
    border-radius: 5px;
    color: hsl(205, 20%, 20%) !important;
    font-weight: bold;
  }
/*   плитки */
  .messages_folder .DefaultFolders li,
  .messages_folder .CreatedFolders li,
  .b-anketa, .tooltip-list, .b-search-navigator,
  .search_form_action_in, .search_form_in,
  #top_inviter, .EnterFrom li,
  label.contact-label, label.c-select,
  .messenger-form, .reinformer__indent, 
  .b-informer, .formGray, ul.selector-vertical,
  .block-rounded-corners{
    background: hsl(205, 60%, 20%) !important;
    border: hsl(205, 50%, 30%) solid 1px !important;
    border-radius: 5px;
    text-decoration: none !important;
    color: hsl(205, 10%, 70%) !important;
  }
/* selected   */
  .ui-userbar, .ui-menu-cont,
  .b-photoline__container,  #xBottomPager,
  .selected-folder .bg-folder, .selected-link *,
    li:hover, li.selected
    {
    background: none !important;
    background-image: linear-gradient(to bottom,
                        hsl(205, 40%, 30%) 0px, 
                        hsl(205, 70%, 35%) 50%, 
                        hsl(205, 40%, 30%) 100%) !important;
    border-color: hsl(205, 50%, 30%) !important;
    border-radius: 5px;
    font-size: 110%;
  }
/*   мордолента */
  .b-photoline__container *{
    border-radius: 5px;
    background: none !important;
    padding : 0px !important;
  }
/*   чат */
  .my-msg, .my-msg *{
    color: hsl(101, 70%, 60%) !important;
    background: hsl(205, 30%, 20%) !important;
  }
  .u-msg, .u-msg *{
    color: hsl(221, 70%, 70%) !important;
    background: hsl(205, 50%, 20%) !important;
  }
  .simple-msg{margin-bottom: 2px !important; padding: 1px !important}
  .mb-msg-name{font-size: 1% !important;line-height: 1px !important }
  
  .MainBlockRight{float: left !important;}
  .MainBlockLeft{float: right !important;}
  .f-name{border: none !important;}
}
