css:
@-moz-document url-prefix("http://pixel-magazine.com/forum/"), url-prefix("http://forum.pixel-magazine.com/") {
html,body {
background-color:#030303 !important;
color:#666 !important;
}

li.row {
border-bottom:1px solid #000 !important;
border-left:1px solid #000 !important;
border-right:1px solid #000 !important;
}

ul.topiclist li {
color:#666 !important;
}

ul.topiclist dt > span {
padding-left:0 !important;
font-style:normal !important;
}

li.row:hover {
background-color:#000 !important;
}

a.forumtitle:hover {
color:#fff !important;
}

.bg2 {
background-color:#161616 !important;
}

.pagination span a,.pagination span a:link,.pagination span a:visited {
background-color:#111 !important;
border-color:#333 !important;
}

.pagination span a,.pagination span a:link,.pagination span a:visited,.pagination span a:active {
padding:1px 6px !important;
}

.post {
border:0 !important;
}

.postbody .content {
border-top:solid 1px #333 !important;
}

.postprofile {
border-left-color:#333 !important;
}

.signature {
border-top-color:#333 !important;
}

.panel {
background-color:#111 !important;
border:solid 1px #000 !important;
padding:12px !important;
}

.sidebar-block-content {
background:#111 !important;
color:#666 !important;
border:0 !important;
}

.dark-header-content {
background-color:#111 !important;
border:0 !important;
}

textarea,.inputbox {
background-color:#111 !important;
border:1px solid #333 !important;
color:#ccc !important;
}

.search-box-inner {
background-color:#222 !important;
border:0 !important;
}

.search-box-inner .inputbox {
background-color:#222 !important;
border:0 !important;
}

ul.forums,.bg1,.row .pagination span a,li.pagination span a {
background-color:#111 !important;
}

a.forumtitle,.postprofile strong {
color:#ccc !important;
}

.content,.postbody {
color:#999 !important;
}

.post:target h3 a {
color: #fff !important;
}

blockquote {
background-color: #222 !important;
border-color: #000 !important;
}
blockquote blockquote {
background-color: #111 !important;
}

.post:target .content {
color: #fff !important;
}

#header li {
border-left: solid 12px #030303 !important;
}

#container2 {
background-color: #030303 !important;
}

.login-popup {
background: #080808 !important;
box-shadow: 0px 0px 20px #000 !important;
-moz-box-shadow: 0px 0px 20px #000 !important;
-webkit-box-shadow: 0px 0px 20px #000 !important;
}

.navbar_username_outer.active, .navbar_password_outer.active {
background-color: #0D1A22 !important;
}

.quick-login {
border-bottom: solid 1px #000 !important;
}

img.close-button, input[type="checkbox"], select, img.mChatImageLoad, a.top {
	-webkit-filter: invert(100%);
	filter: invert(100%);
}

.navbar {
border-top: solid 1px #0D1A22;
border-bottom: solid 1px #0D1A22;
}

div.postbody.mChatBodyFix {
	background-color: #090909 !important;
	border-bottom: 1px solid #000 !important;
	border-left: 1px solid #000 !important;
	border-right: 1px solid #000 !important;
}

div.mChatBG1 {
background-color: #000 !important;
border-bottom: 1px dotted #111 !important;
}

div.mChatBG2 {
background-color: #0D0D0D !important;
border-bottom: 1px dotted #111 !important;
}

div.mChatBG1:hover, div.mChatBG2:hover {
background-color: #111 !important;
}

li.row strong {
color: #99BDD3 !important;
}

#simple-wrap {
background-color: #080808 !important;
border: solid 1px #0D1A22 !important;
}

#simple-wrap h2 {
	border-bottom: solid 1px #43A6DF !important;
}

h2 {
color: #D3D3D3 !important;
}

.cp-mini {
background-color: #111 !important;
color: #888 !important;
}

hr {
border-color: #0D1A22 !important;
}

ul.cplist {
border-top-color: #0D1A22 !important;
}

#cp-main .panel li.row {
border-color: #0D1A22 !important;
}

#cp-main .pm {
	background-color: #111 !important;
}

h3, p {
	color: #D3D3D3;
}

.prefooter-blocks h2 {
	color: #fff !important;
}

dl.details dt {
color: #999 !important;
}

dl.details dd {
color: #A2BECF !important;
}

#page-body span.forum-name {
	padding-left: 14px !important;
}

div.content {
	font-size: /*[[f-size]]*/em;
}

dl.codebox {
	background-color: #111 !important;
	border-color: #000 !important;
}

dl.codebox dt {
border-bottom-color: #0D1A22 !important;
}

/*[[logo]]*/
/*[[logo_black]]*/
/*[[logo_white]]*/
}
