css:
@-moz-document domain('hootsuite.com'), url-prefix('https://hootsuite.com/dashboard') {

#header
{
    background-color:#555;
}    
#dashboardTabs
{
    background-color:#3a3a3a;
}    
#dashboardTabs .tab
{
    border-right:1px solid #444;

}    
#dashboardTabs .tab:hover
{
    background-color:#333;
    color:#eee;
} 
                                 
#dashboardTabs .tab.active
{
    background-color:#444;
} 
#dashboardTabs .tab.active a.text
{
    color:#eee;
}
#streamTabInfo
{
    background-color:#444;
}
#streamsContainer
{
    background-color:#444;
}

.stream .header
{
    background-color:#555;
    border-bottom:1px solid #444;
}
.stream .header .streamName
{
    color:#fff;
}
.rc-MessageContext {
    background-color: #555;    
    color:#fff;
    margin-bottom: 0px;
    border-bottom: 1px solid #444;
}
.rc-Message {
    padding: 0px 12px 0px;    
}
.streamsContainer.x-compact .rc-MessageContext {
    font-size: 11px;
}
.streamsContainer.x-compact .rc-Message .-messageDetails {
    margin-bottom: 0px;    
}
.streamsContainer.x-compact .rc-Message .-body {
    margin-bottom: 0px;
}
.streamsContainer.x-compact .rc-Message .-body .-text {
    margin-top: 0px;
    margin-bottom: 0px;
}
a, .link {
    color: #22AAFF;    
}
.rc-Author a {
    color: #22AAFF;    
}
.streamsContainer.x-compact .rc-MessageContext .rc-Actions {
    margin-top: -10px;
    margin-bottom: -10px;
}
.rc-NotificationWidget.x-fill.x-info {
    background: #2AF none repeat scroll 0% 0%;
}
.rc-NotificationWidget {
    padding: 0px;
}
.rc-NotificationWidget > .-message {
    font-size: 12px;
}
}
