@-moz-document domain("feedly.com") {
div#mainBar {
    width: 98% !important;
}
div#mainBar {
    width: 98% !important;
}
div#sideArea {
    display: none !important;
}
div#feedlyPart {
    width: 96% !important;
    margin-top: -20px !important;
}
div#feedlyPage,
div#mainArea {
    width: 100% !important;
}
div#mainBar>div>div#feedlyPart0 {
    padding-left: 15px;
}
/*Custom font for content*/

#timeline,
.entryBody,
.entryBody h1,
.entryBody h2,
.entryBody h3,
.entryBody h4,
.entryBody h5,
#timeline .entryHeader>a {
    font-family: sans-serif;
}
.metadata,
.metadata a,
.websiteCallForAction {
    font-family: sans-serif;
    font-size: 13px;
}
/*End custom font*/
/* Fixed fat gray background*/

#timeline .u100Frame {
    padding: 15px !important;
}
.selectedEntry {
    background-color: white !important;
    box-shadow: 2px 2px 4px #888888;
}
/* End Fixed */
/* More thight main content*/

#feedlyPart0 {
    padding-left: 0px !important;
}
/* End main content*/
/*Reverted changes because it caused a problem later on ?!?*/
/*.feedUnreadCount, .categoryUnreadCount  {padding-top: 5px}*/
