#feedlyTabs>div {
    margin-bottom: 0px !important
}
/* #feedlyTabs>div:last-child{margin-bottom:80px !important} */

#feedlyTabs>div:first-child {
    height: 18px !important
}
#feedlyTabs {
    padding-right: 20px;
}
#feedlyTabsHolder {
    max-width: 248px
}
#fixedProfile {
    max-width: 229px
}
#feedlyTabs>div:first-child {
    margin-top: 5px !important;
}
/*Upgrade pro button little less intrusive*/
/* #fixedProfile { visibility: hidden !important; } */

.content iframe[title='YouTube video player'] {
    min-height: 328px;
}
.content img {
    margin-left: 0px !important
}
#feedlyFrame {
    overflow-x: hidden;
}
.u100Entry {
    width: 100% !important;
}
.u100Entry.selectedEntry {
    /*    width: none !important; */
    
    width: 100% !important;
}
.u100frame {
    padding: 0 0 0 0 !important;
}
.u100frame table {
    padding: 10px !important;
}
.u100Entry iframe {
    margin-left: 0 !important;
}
.area {
    margin-left: 0 !important
}
.u100Entry .title {
    margin-bottom: 5px !important
}
/*.topWikiWidget.wikiWidget { display: none !important}  Version v10 Fix*/

.entryList .u4Entry,
#mainArea .u10Entry {
    max-width: none !important
}
/*#floatingBar{ width: 73% !important}*/

#feedlyTabsHolder {
    height: 100% !important;
}
#feedlyTabs {
    height: 100% !important;
}
/*div#feedlyTabs { width:400px !important; }
div#feedlyTabsHolder { width:400px !important; }*/

div#mainArea {
    width: 100% !important;
}
/*BODY.home { background: #FFFFFF; !important; }*/

.entryHeader a.title {
    max-width: none !important;
}
.entryBody {
    max-width: none !important;
    margin-top: 0px !important;
}
.content>p:first-child a:first-child,
.content>p:first-child {
    display: inline-block;
}
.content {
    width: 100% !important;
}
.entryBody,
.entryBody>* {
    float: left;
}
.entryBody:after,
.u100Entry:after {
    content: ".";
    display: block;
    height: 0;
    clear: both;
    visibility: hidden;
}
/*.websiteCallForAction{
    display: none !important;
}*/

.websiteCallForAction {
    margin: 5px 0px 5px 0px !important;
    height: 33px !important;
}
/*Logically this breaks line breaks, why did I use it in the first place?*/
/*.entryBody>.content>br{
    display:none !important;
}*/

#navSelectorHolder {
    height: 30px !important;
}
#navSelectorHolder div {
    padding-top: 0 !important;
    padding-bottom: 4px !important;
}
.tab .list>div:last-child {
    height: 4px !important;
    border-bottom: 1px solid rgba(0, 0, 0, 0.1);
}
#recommentationAreaHeadlines>div {
    width: 28% !important;
}
#recommentationAreaHeadlines.topRecommendedEntry,
#recommentationAreaHeadlines>div>div {
    width: 100% !important;
}
#recommendationInlineArea .inlineFrame,
#recommentationAreaHeadlines > div:last-child {
    width: 97% !important;
}
.shareHolder .headerInfo {
    margin-top: 0px;
    margin-bottom: 10px;
}
.websiteCallForAction {
    padding-top: 10px;
    max-height: 14px !important;
}
