css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gqueues.com") {

  /* ALL */
  
  
  * {
    font-family: futura !important;
  }

  /*no padding, no margin*/
  #gqQueueContentSubhead *,
  #gqQueueTitle ,
  .gq-queue-header,
   .gq-i-parent-row,
  .gq-i-tags-row,
  .gq-i-files-row,
  .gq-i-notes-row,
  .gq-i-assign-row,
  .gq-i-parent-row *,
  .gq-i-tags-row *,
  .gq-i-files-row *,
  .gq-i-notes-row *,
  .gq-i-assign-row *
  {
    padding:0!important;
    margin:0!important;
  }
  
  .gq-button {
    font-weight: normal!important;
  }
  
  .gq-queue-header,
#gqQueueContentSubhead *,  
    .gq-category-name, 
  .gq-queue-name,
.gq-i-description, .gq-i-description *,  
#gqSearchInput {
  font-size: 10px!important;
}
  
  
  
  /* TASKS SIDE #####################################*/

  
  #gqQueueContentItems {
  position:relative!important;
    top:0px !important;
  }
  
  #gqQueueContentMain {
  }

  #gqQueueContentItems {
    border:none!important;
  }

  #gqItemList {
    padding-bottom:0!important;
  }
 
  
  
  /*# # # # */
  /* Header */
  /*# # # # */
    
  #gqQueueContentHeader {
      padding:0px!important;
          overflow: hidden!important;
    height:25px!important;    
  }

  #gqQueueContentSubhead{
    position:fixed;
    top:0!important;
    height:25px!important;
    right:0px!important;
    background:none;
  }
  
  #gqQueueContentSubhead * {
    line-height: 12px;
    font-size: 12px!important;
    text-shadow: none!important;
    font-weight: normal!important;
    top:5px;
    height:0!important;
    color:black;
    border:none!important;
    text-transform: lowercase!important;
    color:#eee;
  }
  

  
    #gqQueueTitle {
      color:#ddd!important;
      text-transform: uppercase!important;
      font-weight: normal!important;
                text-shadow: 0px 1px 2px #afa !important;
      font-size: 17px!important;
    }

  .gq-queue-header {
    text-transform: uppercase;
    font-weight: normal!important;
    color:#fff;
    line-height: 11px!important;
    margin-top: 20px!important;
  }

  /*# # #*/  
  /* ROW */
  /*# # #*/

  .gq-item-row {
    padding: 0!important;
    margin: 0!important;
  }

    /* hover row */
  tr:hover {
    background:#fafafa;
  }

  .gq-i-parent-row,
  .gq-i-tags-row,
  .gq-i-files-row,
  .gq-i-notes-row,
  .gq-i-assign-row,
  .gq-i-parent-row *,
  .gq-i-tags-row *,
  .gq-i-files-row *,
  .gq-i-notes-row *,
  .gq-i-assign-row * {
    font-size: 10px!important;
    height: auto!important;
    border:none!important;
    background: none!important;
  }

  /* ### tag row ### */
  .gq-i-tags-row,.gq-i-tags-row *{
    background: transparent!important;
  }

  /*# # # # # # */  
  /* ROW ITEMS  */
  /*# # # # # # */

  
  #gqSearchInput,
  #gqAdvSearchToggle,
  .gq-button,
  .gq-i-date-btn,
  .gq-i-check-btn,
  .gq-i-assign-btn,
  .gq-i-notes-btn,
  .gq-i-files-btn,
  .gq-i-tags-btn,
  .gq-i-duplicate-btn,
  .gq-i-zoom-btn,
  .gq-i-delete-btn,
  .gq-i-order,
  .gq-i-sub-ctrls,
  .gq-queue-handle,
/*  .gq-queue-panel-toggle,
  .gq-queue-panel-toggle collapsed,
  .gq-category-toggle,
  .gq-i-tag *,
*/  .gq-i-notes-btn
  {
    border-radius:5px;
    box-shadow: inset 0 0 0px 0px lightblue;
/*    box-shadow: inset 0.2em 0px 5px 2px rgba(0,50,0,0.1);
  */  border:none!important;
    border-bottom: 1px solid !important;
    opacity: 0.15!important;
   width:40px!important;
    margin-left: 0!important;
  }

  
  .gq-i-spacer {
    width: 50px!important;
  }

  .gq-i-order {
    color:transparent!important;
    box-shadow: none!important;
  }  
  
  .gq-i-sub-btn-open *  {
        border: none!important; 
    }

  .gq-i-sub-btn div{
    border:none!important;
  }
  
  .gq-i-sub-btn span{
    border:4px solid orange!important;
    height:0!important;
    width:0!important;
    border-radius: 4px!important;
   background: none!important;
  }

  .gq-i-selector {
    height: 70%!important;
    color: green;
    padding:0px!important;
    width:4px!important;
    margin-top:2px!important;
    margin-left:90px!important;
  }

  .gq-i-check-btn {
    background:red;
    width: 0px!important;
  }
  
  .gq-i-content {
    border:none!important;
    background:none;
  }
  
  .gq-i-content * {
    color:#ccc!important;
  }
  
  .gq-i-description, 
  .gq-i-description:hover, 
  .gq-i-description *,
  .gq-i-notes,.gq-i-notes *{
    color:#666!important;
   font-size: 10px!important;
  }
  
    .gq-i-description{
       font-weight:normal;
    }
  
  
  /**date */
  .gq-i-date-text {
    text-transform: none;
    font-style: oblique!important;
    font-weight: normal!important;
    position: absolute!important;
    font-size: 10px!important;
    left:0px!important;
    color:#aaa!important;
    text-shadow: 0px 1px 4px #eee !important;
  }
  
  .gq-i-date-btn{
    background:lightgreen;
    display: none!important;
  }
  
  .gq-i-assign-btn{
    background:greenyellow;    
  }
  
 .gq-i-notes-btn {
   width:80px!important;
    background: #ff5!important;
 }
  
  .gq-item-container[notesExist] .gq-i-notes-btn {
    opacity: 0.5!important;
    background:#ee8!important;
  }
  
  .gq-i-files-btn{
    background:lightblue;
  }

  .gq-i-tags-btn{
    background:#5f5;
  }
  
  .gq-i-tag *{
    font-weight: normal;
    padding-left: 4px!important;
  }
  
  .gq-i-add-file, .gq-i-add-tag, .gq-a-add-btn {
    color: transparent!important;
  }

  .gq-i-add-file:hover, .gq-i-add-tag:hover, .gq-a-add-btn:hover{
    color: blue!important;
  }
  


  /*  queue tree ############################################################# */

  .gq-control-header{
    padding: 0!important;
    height: 20px!important;
  }
  
  .gq-control-content{
    padding: 0px!important;
  }
  
  .gq-queue-count {
    display: none!important;
  }
  
  [class*="gq-queue-container selected"] .gq-queue-count  {
    display: block!important;    
    font-weight: normal!important;
    font-size: 9px!important;
  }

  [class*="gq-smart-queue-sort-rows ui-sortable"] .gq-queue-count {
    display: none!important;
  }
  
   .gq-queue-panel, .gq-smart-queue-sort-rows{
    margin:0!important; 
    padding:0!important; 
   }

  .gq-queue-panel-toggle, .gq-queue-panel-toggle collapsed{  
    background-image: none!important;
    width:100%!important;
  } 

  
  .gq-queue-row, .gq-queue-row * {
    padding: 0!important;
    height:17px!important;
    line-height: 13px!important;
  }
 
  .gq-queue-handle,
  .gq-category-toggle {
    height:10px!important;
    width:10px!important;
    display: none;
  }
  
  .gq-control-content *, .gq-control-header {
    border:none;
  }
  .gq-queue-panel-header * {
    padding: 0!important;
    margin: 0!important;
    height: 15px!important;
  }
  .gq-queue-panel-title {
    color:transparent;
  }
  
  .gq-category-name {
    font-weight: normal!important;
    color:#ebb;
  }
  
  [class*="gq-queue-container selected"] .gq-queue-name{
    text-decoration: underline!important;
    font-weight: normal!important;
   color:green!important;
  }
  
  
/*** heading bars */
  
  .gq-bar-wrapper {
    background:transparent;    
    height:0;
  }
  #gqTopBar {
    background:none!important;
  }
  #gqTopBar * {
    border:none!important;
  }
    
  #qqContentWrapper {
    position:relative!important;    
  }
  
  .gq-control-header {
    height:3em;
  }
  

/* ### settings and calendar link ### */
  
  #gqCalendarLink  {
    position:fixed;
    font-size: 0!important;
    left:140px!important;
  }
  
  #quser, #quser *,  #gqCalendarLink, #gqAdvSearchToggle,
    #gqSearch, 
  #gqAdvSearchToggle , 
  .gq-compact #gqSearch, 
  #gqSettingsMenu,
  #gqSearchBar, 
  #gqSearchInput, 
  .gq-search-container, 
  .gq-search-container active, 
  #gqSearch {
    z-index:110;
    position:fixed;
    top:0!important;
    width: 20px!important;
    height:20px!important;
    padding: 0!important;
    margin: 0!important;
    top:0!important;
    background: #fff!important;
    border-radius:15px;
   border:none!important;
    border-bottom:1px solid #eee!important;
  } 
  
  
  #gqSettingsMenu,
  #gqAdvSearchToggle,
  #gqCalendarLink  {
    width:20px!important;
  }

  #quser, #gqSettingsMenu{
      left:162px!important;
    }

  #gqCalendarLink {    
    left:141px!important;
  }
  
  #gqAdvSearchToggle {
    left:120px!important;
  }


/* ### search  ### */

  #gqSearchBar, 
  #gqSearchInput, 
  .gq-search-container, 
  .gq-search-container active, 
  .gq-compact #gqSearch, 
  #gqSearch {
  padding-left: 10px!important;
    left:0px!important;
    width:130px!important;
    background: none!important;
  }
  
   
  
  .gq-i-duplicate-btn,
  .gq-queue-assignments,
  #gqNewFeaturesLink, 
  #gqShareLink, 
  #gqFeedbackLink, 
  #gqLogoParent, 
  #gqSettingsMenu span,
  #gqSearchBtn, 
  .gq-i-delete-btn,
  .gq-category-icon,
  #gqQueueDescriptionToggle,
  #gqAccountMenu,
#gqQuickAddBtn,
  #gqControlTopShadow,
  .gq-control-footer,
  .gq-category-toggle,
  .gq-smart-queue-separator{
    display:none!important;
  }
  
}
