css:
/**
 * Error console tabs (Clearlooks)
 * v. 0.0.2pre
 * 2008-10-16
**/

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url(chrome://global/content/console.xul) {
#console-toolbox {
  -moz-box-direction: reverse !important;
}
#ToolbarEval {
  -moz-appearance: menubar !important;
}
#ToolbarEval>* {
  margin-top: -1.7pt !important;
}
#ToolbarMode>toolbarbutton:not([oncommand="clearConsole();"]) {
  -moz-appearance: none !important;
  border-width: 1px !important;
  border-top-width: 5px !important;
  margin-bottom: -2px !important;
  padding-top: 0 !important;
}
#ToolbarMode>toolbarbutton:not([oncommand="clearConsole();"])[checked] {
  -moz-border-top-colors: 
    rgba(10,2,12,0.564)
    rgba(10,2,12,0.33)
    rgba(10,2,12,0.364)
    rgba(10,2,12,0.43)
    rgba(10,2,13,0.48)
   !important;
  -moz-border-right-colors:
    GrayText
   !important;
  -moz-border-bottom-colors:
    rgba(0,0,0,0)
   !important;
  -moz-border-radius-topleft:  3pt !important;
  -moz-border-radius-topright: 3pt !important;
  background-color: 
    rgba(240,240,240,0.9) !important;
}
#ToolbarMode>toolbarbutton:not([oncommand="clearConsole();"]):hover:not([checked]) {
  -moz-border-top-colors: 
    GrayText
    rgba(0,0,0,0)
    rgba(0,0,0,0)
    rgba(0,0,0,0)
    rgba(0,0,0,0)
   !important;
  -moz-border-bottom-colors:
    GrayText
   !important;
  -moz-border-left-colors:
    GrayText
   !important;
  -moz-border-right-colors:
    GrayText
   !important;
  background-color: 
    rgba(226,220,226,0.9) !important;
  -moz-border-radius:  3.5pt !important;
}
#ToolbarMode>toolbarbutton:not([oncommand="clearConsole();"]):hover {
  /*-moz-border-top-colors: 
    rgba(10,2,12,0.61)
    rgba(10,2,12,0.4)
    rgba(10,2,12,0.44)
    rgba(10,2,12,0.5)
    rgba(10,2,13,0.6)
   !important;*/
}
}
