css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("murobbs.muropaketti.com"), domain("murobbs.plaza.fi") {

/************************************************/
/*       XF Dark theme for MuroBBS (2015)       */
/*      for Firefox & Chrome with Stylish       */
/* -------------------------------------------  */
/*     created by Lifeless - rmj@3rdw.net       */
/* -------------------------------------------  */
/*        version: 1.07 - 2014-12-17            */
/* -------------------------------------------  */
/*            No Lilies Were Harmed             */
/*       During the Making of This Style        */
/* -------------------------------------------  */
/*                nos univers                   */
/*              A Contre-Courant                */
/*              tout Ã  l'envers                 */
/*             pas pour longtemps               */
/************************************************/
  
* {
  color:#ccc!important; /*DARK*/
}  
  
  
/* SET BACKGROUND COLOR */  
html {
  background:#333!important; /*DARK*/
}
body {
  background:transparent!important;
}
.pageWidth {
  margin:0!important;
}
#content .pageContent {
  border:none!important;
  background:transparent!important;
}  
/* SET BACKGROUND COLOR ENDS */  


  
/* REMOVE SCROLLBAR */  
.off-canvas-wrapper {
  overflow-x:inherit!important;
  overflow-y:inherit!important;
}  

  
  
/* REMOVE SIDE BAR */
.uix_mainSidebar {
  display:none!important;
}
.mainContent {
  margin-right:0px!important;
}
/* REMOVE SIDE BAR ENDS */

  
  
/* LOGO */
#header, #logoBlock {
  margin-top:0px!important;
  position:relative;
  top:-10px!important;
}
IMG[src="http://otavafiles.fi/plaza/uploads/images/murobbs_logo.png"] {
  opacity:0;
  width:240px!important;
  padding-left:20px!important;
}
#logo {
  background:url('http://www.3rdw.net/themes/MuroBBS/xfv1/logo2.png') 25% 50% no-repeat;
}
/* LOGO ENDS */

  
  
  
  
#content.forum_view, #content.thread_view {
  position:absolute;
  top:73px;
  background:transparent!important;
}  

#Notices {
  width:100px!important;
  height:27px;
  position:absolute;
  top:-80px;
  left:50%!important;
  opacity:0.8;
  overflow:hidden;
  border-radius:0 0 5px 5px;
/*  display:none!important; */
}
#Notices:hover {
  width:400px!important;
  height:100%;
}
.baseHtml.noticeContent {
  font-size:8pt!important;
}
.baseHtml.noticeContent:before {
  content:'ilmoitukset: \013';
}
  
  
  
/***********************/  
/*     MAIN MENU       */
/***********************/  
/*-- home link --*/  
.navTabs [href*="http://murobbs"] {
   display:none;
}
/*-- main links --*/  
.navTabs {
  width:100%!important;
  position:absolute;
  top:-32px;
  left:-15px;
  font-size: 10pt;
  background:transparent;
  border:none;
  z-index:5000;
}  
.navTabs * {
  border:none!important;
  margin:0!important;
  padding:0!important;
}
.navTab {
  margin:5px!important;
}
.navTab:hover {
  background:rgba(255,255,255,0.5)!important;
  margin:0px!important;
  padding:5px!important;
}
.navTabs .navLink {
  height:10px!important;
  line-height:10px!important;
  padding-left:5px!important;
  padding-right:5px!important;
}
.navTabs .navLink:not([href*="http://murobbs"]) {
  border-left:0px solid #fff!important;
}
/*-- user name --*/  
.visitorTabs {
  position:absolute;
  top:-10px;
  right:0px;
  background:rgba(255,255,255,0.3)!important;
  height:20px;
  line-height:30px!important;
  border-radius:3px 3px;
  padding:3px!important;
}
.visitorTabs li.PopupContainerControl a {
  line-height:30px!important;
}
/*-- main menu murobbs link --*/  
.navLink.NoPopupGadget[rel="Menu"][href="#"] {
  display: none!important;
  content: "\f015" !important;  
}
/*-- quick links --*/  
.navTab.selected .tabLinks {
  width:100%!important;
  position:absolute!important;
  top:145px!important;
  z-index:1000!important;
  border:0px solid #ccc!important;
  background:transparent!important;
}
.tabLinks.forumsTabLinks li {
  float:right!important;
  margin-left:6px!important;
  padding-left:6px!important;
}
.tabLinks.forumsTabLinks LI:first-child {
  border-left:none!important;
}
.tabLinks.forumsTabLinks li * {
  font-size:7pt!important;
}
.tabLinks {
  height:27px!important;
}
.tabLinks * {
  height:27px!important;
  line-height:27px!important;
  font-weight:normal!important;
}
/***********************/  
/*   MAIN MENU ENDS    */
/***********************/  
  
  
/* FORUM TITLE */
.titleBar {
  position:absolute!important;
  top:73px;
  padding-left:1em!important;
}
.titleBar * {
  font-size:10pt!important;
}
#pageDescription {
  font-size:7pt!important;
  /* display:none; */
  width:100%!important;
  height:16px!important;
  overflow:hidden;
}
/* FORUM TITLE ENDS */
  
  
  
/* PAGE NAV */
.PageNav {
  margin-top:2em!important;
  position:relative;
}
/* PAGE NAV ENDS */

  

/* THREAD VIEW */
.listBlock.ikoni {
  background:#fff!important;
  border:none!important;
}
.discussionList.section.sectionMain {
  border:none!important;
}
.discussionList.section.sectionMain li {
  border-bottom:1px solid #ddd!important;
} 
.listBlock.stats.pairsJustified {
  border:none!important;
  background:#f6f6f6!important;
}
.titleText {
  padding-left:3px!important;
}
.titleText .secondRow {
  font-size:8pt;
  padding-left:0px;
}  
.sectionHeaders dd.stats {
}
.discussionListItem .stats {
  width:140px!important;
}
.discussionListItem .stats * {
  color:#000;
  font-size:8pt;
}
.discussionListItem .stats .minor dt {
  width:40px!important;
  height:1.5em;
  overflow:hidden;
}
.discussionListItem .stats .minor dt:before {
  content:'Luettu: ';
}
/* THREAD VIEW ENDS */
  
  
  
/* MESSAGE VIEW */  
li .messageMeta.otsikko {
  background:#333!important;
  border:none!important;
  height: 40px!important;
  padding:0px!important;
}
li .messageMeta.otsikko * {
  padding:0px!important;
  margin:0px!important;
  border:none!important;
}
li .messageMeta.otsikko .postNumber {
  padding:5px!important;
  position:relative;
  z-index:3000;
}
li .messageMeta.otsikko .DateTime {
  padding-top:5px!important;
  padding-left:5px!important;
  display:block;
  width:70px!important;
  height:40px!important;
  border:none!important;
  background:#333;
  position:relative;
  top:0px;
  z-index:2000;
}
.messageUserInfo {
  position:relative;
  top:-40px;
  left:0px;
  height:40px!important;
  width:100%!important;
}
.quickReply  {
  background:transparent!important;
  margin-bottom:1em!important;
}
.quickReply #QuickReply {
  margin-left:0px!important;
  padding:10px!important;
}
.quickReply .messageUserInfo {
  display:none!important;
}
.quickReply .messageUserBlock  {
 display:none!important;
}
.messageUserInfo * {
  padding:0px!important;
  margin-left:37px;
}
.messageUserBlock {
  height:40px!important; 
}
.messageUserBlock .userText {
  position:relative;
  background:#285E78!important;
  height:40px!important;
  z-index:500!important;
  padding-right:5px!important;
}
.messageUserBlock .userText a {
  padding-left:5px!important;
  padding-top:3px!important;
  font-size:14pt!important;
  color:#fff;
  z-index:500!important;
  margin-left:0px!important;
}
.messageUserBlock .extraUserInfo {
  position:relative!important;
  top:-20px!important;
  height:20px!important;
  font-size:7pt!important;
  color:#fff!important;
  background:transparent!important;
  padding-top:1px!important;
  padding-left:1px!important;
  z-index:500;
}
.messageUserBlock .extraUserInfo dl {
  display:inline-block;
  padding-top:0px!important;
  padding-left:5px!important;
}
.messageUserBlock .extraUserInfo dl dt {
/*display:none!important;*/
  display:inline-block;
  color:#fff!important;
}
.messageUserBlock .extraUserInfo dl:first-child dt {
  display:none!important;
}
.messageUserBlock .extraUserInfo dl:first-child dd {
  margin-left:0px!important;
}
.messageUserBlock .extraUserInfo dl:first-child dd:before {
  content:'Rek. ';
}
.messageUserBlock .extraUserInfo dl dd {
  display:inline-block;
}
.messageUserBlock .extraUserInfo dl:last-child {
}
.messageUserBlock .extraUserInfo dl:last-child dd, .messageUserBlock .extraUserInfo dl:last-child dd a {
  position:relative;
  margin-left:0px!important;
}
li.sectionMain {
  border:1px solid #285E78!important;
  background:#ddd!important; 
  margin-bottom:5px!important;
}
li.message .newIndicator {
/*  display:none!important;*/
}
.messageList .message {
  background:transparent!important;
}
.message .messageInfo, .message .messageInfo * {
  background:#222!important; /* DARK */
}
li .primaryContent {
  border-left:none!important;
  margin-left:0px!important;
  qposition:relative;
  qtop:0px!important;
  margin-top:0px;
  position:relative;
  z-index:100;
}
li .primaryContent .messageContent {
  min-height:0px!important;
  position:relative;
  margin-top:0px!important;
  padding-top:0px!important;
}
.signature.messageText {
  opacity:1;
}
.signature.messageText * {
  font-size:7pt!important;
  font-weight:normal!important;
  color: #aaa!important;
}
.signature.messageText a {
  text-decoration:underline!important;
}
.bbCodeQuote, .bbCodeQuote * {
  background:transparent!important;
}
.quoteContainer, .quoteContainer .quoteContainer .quoteContainer, .quoteContainer .quoteContainer .quoteContainer .quoteContainer .quoteContainer {
  border:none!important;
  background:rgba(128,128,128,0.1)!important;
}
.quoteContainer .quoteContainer, .quoteContainer .quoteContainer .quoteContainer .quoteContainer, .quoteContainer .quoteContainer .quoteContainer .quoteContainer .quoteContainer .quoteContainer {
  border:none!important;
  background:rgba(128,128,128,0.2)!important;
}
.bbCodeBlock.bbCodeQuote .attribution.type {
  width:60px!important;
  background:rgba(128,128,128,0.1)!important;
  border:none!important;
  padding-left:5px;
}
/* MESSAGE VIEW ENDS */  
  
  
  
/* ICONS */  
.miniMe{height: 24px!important; padding-left: 24px !important; background-image:url("http://www.3rdw.net/themes/MuroBBS/xfv1/icons24/thread_old_me.png") !important; width: 0 !important; background-repeat: no-repeat;}
IMG[src*="xenforo/icons_vaalea/thread_lock_new.png"]{height: 24px!important; padding-left: 24px !important; background-image:url("http://www.3rdw.net/themes/MuroBBS/xfv1/icons24/thread_old_lock.png") !important; width: 0 !important; background-repeat: no-repeat;}  
IMG[src*="xenforo/icons_vaalea/thread.png"]{height: 24px!important; padding-left: 24px !important; background-image:url("http://www.3rdw.net/themes/MuroBBS/xfv1/icons24/thread_old.png") !important; width: 0 !important; background-repeat: no-repeat;}  
IMG[src*="xenforo/icons_vaalea/thread_new.png"]{height: 24px!important; padding-left: 24px !important; background-image:url("http://www.3rdw.net/themes/MuroBBS/xfv1/icons24/thread_new.png") !important; width: 0 !important; background-repeat: no-repeat;}  
  
a:after {
/* content: "(" attr(href) ")" !important;*/
  }

.inner-wrapper footer {
  display:none!important;
}

/* REMOVE STUFF */
.footer-om {
  display:none;
}
  
  
/* DARK */  
#QuickReply, .quickReply {
  border:none!important;
}  
#QuickReply, .quickReply, .redactor_toolbar, .redactor_btn_group, .PageNav a  {
  background: #333!important;
}
.PageNav a:hover {
  background:#094065!important;
}
.breadcrumb, .crumb {
  background: #000!important;
}
.breadcrumb, .PageNav a {
  border:1px solid #666!important;
}
.breadcrumb .arrow  {
  background:#000!important;
  border-color: transparent #666 transparent #666 !important; 
  opacity:0.2!important;
}
.discussionListItems li  {
  background:transparent!important;
}
.discussionListItems li .listBlock.pairsJustified.stats, .discussionListItems li .listBlock.ikoni {
  background:#333!important;
}
.discussionList.section.sectionMain li {
    border-bottom:1px solid #666!important;
}
.nodeList li li .nodeInfo  {
  background:#333!important;
}


/* END OF STYLE */
}



/* REPLY BOX (CAUSES ALL about:blank PAGES TO CHANGE STYLE) */
@-moz-document url("about:blank") {
  * {
    background:#666!important;
    color:#ccc!important;
  }  
}
