css:
@-moz-document domain("murobbs.muropaketti.com") {

  body {background:none;padding:10px}

  html {background-image:url(http://mikrobb.net/style/mikrobb/img/bg.png);height:100%;}

  a:link, a:visited {color:#333}

  #uix_paneContainer {background-color:#efefef;max-width:1400px;margin: auto;border:solid #fff 5px;border-radius:13px;padding:0;}

  

   /**Turhuukset pois**/

  .sidebar {display:none}

  #logoBlock {display:none}

  

  /*Päävalikko**/

  .navTabs {background-image: linear-gradient(to bottom, #333 0%, #000 50%, #333 0%);} .navTabs .navTab {border:0} .navTabs {border:0}

  .navTabs .navTab.Popup.PopupContainerControl.PopupOpen, .navTabs .navTab.selected.PopupOpen .navLink {background-image: linear-gradient(to bottom, #333 0%, #000 50%, #333 0%);}

 

  /**Dropdown valikot**/

  .Menu {box-shadow:none;border:2px solid #333;}



  .blockLinksList a:hover, .blockLinksList a:focus, .blockLinksList li.kbSelect a, .blockLinksList label:hover, .blockLinksList label:focus, .blockLinksList li.kbSelect label {background:none;text-decoration:underline;}

  /**Ilmoitus**/

  .navTabs .navLink .itemCount {background:#ff0000;border-radius:7px;top:1px;right:16px}

  .navTabs .navLink .itemCount .arrow {display:none;}



  /**Subvalikko**/

  .navTabs .navTab.selected .tabLinks a:hover, .navTabs .navTab.selected .tabLinks a:focus {color:#000}

  

  /**Pikanavigointinappi**/

  .breadcrumb .jumpMenuTrigger {color:#333}

  .uix_icon-sitemap:before {display:none}

  /**Jättimainos pois**/

  .funbox {display:none;}

  /**Ilmoitusruutu**/

  #Notices .panel .noticeContent {font-size:0.6em;line-height:0.6em;}

  .PanelScroller .scrollContainer {}



  /**Foorumisällön maincontent **/

  .discussionList {border:1px solid #c0c0c0}

  

  #content .pageContent {background:#efefef}

  #content .pageContent {border:0;}

  .Responsive .pageWidth  {margin:0px!important}

  

  #content .pageContent {margin-top:0}

  .avatar img, .avatar .img, .avatarCropper {background:#4390d1}

  .PageNav, .pageNavLinkGroup {line-height:24px}

  .PageNav .pageNavHeader {border-radius:3px}

  .PageNav a {border-radius:3px;border:0;}

  .PageNav a:focus {border-radius:3px}

  .PageNav a:hover {border-radius:3px;border:0; background:#4390d1}

  .PageNav a.currentPage {border-radius:3px;border:0;background:#4390d1;}



  

  .titleBar h1 {font-size:1em}

  .mainContent {margin-right:0px!important;}

  .messageList .message {border:0;}

  #messageList {border:1px solid #c0c0c0; padding-bottom:3px} /**huijausta**/

  .message .messageMeta.otsikko {background-image: linear-gradient(to bottom, #C0C0C0 0%, #C9C9C9 50%, #C0C0C0 0%);}

  

  /**Indeksin linkit**/

  .node .nodeText .nodeTitle a {color:#333}

  

  /**Viestien linkit**/

  .ugc a:hover {box-shadow:none;color:#4390d1}

  

  /**Pikanavigointi**/

  .xenOverlay #jumpMenu .nodeList {border-color:#c0c0c0}

  #jumpMenu .blockLinksList ul {border-color:#c0c0c0}

  #jumpMenu .nodeList li.d0 {border-color:#c0c0c0}

  

  /**Muut popupit **/

  .xenOverlay .formOverlay {background:#fafafa}

  .xenOverlay .formOverlay .heading {color:#fafafa}

  .xenOverlay .formOverlay a {color:#fff}

  

  /**Signature**/

  .message .signature {padding-top:0;padding-bottom:0;border:0;color:#c0c0c0}

  .message .signature a {color:#c0c0c0;text-decoration:underline}

  .message .signature .bbCodeBlock {background:#ebebeb;border-color:#c0c0c0;}

  .message .signature .bbCodeQuote .attribution {color:#c0c0c0}

  

  /**Signaturen spoiler nappi ja tausta **/

  .message .signature .button {background-image: linear-gradient(to bottom, #C0C0C0 0%, #C9C9C9 50%, #C0C0C0 0%);}

  .message .signature .bbCodeSpoilerText {background:#ebebeb;border-color:#c0c0c0;margin-right:15px}



  /**Viimeksi muokattu teksti**/

  .message .editDate {color:#c0c0c0}

  

  /**Uudet viestit**/

  .message .newIndicator {background:#4390d1;}

  .uix_icon-lukematon:before {content: "Uusi";color:#fff; font-family:Arial;font-weight:bold;font-size:1em}

  

  .messageUserBlock a.username {color:#333;font-weight: bold;}

  /**Sticky**/

  .discussionList .discussionListItem.sticky {background:#eff8ff}

  

  /**nappi**/

  .button {box-shadow:none;background-image: linear-gradient(to bottom, #333 0%, #000 50%, #333 0%);color:#FFF}

  .bbCodeSpoilerButton {height:20px;line-height:20px;}

  

  /**Spoiler**/

  .hasJs .bbCodeSpoilerText {background:#ffcaca}

  

  /**QuoteBox**/

  .bbCodeQuote {width:100%;border-radius:4px;border:1px solid #ede5b2;background:#fffce7;padding-top:5px;padding-bottom:5px} .bbCodeQuote .quoteContainer {border:0;background:transparent} .bbCodeQuote .attribution, .bbCodeBlock .type {border:0;background:transparent;padding-left:10px;font-weight:bold;}

  .bbCodeBlock {margin:0;margin-bottom:10px}

  /**Kirjoitusboksi**/

  .nodeList .categoryStrip {background-image: linear-gradient(to bottom, #333 0%, #000 50%, #333 0%);}

  

  /**Vastausboksi**/

  .redactor_box {border-color:#c0c0c0;border-radius:0;margin-right:5px}

  #QuickReply .submitUnit {padding-bottom:5px;margin-right:5px}

  /**Ketjulinkin alempi rivi**/

  .discussionListItem .posterDate {font-size:11px;color:#c0c0c0}

  body .muted, body a.muted, body .muted a {color:#a2a2a2;}



  /** Viimeinen kolumni **/

  .discussionList .lastPost {white-space: nowrap; text-overflow:ellipsis;}

  

  /**Numerolinkit**/

  body .itemPageNav a {border:1px solid #c0c0c0;border-radius:2px;font-size:11px;}
  body .itemPageNav a:hover {border:1px solid #4390d1;background:none;color:#4390d1;}
  body .itemPageNav span {border:0}

  /**Turha pallukka pois**/

  .LoggedIn .discussionListItem .unreadLink {display:none}

  /**Turha padding pois foorumeiden listauksessa**/

  .LoggedIn .discussionListItem .titleText {padding-left:8px}

  /**forumlistauksen 2 viimeistä kolumnia**/

  .discussionListItem .lastPostInfo .dateTime {color:#333;font-weight:bold}

  .discussionListItem .lastPostInfo .username, .discussionListItems .unread .lastPostInfo .username{color:#a2a2a2}

  .discussionListItem .stats .minor {color:#333}

  

  .discussionList .sectionHeaders {background-image: linear-gradient(to bottom, #333 0%, #000 50%, #333 0%);}

  #pageDescription {font-size:10px;}

  .breadBoxTop, .breadBoxBottom {padding-bottom:5px;}

  a.callToAction span{background:#333}

  a.callToAction:hover span{background:#4390d1}

  .breadcrumb .crust a.crumb {color:#333}

  .breadcrumb .crust:last-child a.crumb {color:#333}

  .sectionFooter {background-image: linear-gradient(to bottom, #C0C0C0 0%, #C9C9C9 50%, #C0C0C0 0%);}

  .discussionList .ikoni {background:none}



  .discussionListItems .unread .title a {color:#333}

  .breadcrumb .crust{color:#000}

  .discussionListItem .stats {background:none}

  

  /**Someroska pois**/

  .sharePage {display:none}  

  /**Jalat**/

  .breadBoxBottom {margin-top:10px}

  .footer .pageContent {display:none;}

  .footer-om {display:none}

  

  /**Korkeusfix**/

  .off-canvas-wrapper > .inner-wrapper, .off-canvas-wrapper {min-height:0}

    

  /**Vammaset title linkit **/  

  .discussionList .sectionHeaders a {background-image: linear-gradient(to bottom, #333 0%, #000 50%, #333 0%);}

  .discussionList .sectionHeaders a span {padding: 5px 7px;}

  

  /**Viestinappulat**/

  .message .messageMeta a {color:#c0c0c0}

  .message .messageMeta a:hover {color:#333}

  

  /**Asetus-sivut**/

  /**Vammanen margin vasemmassa valikossa**/

  .navigationSideBar .heading { margin-top:0;}

  .navigationSideBar > ul {margin-bottom:0}

  /**Nappulan bg**/

  .heading, .xenForm .formHeader {background-image: linear-gradient(to bottom, #333 0%, #000 50%, #333 0%);}

  .subHeading {background-image: linear-gradient(to bottom, #333 0%, #000 50%, #333 0%);}

  /**Pääboksi**/

  .mainContentBlock {background:#fff;padding:10px}

  .alerts .alertGroup {margin-left:10px;margin-right:10px}

  /**Profiilisivu**/

  .profilePage .primaryUserBlock .mainText {background:none}

  .profilePage .mainProfileColumn {background:#fff;border:0;border-radius:4px;border:1px solid #cccccc;padding-bottom:10px;margin-left:230px}

  .profilePage .primaryUserBlock .lastActivity {border:0}

  .searchResult {border-color:#c0c0c0;padding:5px;}

  .profilePage .mast .section.infoBlock .secondaryContent {padding:5px;}

  .profilePage .mast {width:220px}

}
