css:
@-moz-document domain("www.sweclockers.com") {
/* Main page */

.carousel .slide>.inner, .mainContent, .newsList .commentsBubble, .videoPush .image img, .popularGalleries .image, .mainWidget, .orangeInfoBox {
	border-radius: 0px !important;
	-moz-border-radius: 0px !important;
	-webkit-border-radius: 0px !important;
}




/* Right column push lists */

.pushListInternal, .pushListSocial, .pushListMonster {
	border-radius: 0px !important;
	-moz-border-radius: 0px !important;
	-webkit-border-radius: 0px !important;
}




/* Menu ribbon bar */

.menuItem {
    border-top-left-radius: 0px !important;
    border-top-right-radius: 0px !important;
    -webkit-border-top-left-radius: 0px !important;
    -webkit-border-top-right-radius: 0px !important;
}



/* Gallery */

.hotGallery .image, .galleryItem .image, .galleryHeader .thumb img {
	border-radius: 0px !important;
	-moz-border-radius: 0px !important;
	-webkit-border-radius: 0px !important;
}



/* Facebook share button & co */
.fbShareButton, .mainContent .greyContentShare #fbLikeButton, .pluginButton {
	border-radius: 0px !important;
	-moz-border-radius: 0px !important;
	-webkit-border-radius: 0px !important;
}

/* A few quirk workarounds... */
.gallery .image {
  height: auto;
}
}
