@-moz-document domain("pinterest.com"), domain("pinterest.fr") {
/*__________________Pinterest - Gray Theme______________________*/
html, .Closeup.canClose {
    background-color: #222 !important;
  }

  .pinMeta {
    background-color: #090909 !important;
  }

  .Pin.summary .pinCredits {
    border-top-color: #000 !important;
  }

  .Pin.summary .creditItem:hover {
    background: inherit !important;
  }

  .PinBase.summary .recommendationReason {
    border-top-color: #111 !important;
  }

  .Pin.summary {
    box-shadow: 3px 3px 5px 0px rgba(0, 0, 0, 0.5) !important;
    border-radius: 6px !important;
    background-color: #000 !important;
  }

  .Pin.detailed .closeupContentSection,
  .Pin .sourceFlagWrapper,
  .CloseupRichPinContent .richPinTitleWrapper.onlyAttribution,
  .Board.boardPinsGrid,
  .richPinMeta,
  .CloseupRichPinContent .richPinInformation {
    background-color: #000 !important;
    border-color: #222 !important;
  }

  .imageContainer,
  .PaddedPin .paddedPinLink,
  .transitionImage /* loading image background on closeup (omg, pain in the ass to find!) */
  {
    background: #111 !important;
    border-color: #222 !important;
  }

  .hoverMask, .hoverMask:hover  {
     visibility: hidden !important;
  }

  .navLinkOverlay {
    visibility: hidden !important;
  }

  .repinSendButtonWrapper,
  .likeEditButtonWrapper{
    visibility: hidden !important;
  }

  /* remove comments */
  .pinDescriptionCommentItem.pinUserCommentBox,
  .PinCommentList {
    display: none !important;
  }

  .Button.expandGiraffe {
    background: rgba(0,0,0, 0.5) !important;
    color: #ccc !important;
  }

  .Button.expandGiraffe .buttonText {
    color: #bbb !important;
  }

  .sectionTitle,
  .description {
    color: #ccc !important;
    text-shadow: none !important;
  }

  .boardName {
    color: #fff !important;
    text-shadow: 3px 3px 5px #000 !important;
  }

  .domainWrapper,
  .Board.boardCoverImage,
  .Board.boardPinsGrid.noShadow .boardHeader {
    background: #000 !important;
  }

  .Board.boardCoverImage .boardCoverWrapper .boardCover,
  .Board.boardCoverImage .boardThumbs li .thumb {
    border-radius: 0 !important;
  }

  .Button.btn,
  button {
    background: #444 !important;
    border-color: #555 !important;
    color: #ccc !important;
    text-shadow: 0px 1px #000 !important;
   }



  .PinActionBar.guided,
  .InfoBarBase {
    background: #000 !important;
    border-color: #111 !important;
  }

  .BoardInfoBar .counts .active:hover, .BoardInfoBar .counts .active.active,
  .InfoBarBase li a, .InfoBarBase li a:hover {
    background: transparent !important;
    border-color: #111 !important;
    color: #ccc !important;
    text-shadow: none !important;
  }

  .headerBackground {
    background: #333 !important;
  }

  .Header .rightHeaderContent .RightHeader {
    background: inherit !important;
  }

  .Header .rightHeaderContent .RightHeader button {
    background: #222 !important;
    border-color: #111 !important;
  }


  .ui-TypeaheadField.guided .tokenizedInputWrapper {
    background: #000 !important;
    border-color: #111 !important;
  }

  .TokenizedInput.guided .tokensWrapper .tokenizedItem.inputToken .field {
    color: #fff !important;
  }

  .PinActionBar.guided {
    background: #000 !important;
  }

  .AuthHomePage .unauthPinGridWrapper {
    border-top: none !important;
  }

  .Nags,
  .everythingFeedPinGridTitle /* Want to see more Pins you love in your feed? Follow interesting boards */
  {
    display: none !important;
  }

  .Pin .sourceFlagWrapper a, .Pin .sourceFlagWrapper p {
    text-shadow: none !important;
  }

  .Button {
    box-shadow: none !important;
  }

  /* + ? */
  .footerButtonsWrapper {
    display: none !important;
  }

  .Domain .thumb {
    background: none !important;
  }

  .Domain .thumb img[src=""] {
    visibility: hidden !important;
  }

  .pinGridWrapper {
    overflow: hidden !important;
  }

  .pinGridWrapper:hover {
    overflow: scroll !important;
  }

  .closeupContentSection,
  .Board.boardPinsGrid,
  .Domain .domainWrapper,
  .Board.boardCoverImage {
    box-shadow: 3px 3px 5px 0px rgba(0, 0, 0, 0.5) !important;
  }

  .moreItemsContainer {
    margin-top: 15px !important;
  }

   .moreItemsContainer button {
     border-radius: 4px !important;
     box-shadow: 1px 1px 2px 0px rgba(0, 0, 0, 0.5) !important;
     border: 1px solid #555 !important;
   }

  .gridLoading {
    background: none !important;
  }

  .BoardHeader,
  .BoardInfoBar
  {
    background: #000 !important;
    border-color: #111 !important;
  }
  
  .divider > hr {
    background: #444 !important;
  }
  
  .pinsAndFollowerCount li {
    background: transparent !important;
  }
  
  .UserProfileHeader.profileHeader .header,
  .UserProfileHeader.profileHeader .fixedHeader,
  .InfoBarBase.infoBar .tabs li {
    background: #000 !important;
  }
  
  .UserProfileHeader.profileHeader .profileImage img {
    box-shadow: 0px 0px 0px -2px rgba(255, 255, 255, 0.48) inset, 0px 0px 0px 1px rgba(0, 0, 0, 0.14), 0px 0px 0px 8px #444 !important;
  }
  
  .aboutBar .name,
  .BoardHeader .boardName,
  .feedName {
    color: #ccc !important;
    text-shadow: none  !important;
  }
/*--------------------------------------------------------------------------------------------------------*/






html {
    background: #342F2F;
}
.UserNews * {
background: rgb(0, 0, 0) !important;
color: rgb(255, 255, 255);
border-radius: 0px;
}
.buttonText{
  background: rgba(0, 0, 0,0) !important;
}
  .headerBackground {
    background: #333 !important;
  }

  .Header .rightHeaderContent .RightHeader {
    background: inherit !important;
   }

  .Header { z-index:100;
  }

  .Header .rightHeaderContent .RightHeader button {
    background: #222 !important;
    border-color: #111 !important;
  }

.pinWrapper * 
{

-webkit-transition:all .8s;
-moz-transition:all .8s;
-o-transition:all .8s;
transition:all .8s;
}
.pinWrapper {
    overflow: hidden;
      background: rgba(0, 0, 0, 0.4);
}
.Pin.summary.summary{
border-radius:0;
    background: #000;
}

.pinMeta, .pinCredits,.recommendationReasonWrapper
 {
    bottom: -150px !important;
    position: absolute !important;
    color: #FFF;
    background: rgba(0, 0, 0, 0.6) !important;
  z-index:2;
  height:0;
  opacity:0 !important;
}
.pinMeta *, .pinCredits *,.recommendationReasonWrapper *
 {
      color: #FFF !important;
}
.recommendationReasonWrapper{
  height:0 !important;
  position:absolute!important;
  width:100%;
}

.pinWrapper:hover .pinMeta,.pinWrapper:hover .pinCredits,.pinWrapper:hover .recommendationReasonWrapper
 {
    bottom: 0px !important;
    color: #FFF;
  position:relative !important;
    opacity:1 !important;
    height:auto;
}

  .pinMeta, .pinCredits, .richPinMeta {
        position: absolute;
    }

    .Pin.summary .pinImageWrapper, .Pin.summary .pinImg.fullBleed {
        border-radius: 0px;
    }
.Header.guided.Module {
    opacity: 0;
    transition: all 1s;
}
.Header.guided.Module:hover {
    opacity: 1;
    transition: all 1s;
}

.closeupContentSection {
    border-radius: 0 !important;
    border: #00FFDF 1px solid !important;
}
img.pinImg.fullBleed.noFade {
    border-radius: 0;
}

.pinWrapper {
box-shadow: rgba(169, 183, 157, 0.14) 0px 8px 10px -4px,rgba(0, 255, 148, 0.2) 0px 19px 13px -12px,rgba(0, 165, 255, 0.44) 0px 19px 29px -12px;
border-radius: 0 !important;
outline: rgba(0, 0, 0, 0.47) 1px solid;
  margin:12px 0;
}
.pinHolder {
    border-radius: 0px;
}

.Pin.summary .pinImageWrapper, .Pin.summary .pinImg.fullBleed {
    border-radius: 0px !important;
    background-color:  rgba(0, 0, 0, 0.4) !important;
}


.headerBackground {
    opacity: 0;
}

.headerContainer {
    opacity: 0;
}
.headerContainer:hover {
    opacity: 1;
}
.Header {
    opacity: 0;
    transition: all .8s ease;
    background-color: rgba(0, 0, 0, 0.79);
    box-shadow: rgba(0, 0, 0, 0.73) 0 1em 1em;
    border-bottom: rgba(205, 33, 55, 0.8) 2px solid;
}

.Header:hover {
    opacity: 1;
}
}
