/* Sweclockers dark fixed menu ( Made very quickly , works atleast on Chrome) */

/* Made by solding */

@-moz-document domain("www.sweclockers.com") {
#head {
	margin-top: 40px !important;
}
  
#head .menuShadow {
	position: fixed;
	z-index: 999;
	top: 1px;
	width: 100%;
	height: 40px;
  	background: rgba(20, 20, 20, 0.98);
  	border-bottom: none;
  	box-shadow: 0 0 3px 2px rgba(20, 20, 20, 0.98);
}
  
#head .banner {
	padding: 10px 0px 36px 0px;
	border-bottom: 1px solid #9B9B9B;
}

#head .menu li {
	height: 38px;
	border-right: 1px solid #000 !important;
	border-left: 1px solid #222 !important;
	box-shadow: none;
	background: none;
  	margin: 0px;
  	border-top: none;
  	border-bottom: none;
}

#head .menu li:hover {
	background: #151515;
	box-shadow: inset 0 0 10px #000;
	border-bottom: 1px solid #292929;
}

#head .menu li a {
  	padding: 14px 16px 6px 16px !important;
  	font-weight: 900;
  	color: rgba(255, 255, 255, 0.72) !important;
}

#head .menu li.current {
	background: rgba(20, 20, 20, 0.98);
  	color: rgba(255, 255, 255, 0.72);
	box-shadow: inset 0 0 10px #000;
    border-bottom: 1px solid #292929;
}
}
