css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("https://portal.sf.mail.ru/"), url-prefix("http://portal.sf.mail.ru/") {

html {
background: url("http://egida-guild.ru/bg2.jpg") no-repeat top center fixed #fff !important;
  min-height: 100% !important;
  overflow: -moz-scrollbars-vertical !important;
  overflow-y: scroll !important;
  background-size: cover !important;
 
}

.main-menu {
background-color: white !important;
}

li.set-active > a {
-webkit-box-shadow: inset 0 -3px 0 0 #2B7196;
-moz-box-shadow: inset 0 -3px 0 0 #2B7196;
box-shadow: inset 0 -3px 0 0 #2B7196 !important;}
  
.main-menu > ul > li:hover > a {
color: #FAFBFC;
-webkit-box-shadow: inset 0 -3px 0 0 #2B7196;
-moz-box-shadow: inset 0 -3px 0 0 #2B7196;
box-shadow: inset 0 -3px 0 0 #2B7196 !important;
}

.filter-item.set-active, .filter-item:hover {
-webkit-box-shadow: inset 0 -3px 0 0 #2B7196;
-moz-box-shadow: inset 0 -3px 0 0 #2B7196;
box-shadow: inset 0 -3px 0 0 #2B7196 !important;}
.main-menu .head-dropdown-menu a { color: #2B7196;}
.btn {background-color: #2B7196 !important;}
.btn:hover,a.btn:hover,.btn:focus,a.btn:focus { background-color: #106491 !important;  text-decoration: none !important;}
.widget-control {background-color: rgba(17, 83, 117, 0.75) !important;}
.chat-control {background-color: rgba(17, 83, 117, 0.75)!important;}
element.style {background-color: white !important;}
.widgets-wrap {background: rgba(255, 255, 255, 0.75) !important;}
.profile-widget {color: #2B7196 !important;}
.notific i{color: #2B7196 !important;}
.notific i:hover {color: #48AADD !important;}
.sec-head {background: rgba(43, 113, 150, 0.5) !important;}
.widgets {border-top: 1px solid gray !important;}

.brief__pic.set-online:after { background-color: #2B7196 !important;}
.ulist__all {background-color: #2B7196 !important;}      
.ulist__all:hover {background-color: #48AADD !important;}
.friends-widget-icon i {border: 2px solid #2B7196 !important;} 
.friends-widget-icon i:hover {border: 2px solid #48AADD !important;}
.friends-widget-icon {color: #2B7196 !important;}
.friends-widget-icon:hover {color: #48AADD !important;}
.friends-widget-icon:hover i {color: #48AADD !important;}
.antiscroll-scrollbar-vertical {background-color: #2B7196 !important;}
.card-opener:hover, .card-opener.set-active {background: #2B7196 !important;}
.game-widget__pic path, .game-widget__pic g {fill: #2B7196 !important;}
.widgets > section, .widgets > div {max-width: 320px !important;}
.friends-widget {position: relative;padding: 0px 20px 0 0 !important;}
.widgets-editor {background-color: #2B7196 !important;}
.head-logo path {fill: #2B7196 !important;}
.main-menu > ul > li:hover > a {color: #fff; background: rgba(43, 113, 150, 0.7) !important;}
.main-menu .head-dropdown-menu a {color: white !important;}
.main-menu .head-dropdown-menu a:hover {color:  #2B7196 !important;}
.main-menu .head-dropdown-menu {  background: rgba(43, 113, 150, 0.5) !important;}
.main-menu > ul > li.set-active > a {-webkit-box-shadow: inset 0 -3px 0 0 #2B7196;-moz-box-shadow: inset 0 -3px 0 0 #2B7196;box-shadow: inset 0 -3px 0 0 #2B7196 !important;}
.btn, a.btn, .select .btn-dropdown {background-color: #2B7196 !important;}
.caret {border-top: 8px solid #2B7196 !important;border-left: 8px solid #2B7196 !important;border-right: 8px solid #2B7196 !important;}
.brief__pic .img{background-color: #2B7196 !important;}
.chat-contact-control-wrap, .chat-contact .brief__pic .img {background-color: #2B7196 !important;}
.chat-contact-head__room, .chat-add__icon, .chat-filter__input {color: #2B7196 !important;}
.chat-user__name {color: #2B7196 !important;}
.cont-load{color: #2B7196 !important;}
.foot-wrap {background-color: rgba(185, 231, 230, 0.75) !important;}
.card-wrap a.brief__info, .card-wrap .brief__info a {color: #2880AF !important;}
.progress-bar .set-active {background: #2B7196 !important;}
.post-control i{color: #2B7196 !important;}
.post-control i:hover {color: #106491 !important;}
.redactor_editor a, .wysiwyg a {color: #2B7196 !important;}
.form-group.focus .form-control, .form-control:focus {border: 2px solid #2B7196 !important;}
.checkbox.checked .second-icon, .radio.checked .second-icon{color: #2B7196 !important;}
.select .btn-dropdown .filter-option {border-right: 2px solid rgba(255, 255, 255, 0.5) !important}
.ui-datepicker{border: 7px solid #2B7196 !important;}
.ui-datepicker {border: 7px solid #2B7196 !important;}
.ui-datepicker .ui-datepicker-prev:hover,.ui-datepicker .ui-datepicker-next:hover {  background: #106491 !important;}
.ui-state-active {background-color: #2B7196 !important;}
.spinner > div {background-color: #2B7196 !important;}
.s-big-chat .chat-head {background-color: rgba(17, 83, 117, 0.75) !important;}
.chat-head{background-color: #2B7196 !important;}
.chat-message-name i {color: #2B7196 !important;}
.forum-theme-icon i {color: #2B7196 !important;}
.forum-sec-icons {color: #2B7196 !important;}
.breadcrumbs a {color: #2B7196 !important;}
#redactor_tabs a {background-color: #2B7196 !important;}
.redactor_modal_action_btn{background-color: #2B7196 !important;}
.redactor_modal_action_btn:hover{background-color: #106491 !important;}
#redactor_modal input:focus,#redactor_modal textarea:focus {border-color: #106491 !important;}
.redactor_modal_btn:hover {background: rgb(203, 195, 195) !important;}
.redactor_toolbar li a:hover {border-color: #2B7196;border-radius: 1px !important;}
.lang-active {  background-color: #2B7196 !important;}
.lang-list {  background: rgba(33, 98, 132, 0.7) !important;}
.lang-list a {color: #fff !important;}
.lang-list a:hover {color: #094689 !important;}
.lang__wrap:hover .lang-active__wrap {background-color: rgba(33, 98, 132, 0.7) !important;}
.friends-widget-icon:hover i {border-color: 48AADD !important;}
.chat-contact-add_icon {color: #2B7196; border: 3px solid #2B7196 !important;}
.control-item a.set-active,.control-item.set-active {color:  #2B7196 !important;}
.widget-border {  border-top: 1px solid #E4F2FB !important;}
.widgets > section:not(:first-child):last-child.widget-border {box-shadow: inset 0 -1px 0 0 #E4F2FB, inset 0 1px 0 0 #E4F2FB !important;}
.control-wrap.set-active {background-color: #2B7196 !important;}
.control-wrap {background-color: rgba(85, 170, 223, 0.6) !important;}
.sec-body {box-shadow: 0 1px 10px rgba(30, 26, 26, 0.33) !important;}
.friends-widget-icon:hover i { border-color: #48AADD !important;}
.friends-widget-icon { background-color: rgba(43, 113, 150, 0.07) !important; box-shadow: inset 0px 0px 10px rgba(43, 113, 150, 0.61) !important;}
.chat-contact:hover {background-color: rgba(103, 182, 251, 0.13) !important;}
.chat-contact-count {
  position: absolute !important;
  display: block !important;
  top: 35% !important;
  right: 6px !important;
  line-height: 20px !important;
  min-width: 18px !important;
  min-height: 18px !important;
  text-align: center !important;
  font-size: 11px !important;
  font-size: 1.1rem !important;
  line-height: 15px !important;
  letter-spacing: -1px !important;
  background-color: #ff0000 !important;
  color: #fff !important;
  box-shadow: inset 0px 0px 1px 1px rgba(255, 255, 255, 0.71) !important;
  border-radius: 0px !important;}
.chat-contact-control-wrap {right: 12px !important;}
.chat-contact__wrap {width: 260px !important;}
.context-menu a:hover {color: #2B7196 !important;}
.chat-contact-head ::-webkit-input-placeholder { /* WebKit browsers */ color: #2B7196;}
.chat-contact-head :-moz-placeholder {  /* Mozilla Firefox 4 to 18 */   color: #2B7196;  opacity: 1;}
.chat-contact-head ::-moz-placeholder {  /* Mozilla Firefox 19+ */   color: #2B7196; opacity: 1;}
.chat-contact-head :-ms-input-placeholder {  /* Internet Explorer 10+ */   color: #2B7196;}
.comment-head.set-target {  background-color: rgba(46, 190, 225, 0.25) !important;}
.redactor_editor .blockquote-link:hover,
.wysiwyg .blockquote-link:hover,.redactor_editor .blockquote-link.set-active,.wysiwyg .blockquote-link.set-active {  background: #2B7196 !important;}
.dropdown-menu li.active > a,.dropdown-menu li.selected > a,.dropdown-menu li.active > a.highlighted,.dropdown-menu li.selected > a.highlighted {background:  #2B7196 !important;}
.dropdown-menu li.active > a:hover,.dropdown-menu li.selected > a:hover,.dropdown-menu li.active > a.highlighted:hover,.dropdown-menu li.selected > a.highlighted:hover,
.dropdown-menu li.active > a:focus,.dropdown-menu li.selected > a:focus,.dropdown-menu li.active > a.highlighted:focus,.dropdown-menu li.selected >
a.highlighted:focus { background: #106491 !important;}
.ui-datepicker .ui-datepicker-header {background: #2B7196 !important}
.ui-datepicker:before {border-color: transparent transparent #2B7196 !important;}
.ui-datepicker .ui-state-active {background-color: #2B7196 !important;}
.ui-datepicker:hover .ui-state-active:hover{color:#86BEE !important;}
.ui-datepicker td a:hover {color: #2B7196 !important;}
.ui-datepicker select.ui-datepicker-year {color:  #2B7196 !important;}
.select .btn-dropdown.set-inverse .caret {border-top: 8px solid #FFFFFF !important;}
.paging-control{background-color: #2B7196 !important;}
.panel-admin a.panel-admin-info {box-shadow: inset 0 -3px 0 0 rgba(12, 122, 176, 0.99) !important;}
.dropdown-menu li > a {color: rgba(12, 122, 176, 0.99) !important;}
.antiscroll-scrollbar-horizontal {visibility: hidden !important;}
.is-member .i-user-add {color: #2B7196 !important;}
.aside-right_chat {background: rgba(255, 255, 255, 0.43) !important;}
.filter-tabs a {color: #86E9FD !important;}
.wysform-control:hover,a.wysform-control:hover {color: #106491 !important;}
.control-wrap.set-disabled .control-item {color: rgba(46, 134, 244, 0.5) !important;}
.notific:hover i {color: #106491 !important;}
.is-txt a {color: #2B7196 !important;}
.profile-widget__name {color: #222324 !important;}
.ui-datepicker select.ui-datepicker-month,.ui-datepicker select.ui-datepicker-year, .ui-datepicker th {color: #2B7196 !important;}
.input-group.focus {border-color: #2B7196 !important;}
a.friends-widget-add:hover { color: #2B7196 !important;}
.profile-widget .brief__pic .img,
.profile-widget .brief__pic .info {box-shadow: 0 0 20px rgba(0, 0, 0, 0.71) !important; transform: rotate(8deg) !important;}
.chat-message_note span {color: #2B7196 !important;}
.brief__title span {color: #004F79 !important;}
.brief .set-emphasis {color: #004F79 !important;}
.region-page-title span {color: rgb(3, 69, 110) !important;}
.login-reg{color: rgb(3, 69, 110) !important;}
.checkbox.checked, .radio.checked{color: rgb(3, 69, 110) !important;}
.search .search-input:focus {color:  #2B7196 !important;}
.btn.set-inv.set-active,a.btn.set-inv.set-active,.btn.set-inv:hover,a.btn.set-inv:hover{ -webkit-box-shadow: inset 0 -3px 0 0 rgb(62, 128, 184);-moz-box-shadow: inset 0 -3px 0 0 rgb(62, 128, 184); box-shadow: inset 0 -3px 0 0 rgb(62, 128, 184) !important;}
.widget-i span{color: #2B7196 !important;}
.is-link-text{color: #2B7196 !important;}
.chat-user__name.set-deletable {color: #8B8B8B !important;}
.redactor_dropdown a:hover { background-color: #2B7196 !important;}
.s-newcomments .sec-head--r a {color: #2B7196 !important;}
.profile-avatar__del {color: #2B7196 !important;}
.btn.set-global,
a.btn.set-global { background-color: #2B7196 !important;}
.btn.set-global,a.btn.set-global:hover {background-color: #48AADD !important;}
.btn.set-alt, a.btn.set-alt{color:#fff !important;}
.btn.set-alt, a.btn.set-alt:hover{color:#fff !important;}
.lb-prev {  background: url('https://portal.sf.mail.ru/images/lightbox/icon_media_arrow_left_hover.png') left 48% no-repeat !important;}
.lb-next {background: url('https://portal.sf.mail.ru/images/lightbox/icon_media_arrow_right_hover.png') right 48% no-repeat !important;}
.lb-data .lb-close {background: url('https://portal.sf.mail.ru/images/lightbox/icon_close_popup_hover.png') top right no-repeat !important;}
}
