css:
@-moz-document domain(youtube.com) {
#logo {
background: no-repeat url(http://i.imgur.com/XO08Lak.png)!important;
}
#yt-masthead #logo-container span.content-region {
position: relative;
top: -19px;
left: 2px;
color: #777;
font-size: 0px;
text-decoration: none;
font-weight: normal;
}
/*SEARCH BUTTON*/
#masthead-search .search-btn-component {
height: 29px;
}
.search-form > .yt-uix-button, .search-form > .yt-uix-button-group, #channel-search .search-btn, #channel-search .search-dismiss-btn {
-moz-appearance: none!important;
background: transparent url(http://i.imgur.com/cP9KWEb.png) no-repeat!important;
border: none!important;
width: 66px!important;
height: 29px!important;
position: relative;
left: 0px!important;
bottom: 0px!important;
background-size: 66px 29px!important;
border-radius: 0px!important;
}
#masthead-search .search-btn-component .yt-uix-button-content {
display: block;
border: none;
padding: 0;
opacity: .6;
text-indent: -10000px;
background: no-repeat url(http://i.imgu.com/cP9KWEb.png) 0 -1893px;
background-size: auto;
width: 1px;
height: 1px;
box-shadow: none;
}
#yt-masthead-user .sb-notif-on .yt-uix-button-icon-bell {
background: no-repeat url(http://i.imgurcom/cP9KWEb.png) 0 0px;
background-size: 0;
width: 1px;
height: 1px;
}
#yt-masthead-user .sb-notif-on .yt-uix-button-content {
position: absolute;
top: 0px;
left: 0px;
z-index: 0px;
min-width: 0px;
border-bottom: 0px solid rgba(255, 255, 255, 0);
border-left: 0px solid rgba(255, 255, 255, 0);
background: rgba(203, 68, 55, 0);
color: rgba(255, 255, 255, 0);
line-height: 0px;
text-align: center;
opacity: 1;
border-radius: 0px;
}
#yt-masthead-user .yt-uix-button-icon-bell {
position: absolute;
left: 0;
top: -2px;
background: no-repeat url(http://i.imgu.com/cP9KWEb.png) 0 -665px;
background-size: auto;
width: 1px;
height: 1px;
opacity: .55;
filter: alpha(opacity=55);
}
}
