css:
@-moz-document regexp(".+demonoid\\..{2,3}\\/files\\/.+") {
a:visited {
  color: hsl(240, 38%, 70%) !important;
}

.pad9px_left > table:nth-of-type(4) {
  position: fixed;
  top: 0;
  left: 0;
}

.pad9px_left > table:nth-of-type(5) {
  position: fixed;
  top: 30px;
  left: 0;
}

.pad9px_left > table:nth-of-type(5) br {
  display: none;
}

.pad9px_left > table:nth-of-type(5) a {
  display: none;
}

.pad9px_left > table:nth-of-type(5) a:nth-of-type(3) {
  display: inline;
}

.pad9px_left > table:nth-of-type(6) {
  position: fixed;
  top: 74px;
  left: 0;
}

.pad9px_left > table:nth-of-type(2) tr:nth-of-type(4) {
  position:fixed;
  top: 54px;
  left: 0;
  background-color: hsl(0, 0%, 93%);
  width: 100%;
}


.pad9px_left > table:nth-of-type(2) tr:nth-of-type(4) td:first-of-type {
  border-left: 1px solid black;
  width: 100%;
  padding: 7px;
}

.pad9px_left > table:nth-of-type(2) tr:nth-of-type(4) td:last-of-type {
  border-right: 1px solid black;
  width: 100%;
}

body {
  border-top: 10px solid hsl(0, 0%, 80%);
  padding-top: 75px !important;
}

#envel {
  display: none;
}
}
