@-moz-document domain(pinboard.in) {

  /* latin-ext */
  @font-face {
    font-family: 'Lora';
    font-style: normal;
    font-weight: 400;
    src: local('Lora'), local('Lora-Regular'), url(http://fonts.gstatic.com/s/lora/v9/tHQOv8O1rd82EIrTHlzvmhTbgVql8nDJpwnrE27mub0.woff2) format('woff2');
  }
  /* latin */
  @font-face {
    font-family: 'Lora';
    font-style: normal;
    font-weight: 400;
    src: local('Lora'), local('Lora-Regular'), url(http://fonts.gstatic.com/s/lora/v9/rAXKWvABQNHjPUk26ixVvhTbgVql8nDJpwnrE27mub0.woff2) format('woff2');
  }

  /* latin-ext */
  @font-face {
    font-family: 'Open Sans';
    font-style: normal;
    font-weight: 400;
    src: local('Open Sans'), local('OpenSans'), url(http://fonts.gstatic.com/s/opensans/v10/u-WUoqrET9fUeobQW7jkRZBw1xU1rKptJj_0jans920.woff2) format('woff2');
  }
  /* latin */
  @font-face {
    font-family: 'Open Sans';
    font-style: normal;
    font-weight: 400;
    src: local('Open Sans'), local('OpenSans'), url(http://fonts.gstatic.com/s/opensans/v10/cJZKeOuBrn4kERxqtaUH3ZBw1xU1rKptJj_0jans920.woff2) format('woff2');
  }
  /* latin-ext */
  @font-face {
    font-family: 'Open Sans';
    font-style: normal;
    font-weight: 600;
    src: local('Open Sans Semibold'), local('OpenSans-Semibold'), url(http://fonts.gstatic.com/s/opensans/v10/MTP_ySUJH_bn48VBG8sNSiYtBUPDK3WL7KRKS_3q7OE.woff2) format('woff2');
  }
  /* latin */
  @font-face {
    font-family: 'Open Sans';
    font-style: normal;
    font-weight: 600;
    src: local('Open Sans Semibold'), local('OpenSans-Semibold'), url(http://fonts.gstatic.com/s/opensans/v10/MTP_ySUJH_bn48VBG8sNShampu5_7CjHW5spxoeN3Vs.woff2) format('woff2');
  }
  /* latin-ext */
  @font-face {
    font-family: 'Open Sans';
    font-style: normal;
    font-weight: 700;
    src: local('Open Sans Bold'), local('OpenSans-Bold'), url(http://fonts.gstatic.com/s/opensans/v10/k3k702ZOKiLJc3WVjuplzCYtBUPDK3WL7KRKS_3q7OE.woff2) format('woff2');
  }
  /* latin */
  @font-face {
    font-family: 'Open Sans';
    font-style: normal;
    font-weight: 700;
    src: local('Open Sans Bold'), local('OpenSans-Bold'), url(http://fonts.gstatic.com/s/opensans/v10/k3k702ZOKiLJc3WVjuplzBampu5_7CjHW5spxoeN3Vs.woff2) format('woff2');
  }

  #banner {
    opacity: .4;
    transition: all 200ms cubic-bezier(0.26, 0.86, 0.44, 0.985);
  }

  #banner:hover {
    opacity: 1;
  }

  #search_query_field {
    font-size: 16px;
    height: 24px;
    padding: 4px 8px 3px;
    width: 80%;
  }

  #search_query_field:focus {
    outline: none;
  }

  .search_button {
    margin-top: 6px;
    text-align: right;
    width: 86%;
  }

  .search_button input {
    background: #34495e;
    border: none;
    border-radius: 2px;
    color: #fafafa;
    cursor: pointer;
    font-size: 11px;
    letter-spacing: .06em;
    transition: all 200ms cubic-bezier(0.26, 0.86, 0.44, 0.985);
  }

  .search_button input:hover,
  .search_button input:hover {
    background: #4A6075;
  }

  .search_button input:focus {
    outline: none;
  }

  #content {
    color: #333;
    font-family: 'Open Sans', sans-serif;
  }

  .bookmark {
    padding-bottom: 26px;
    position: relative;
    width: 100%;
  }

  .bookmark::after {
    background: #bdc3c7;
    content: "";
    display: block;
    height: 1px;
    position: absolute;
    bottom: 1px;
    left: 10%;
    right: 20%;
    width: 70%;
  }

  .display {
    width: 90%;
  }

  .selected_star {
    color: #f1c40f;
  }

  .bookmark_title,
  .bookmark_title:visited {
    color: #333;
    font-weight: 600;
    font-size: 16px;
  }

  .star {
    margin-top: -4px;
  }

  a.url_display {
    display: block;
    transition: all 200ms cubic-bezier(0.26, 0.86, 0.44, 0.985);
  }

  a.url_display:hover,
  a.url_display:active {
    color: #34495e;
  }

  .description {
    color: #7f8c8d;
    font-family: 'Lora', serif;
    margin-bottom: 16px;
  }

  .description blockquote {
    color: inherit;
    margin: 0;
  }

  a.tag {
    background: #85C4ED;
    color: #fafafa;
    font-size: 11px;
    padding: 1px 4px 2px;
    transition: all 200ms cubic-bezier(0.26, 0.86, 0.44, 0.985);
  }

  a.tag:hover {
    background: #3498db;
  }

  a.when,
  a.when:visited {
    color: #bdc3c7;
    margin-top: 4px;
    transition: all 200ms cubic-bezier(0.26, 0.86, 0.44, 0.985);
  }

  a.when:hover,
  a.when:active {
    color: #34495e;
  }

  #tag_cloud_header a.tag,
  .tag_table a.tag {
    background: none !important;
    color: #333 !important;
  }

  #tag_cloud_header a.tag,
  .tag_table a.tag:hover {
    color: #3498db !important;
  }

  .url_link {
    background: none !important;
    text-decoration: underline;
  }

  .edit_links {
    float: right;
    opacity: 0;
    transition: all 200ms cubic-bezier(0.26, 0.86, 0.44, 0.985);
  }

  .bookmark:hover .edit_links {
    opacity: 1;
  }

  a.edit,
  a.delete,
  a.edit:visited,
  a.delete:visited {
    color: #95a5a6;
  }

  a.edit:hover,
  a.delete:hover {
    color: #34495e;
  }

  #per_page_picker {
    font-size: 14px;
  }

  #per_page_picker a.per_page_selected {
    background: none;
    color: #34495e;
    display: inline-block;
    font-size: 130%;
    font-weight: bold;
    position: relative;
    top: 1px;
  }

  #footer {
    opacity: .4;
    transition: all 200ms cubic-bezier(0.26, 0.86, 0.44, 0.985);
  }

  #footer:hover {
    opacity: 1;
  }

}
