css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("schildwall.info") {
  body {
    color: #999;
    background: none;
    background-color: #0e1720;
    font-family: Oswald, Sans;
    font-weight: normal;
    font-size: 100%;
  }
  .phoebe_header, .rhea_header, .tethys_header, .theia_header, .themis_header {
    height: 100px; 
    border-radius: 0px;
    margin-top: 30px;
  }
  .back_phoebe, .back_rhea, .back_tethys, .back_theia, .back_themis {
    background: none;
    background-color: #0e1720;
  }
  .playground_header {
   margin-top: 30px;
    width: 100%;
  }
  .tab_header {
    text-align: center;
    font-weight: 400;
  }
  .highlight_red {
   color: #b00;
   background-color: #300;
  }
  .ui-helper-clearfix:after {
    clear: right;
  }
  .rounded_header {
    background: none;
    font-size:  80%;
    padding: 20px;
    border: none;
    color: #999;
    align-self: center;
    text-align: center;
    border-radius: 3px;
  }
  .portlet, .ui-widget, .ui-helper-clearfix, .ui-corner-all {
    padding: 0px;
    background-color: #411;
    margin: 1px;
    border: none;
  }
  .ui-widget-content {
    background: none;
  }
  .ui-state-default {
    border: none;
    color: white;
    background-color: #2e3740;
   }
  .ui-widget-content {
    border: 0px solid #222;
    color: #FFF;
    background-color: #2e3740;
  }
  .tab_header {
    width: 99%;
    border: 0pc solid #222;
    height: 100px;
  }
  .tradehubs {
    background-color: #123;
    border-radius: 3px;
    padding: 4px;
  }
  .portlet {
    background-color: #123;
    border: 1px solid #345;
    padding:2px;
    border-radius: 3px;
    line-height: 16px;
   }
   .portlet-header, .portlet-header_2 {
     background-color: #654;
     border-radius: 1px;
     color: #ddd;
     padding: 2px;
     margin: 0px;
   }
  .portlet-header:hover, .portlet-header_2:hover, .guide_accordion:hover {
    background: #345;
    border-radius: 2px;
   }
  .dashtable tbody{
    border: 1px solid #345;
    background-color: #234;
    border-radius: 1px;
    font-size: 100%;
    margin: 0px;
    padding: 2px;
  }
  .dashtable td {
    border: none;
    border-bottom: 1px solid #345;
    padding: 1px 0px;
  }
  .linkgruppe {
    padding: 4px;
    width: 95%;
    font-size:  90%;
    background: none;
    border: 1px solid #234;
    background-color: #234;
    margin: 4px;
  }
   .linkgruppe > div, .dashtable {
    background-color: #123;
   }
  .mission {
    background: none;
    background-color: #0e1720;
  }
  .fg-toolbar {
    color: #6e96be;
    background-color: #0e1720;
    border: none;
  }
  .ui-widget-header {
    color: #6e96be;
    background-color: #0e1720;
    border: none;
    background: none;
  }
  .dataTables_info {
    padding: 15px;
    clear: both;
    font-size: 80%;
    float: right;
  }
  li {
    color: #ddd;
  }
  .rounded_header, .backhome {
    background-color: #2e3740;
  }
  a, a:visited {
    color: #8eb5de !important;
  }
  a:hover {
    color: #bee6fe !important;
    text-decoration: none;
  }
  a:active {
    color: #9eb6de !important;
  }
  .dashtable th {
    padding: 1px;
    border: 0px solid #000;
  }
  table.dataTable td {
    padding: 1px;
    border: 0px solid #000;
    border-bottom: 1px solid #0e1720;
  }
  input {
    border: 1px solid #3e668e;
    background-color: #1e466e;
    margin: 10px;
    padding: 2px;
    border-radius: 3px;
  }
  table {
    background-color: #1e2730;
    border: 0px solid #000;
    border-radius: 3px;
  }
  table.dataTable thead th div.DataTables_sort_wrapper {
    position: relative;
    padding-right: 6px;
    font-size: 80%;
    font-variant: titling-caps;
  }
  .zentriert {
    text-align: center;
    padding: 5px;
  }
  .rounded_header_darker {
    background-color: #1e2730;
    border: 1px solid #0e1720;
    border-radius: 5px;
    padding: 20px;
    text-align: center;
    font-size: 80%;
  }
  .skillcell {
    background-color: #234;
  }
  .hilfe {
    background-color: #0e1720;
    background-image: none;
  }
  .level {
    color: #6e96be;
  }
}
