css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dic.nicovideo.jp") {
/*AD Delete*/
.adsense-728,div[id*="ads"] {
display:none !important;
}
/*Parts*/
#header,#notice,#right-column,#footer,.border,.mobile-qr-code-container {
display:none !important; }

/*Ancher Link*/
a:link {
color: #68c933 !important;
text-decoration: none !important;
}
a:hover, a:active {
color: #ffb300 !important;
text-decoration: none !important;
background: #222 !important;
}
#topbarMenu,.popupMenu li a,
#topbarRightMenu {
background: #222 !important;
}
  
div.pager a:link {
background: #0d0015 !important;
border: 1px solid #F80;
font-weight: normal !important;
}
  
div.pager a:hover {
background: #333 !important;
font-weight: normal !important;
}

/*color*/
*[style*=background] {
background: #0d0015 !important;
}

*[style*=color] {
color: #e1e1e1 !important;
}
  
body#basebody,div.left-box,#main {
color: #e1e1e1 !important;
background: #0d0015 !important;
}
   
#menu-bar,#menu-bar .counter,#article-tab-nico {
color: #f0f0f0;
background: url(http://) !important;
}

span {
color: #e1e1e1 !important;
background: #0d0015 !important;
}

body#tinymce th, div.article th, div.content th,td {
background: #0d0015 !important;
}
  
#menu-bar {
margin: -98px 0 0 0 !important;
}
  
#main {
margin: -98px 0 0 150px !important;
}

div[style*="background"] {
background: url(http://) !important;
}
div.article, div.content {
font-size: 1.3em
}
dt.reshead {
font-size: 0.8em !important;
}
}
